            val iepsTax = if (item.category in listOf("alcohol", "tobacco", "fuel", "sugary_drinks")) {
                itemPrice * iepsRate
            } else 0.0

            // Zero-rated items
            if (item.category in listOf("food", "medicine", "books")) {
                ivaTax = 0.0
            }

            totalTax += ivaTax + iepsTax
        }

        return totalTax
    }

    private fun calculateSingaporeTax(order: Order): Double {
        val gstRate = 0.08 // Goods and Services Tax

        var totalTax = 0.0
        order.items.forEach { item ->
            val itemPrice = item.price * item.quantity
            var itemTax = itemPrice * gstRate

            // Zero-rated items
            if (item.category in listOf("exports", "international_services")) {
                itemTax = 0.0
            }

            // Exempt items
            if (item.category in listOf("residential_property", "financial_services", "digital_services")) {
                itemTax = 0.0
            }

            totalTax += itemTax
        }

        return totalTax
    }

    private fun calculateSouthKoreaTax(order: Order): Double {
        val vatRate = 0.10

        var totalTax = 0.0
        order.items.forEach { item ->
            val itemPrice = item.price * item.quantity
            var itemTax = itemPrice * vatRate

            // Zero-rated items
            if (item.category in listOf("exports", "medical", "education")) {
                itemTax = 0.0
            }

            // Individual consumption tax on luxury items
            if (item.category == "luxury" && itemPrice > 5000) {
                itemTax += itemPrice * 0.20
            }

            totalTax += itemTax
        }

        return totalTax
    }

    private fun calculateChinaTax(order: Order): Double {
        val vatRate = 0.13 // Standard VAT rate
        val reducedVatRate = 0.09

        var totalTax = 0.0
        order.items.forEach { item ->
            val itemPrice = item.price * item.quantity
            val itemTax = when (item.category) {
                "food", "books", "newspapers" -> itemPrice * reducedVatRate
                "luxury" -> itemPrice * (vatRate + 0.10) // Consumption tax on luxury
                else -> itemPrice * vatRate
            }

            totalTax += itemTax
        }

        return totalTax
    }

    private fun calculateGermanyTax(order: Order): Double {
        val standardVat = 0.19
        val reducedVat = 0.07

        var totalTax = 0.0
        order.items.forEach { item ->
            val itemPrice = item.price * item.quantity
            val itemTax = when (item.category) {
                "food", "books", "newspapers", "public_transport" -> itemPrice * reducedVat
                else -> itemPrice * standardVat
            }

            totalTax += itemTax
        }

        return totalTax
    }

    private fun calculateFranceTax(order: Order): Double {
        val standardVat = 0.20
        val intermediateVat = 0.10
        val reducedVat = 0.055
        val superReducedVat = 0.021

        var totalTax = 0.0
        order.items.forEach { item ->
            val itemPrice = item.price * item.quantity
            val itemTax = when (item.category) {
                "medicine", "books" -> itemPrice * superReducedVat
                "food", "restaurant" -> itemPrice * reducedVat
                "transport", "accommodation" -> itemPrice * intermediateVat
                else -> itemPrice * standardVat
            }

            totalTax += itemTax
        }

        return totalTax
    }

    private fun calculateItalyTax(order: Order): Double {
        val standardVat = 0.22
        val reducedVat = 0.10
        val superReducedVat = 0.04

        var totalTax = 0.0
        order.items.forEach { item ->
            val itemPrice = item.price * item.quantity
            val itemTax = when (item.category) {
                "food", "medicine" -> itemPrice * superReducedVat
                "books", "transport" -> itemPrice * reducedVat
                else -> itemPrice * standardVat
            }

            totalTax += itemTax
        }

        return totalTax
    }

    private fun calculateDigitalGoodsTax(order: Order): Double {
        var digitalTax = 0.0
        order.items.filter { it.isDigital }.forEach { item ->
            val itemPrice = item.price * item.quantity
            digitalTax += itemPrice * 0.02 // Additional digital services tax
        }
        return digitalTax
    }

    private fun calculateSeasonalAdjustment(order: Order): Double {
        val calendar = Calendar.getInstance()
        calendar.time = order.orderDate
        val month = calendar.get(Calendar.MONTH)

        // Holiday season surcharge (November-December)
        return if (month in 10..11) {
            order.getTotalAmount() * 0.005 // 0.5% holiday surcharge
        } else 0.0
    }

    private fun isInterstateTransaction(order: Order): Boolean {
        // Simplified logic - check if buyer and seller are in different states
        return order.customerId.substring(2, 4) != "MH" // Assuming seller is in Maharashtra
    }

    private fun getStateTaxRates(): Map<String, Double> = mapOf(
        "CA" to 0.0725, "NY" to 0.045, "TX" to 0.0625, "FL" to 0.06,
        "WA" to 0.065, "OR" to 0.0, "MT" to 0.0, "NH" to 0.0, "DE" to 0.0,
        "NV" to 0.0685, "IL" to 0.0625, "PA" to 0.06, "OH" to 0.0575
    )

    private fun getCategoryTaxRates(): Map<String, Double> = mapOf(
        "luxury" to 0.03, "food" to 0.0, "electronics" to 0.01,
        "clothing" to 0.005, "automotive" to 0.02, "books" to 0.0,
        "medicine" to 0.0, "fuel" to 0.03
    )

    private fun getCountryVatRates(): Map<String, Double> = mapOf(
        "DE" to 0.19, "FR" to 0.20, "IT" to 0.22, "ES" to 0.21,
        "NL" to 0.21, "BE" to 0.21, "AT" to 0.20, "SE" to 0.25,
        "DK" to 0.25, "FI" to 0.24, "NO" to 0.25, "PL" to 0.23
    )

    private fun getProvinceTaxRates(): Map<String, Double> = mapOf(
        "ON" to 0.08, "BC" to 0.07, "AB" to 0.0, "QC" to 0.09975,
        "NS" to 0.10, "NB" to 0.10, "MB" to 0.07, "SK" to 0.06,
        "PE" to 0.10, "NL" to 0.10, "YT" to 0.0, "NT" to 0.0, "NU" to 0.0
    )

    private fun getIndiaGSTRates(): Map<String, Double> = mapOf(
        "food" to 0.05, "clothing" to 0.12, "electronics" to 0.18,
        "luxury" to 0.28, "automotive" to 0.28, "books" to 0.0,
        "medicine" to 0.05, "gold" to 0.03, "textiles" to 0.05,
        "petroleum" to 0.0, "alcohol" to 0.0 // Alcohol is under state excise
    )
}
