            "verified" => $"[VERIFIED-REVIEW] {text}",
            _ => text
        };
    }

    private string ModerateExtremeOpinions(string text)
    {
        return Regex.Replace(text, @"(?i)love it|hate it|perfect|disaster", "[OPINION]");
    }

