public class TaxCalculator {
    private static final double DEFAULT_TAX_RATE = 0.05;

    public double calculateTax(Order order) {
        double totalTax = 0.0;

        if (order.getCountry().equals("US")) {
            totalTax = calculateUSTax(order);
        } else if (order.getCountry().equals("EU")) {
            totalTax = calculateEUTax(order);
        } else if (order.getCountry().equals("CA")) {
            totalTax = calculateCanadaTax(order);
        } else if (order.getCountry().equals("UK")) {
            totalTax = calculateUKTax(order);
        } else if (order.getCountry().equals("AU")) {
            totalTax = calculateAustraliaTax(order);
        } else if (order.getCountry().equals("JP")) {
            totalTax = calculateJapanTax(order);
        } else if (order.getCountry().equals("IN")) {
            totalTax = calculateIndiaTax(order);
        } else {
            // Default tax calculation for unsupported countries
            for (OrderItem item : order.getItems()) {
                double itemPrice = item.getPrice() * item.getQuantity();
                totalTax += itemPrice * DEFAULT_TAX_RATE;
            }
        }

        // Apply business order discount
        if (order.isBusinessOrder()) {
            totalTax *= 0.9; // 10% discount on tax for business orders
        }

        // Apply digital goods adjustment
        totalTax += calculateDigitalGoodsTax(order);

        return totalTax;
    }

    private double calculateUSTax(Order order) {
        Map<String, Double> stateTaxRates = getStateTaxRates();
        Map<String, Double> categoryTaxRates = getCategoryTaxRates();

        String state = order.getCustomerId().substring(0, 2);
        double stateTaxRate = stateTaxRates.getOrDefault(state, 0.05);

        double totalTax = 0.0;
        for (OrderItem item : order.getItems()) {
            double itemPrice = item.getPrice() * item.getQuantity();
            double categoryRate = categoryTaxRates.getOrDefault(item.getCategory(), 0.0);
            double itemTax = itemPrice * (stateTaxRate + categoryRate);

            // Special handling for luxury items over $1000
            if (item.getCategory().equals("luxury") && itemPrice > 1000) {
                itemTax += itemPrice * 0.02; // Additional 2% luxury tax
            }

            totalTax += itemTax;
        }

        return totalTax;
    }

    private double calculateEUTax(Order order) {
        Map<String, Double> countryVatRates = getCountryVatRates();

        String countryCode = order.getCustomerId().substring(0, 2);
        double vatRate = countryVatRates.getOrDefault(countryCode, 0.20);

        double totalTax = 0.0;
        for (OrderItem item : order.getItems()) {
            double itemPrice = item.getPrice() * item.getQuantity();
            double itemVat = itemPrice * vatRate;

            // Reduced VAT for food items
