            text = re.sub(r'(?i)dirty|filthy|disgusting', 'needs improvement', text)
        return text

    def _add_reviewer_status_labels(self, text: str, reviewer_status: str) -> str:
        if reviewer_status == "verified":
            text = "[VERIFIED-REVIEW] " + text
        return text

    def _moderate_extreme_opinions(self, text: str) -> str:
        return re.sub(r'(?i)love it|hate it|perfect|disaster', '[OPINION]', text)

    def _add_publication_labels(self, text: str, publication: str) -> str:
        if publication == "tabloid":
            text = "[TABLOID] " + text
        return text

    def _add_category_labels(self, text: str, category: str) -> str:
        if category == "opinion":
            text = "[OPINION-PIECE] " + text
        return text

    def _filter_video_specific_content(self, text: str, platform: str) -> str:
        if platform == "youtube":
            text = re.sub(r'(?i)like and subscribe', '[ENGAGEMENT-REQUEST]', text)
        return text

    def _add_content_type_labels(self, text: str, content_type: str) -> str:
        if content_type == "tutorial":
            text = re.sub(r'(?i)step by step|how to', '[TUTORIAL]', text)
        return text

    def _filter_commercial_content(self, text: str) -> str:
        return re.sub(r'(?i)spam|advertisement|promotion', '[COMMERCIAL]', text)

    def _remove_common_profanity(self, text: str) -> str:
        for word in self.banned_words:
            pattern = rf'(?i)\b{re.escape(word)}\b'
            text = re.sub(pattern, '[INAPPROPRIATE]', text)
        return text

    def _remove_personal_info(self, text: str) -> str:
        text = self.ssn_pattern.sub('[SSN]', text)
        text = self.credit_card_pattern.sub('[CREDIT_CARD]', text)
        text = re.sub(r'\b(?:\d{1,3}\.){3}\d{1,3}\b', '[IP_ADDRESS]', text)
        return text

    def _filter_urls(self, text: str, metadata: Dict[str, str]) -> str:
        url_policy = metadata.get("urlPolicy", "allow")
        if url_policy == "block":
            text = self.url_pattern.sub('[URL]', text)
        return text

    def _filter_emails(self, text: str, metadata: Dict[str, str]) -> str:
        email_policy = metadata.get("emailPolicy", "allow")
        if email_policy == "block":
            text = self.email_pattern.sub('[EMAIL]', text)
        return text

    def _filter_phone_numbers(self, text: str, metadata: Dict[str, str]) -> str:
        phone_policy = metadata.get("phonePolicy", "allow")
        if phone_policy == "block":
            text = self.phone_pattern.sub('[PHONE]', text)
        return text

    def _update_stats(self, content_type: str) -> None:
        self.filter_stats[content_type] = self.filter_stats.get(content_type, 0) + 1

    def get_filter_stats(self) -> Dict[str, int]:
        return self.filter_stats.copy()

    def get_total_filtered(self) -> int:
        return self.total_filtered

    def add_banned_word(self, word: str) -> None:
        self.banned_words.add(word.lower())

    def remove_banned_word(self, word: str) -> None:
        self.banned_words.discard(word.lower())

    def get_banned_words(self) -> Set[str]:
        return self.banned_words.copy()
