data class OrderItem(
    val productId: String,
    val price: Double,
    val quantity: Int,
    val category: String,
    val isDigital: Boolean = false,
    val weight: Double = 0.0
) {
    fun getTotalPrice(): Double = price * quantity
}

data class Order(
    val customerId: String,
    val items: List<OrderItem>,
    val country: String,
    val orderDate: Date,
    val orderType: String,
    val isBusinessOrder: Boolean = false
) {
    fun getTotalAmount(): Double = items.sumOf { it.getTotalPrice() }
}

class TaxCalculator {
    companion object {
        private const val DEFAULT_TAX_RATE = 0.05
    }

    fun calculateTax(order: Order): Double {
        var totalTax = 0.0

        when (order.country) {
            "US" -> totalTax = calculateUSTax(order)
            "EU" -> totalTax = calculateEUTax(order)
            "CA" -> totalTax = calculateCanadaTax(order)
            "UK" -> totalTax = calculateUKTax(order)
            "AU" -> totalTax = calculateAustraliaTax(order)
            "JP" -> totalTax = calculateJapanTax(order)
            "IN" -> totalTax = calculateIndiaTax(order)
            "BR" -> totalTax = calculateBrazilTax(order)
            "MX" -> totalTax = calculateMexicoTax(order)
            "SG" -> totalTax = calculateSingaporeTax(order)
            "KR" -> totalTax = calculateSouthKoreaTax(order)
            "CN" -> totalTax = calculateChinaTax(order)
            "DE" -> totalTax = calculateGermanyTax(order)
            "FR" -> totalTax = calculateFranceTax(order)
            "IT" -> totalTax = calculateItalyTax(order)
            else -> {
                // Default tax calculation for unsupported countries
                order.items.forEach { item ->
                    val itemPrice = item.price * item.quantity
                    totalTax += itemPrice * DEFAULT_TAX_RATE
                }
            }
        }

        // Apply business order discount
        if (order.isBusinessOrder) {
            totalTax *= 0.9 // 10% discount on tax for business orders
        }

        // Apply digital goods adjustment
        totalTax += calculateDigitalGoodsTax(order)

        // Apply seasonal adjustments
        totalTax += calculateSeasonalAdjustment(order)

        return totalTax
    }

    private fun calculateUSTax(order: Order): Double {
        val stateTaxRates = getStateTaxRates()
        val categoryTaxRates = getCategoryTaxRates()

        val state = order.customerId.substring(0, 2)
        val stateTaxRate = stateTaxRates[state] ?: 0.05

        var totalTax = 0.0
        order.items.forEach { item ->
            val itemPrice = item.price * item.quantity
            val categoryRate = categoryTaxRates[item.category] ?: 0.0
            var itemTax = itemPrice * (stateTaxRate + categoryRate)

            // Special handling for luxury items over $1000
            if (item.category == "luxury" && itemPrice > 1000) {
                itemTax += itemPrice * 0.02 // Additional 2% luxury tax
            }

            // Sales tax exemption for certain states on clothing under $110
            if (item.category == "clothing" && state in listOf("NY", "NJ") && itemPrice < 110) {
                itemTax = 0.0
            }

            // Special handling for food items in certain states
            if (item.category == "food" && state in listOf("CA", "NY", "WA")) {
                itemTax = 0.0
            }

            totalTax += itemTax
        }

        return totalTax
    }

    private fun calculateEUTax(order: Order): Double {
        val countryVatRates = getCountryVatRates()

        val countryCode = order.customerId.substring(0, 2)
        val vatRate = countryVatRates[countryCode] ?: 0.20

        var totalTax = 0.0
        order.items.forEach { item ->
            val itemPrice = item.price * item.quantity
            var itemVat = itemPrice * vatRate

            // Reduced VAT for food items
            if (item.category == "food") {
                itemVat = itemPrice * (vatRate * 0.5)
            }

            // Zero VAT for books and newspapers in some countries
            if (item.category in listOf("books", "newspapers") && countryCode in listOf("UK", "IE")) {
                itemVat = 0.0
            }

            // Special VAT rates for digital services
            if (item.isDigital && countryCode in listOf("DE", "FR", "IT")) {
                itemVat = itemPrice * 0.19 // Digital services VAT
            }

            totalTax += itemVat
        }

        return totalTax
