            else -> text
        }
    }

    private fun moderateExtremeOpinions(text: String): String {
        return text.replace(Regex("(?i)love it|hate it|perfect|disaster"), "[OPINION]")
    }

    private fun addPublicationLabels(text: String, publication: String): String {
        return when (publication) {
            "tabloid" -> "[TABLOID] $text"
            else -> text
        }
    }

    private fun addCategoryLabels(text: String, category: String): String {
        return when (category) {
            "opinion" -> "[OPINION-PIECE] $text"
            else -> text
        }
    }

    private fun filterVideoSpecificContent(text: String, platform: String): String {
        return when (platform) {
