            else -> text
        }
    }

    private fun addContentTypeLabels(text: String, contentType: String): String {
        return when (contentType) {
            "tutorial" -> text.replace(Regex("(?i)step by step|how to"), "[TUTORIAL]")
            else -> text
        }
    }

    private fun filterCommercialContent(text: String): String {
        return text.replace(Regex("(?i)spam|advertisement|promotion"), "[COMMERCIAL]")
    }

    private fun removeCommonProfanity(text: String): String {
        var result = text
        bannedWords.forEach { word ->
            val pattern = Regex("(?i)\\b${Regex.escape(word)}\\b")
            result = result.replace(pattern, "[INAPPROPRIATE]")
        }
        return result
    }

    private fun removePersonalInfo(text: String): String {
        return ssnPattern.matcher(text).replaceAll("[SSN]")
                          .let { creditCardPattern.matcher(it).replaceAll("[CREDIT_CARD]") }
                          .replace(Regex("\\b(?:\\d{1,3}\\.){3}\\d{1,3}\\b"), "[IP_ADDRESS]")
    }

    private fun filterUrls(text: String, metadata: Map<String, String>): String {
        val urlPolicy = metadata["urlPolicy"] ?: "allow"
        return when (urlPolicy) {
            "block" -> urlPattern.matcher(text).replaceAll("[URL]")
            else -> text
        }
    }

    private fun filterEmails(text: String, metadata: Map<String, String>): String {
        val emailPolicy = metadata["emailPolicy"] ?: "allow"
        return when (emailPolicy) {
            "block" -> emailPattern.matcher(text).replaceAll("[EMAIL]")
            else -> text
        }
    }

    private fun filterPhoneNumbers(text: String, metadata: Map<String, String>): String {
        val phonePolicy = metadata["phonePolicy"] ?: "allow"
        return when (phonePolicy) {
            "block" -> phonePattern.matcher(text).replaceAll("[PHONE]")
            else -> text
        }
    }

    private fun updateStats(contentType: String) {
        filterStats[contentType] = filterStats.getOrDefault(contentType, 0) + 1
    }

    fun getFilterStats(): Map<String, Int> = filterStats.toMap()

    fun getTotalFiltered(): Int = totalFiltered

    fun addBannedWord(word: String) {
        bannedWords.add(word.lowercase())
    }

    fun removeBannedWord(word: String) {
        bannedWords.remove(word.lowercase())
    }

    fun getBannedWords(): Set<String> = bannedWords.toSet()
}

