        sales.Add(sale);
    }

    public List<Sale> GetSalesByRegion(string region)
    {
        return sales.Where(sale => sale.Region == region).ToList();
    }

    public decimal GetTotalSales()
    {
        return sales.Sum(sale => sale.GetNetAmount());
    }
}

