            double total = dailySales.values().stream().mapToDouble(Double::doubleValue).sum();
            report.append("\nTotal Sales: $").append(total);

        } else if (reportType.equals("product")) {
            report.append("PRODUCT SALES REPORT\n");
            report.append("====================\n\n");

