class ShippingService:
    def __init__(self, order_repository, inventory_service, logger):
        self.order_repository = order_repository
        self.inventory_service = inventory_service
        self.logger = logger

    def process_shipment(self, order_id):
        order = self.order_repository.find_by_id(order_id)

        if order is None:
            raise ValueError(f"Order not found: {order_id}")

        if order.get_status() != OrderStatus.PAID:
            return ShipmentResult(False, "Order is not paid yet", None)

        for item in order.get_items():
            if not self.inventory_service.is_in_stock(item.get_product_id(), item.get_quantity()):
                return ShipmentResult(False, f"Product out of stock: {item.get_product_id()}", None)

        shipment = Shipment(order_id, datetime.datetime.now())
        shipping_cost = self.calculate_shipping_cost(order)
        shipment.set_cost(shipping_cost.get_cost())

        # Reserve inventory items
        for item in order.get_items():
            self.inventory_service.reserve(item.get_product_id(), item.get_quantity())

        # Update order status
        order.set_status(OrderStatus.SHIPPED)
        self.order_repository.update(order)

        self.logger.log(f"Order shipped: {order_id}, cost: {shipping_cost.get_cost()}")
        return ShipmentResult(True, "Order shipped successfully", shipment)

    def calculate_shipping_cost(self, order):
        destination = order.get_shipping_address()
        total_weight = self.calculate_order_weight(order)
        base_cost = 0.0

        shipping_method = order.get_shipping_method()

        if shipping_method == "standard":
            # Standard shipping: base cost + weight factor
            base_cost = 5.0
            weight_cost = total_weight * 0.5
            distance = self.calculate_distance(destination)
            distance_factor = distance * 0.01

            return ShippingCost(base_cost + weight_cost + distance_factor)

        elif shipping_method == "express":
            # Express shipping: higher base cost + weight factor
            base_cost = 15.0
            weight_cost = total_weight * 0.75
            distance = self.calculate_distance(destination)
            distance_factor = distance * 0.015

            return ShippingCost(base_cost + weight_cost + distance_factor)

        elif shipping_method == "overnight":
            # Overnight shipping: premium cost
            base_cost = 25.0
            weight_cost = total_weight * 1.0
            distance = self.calculate_distance(destination)
            distance_factor = distance * 0.02

            return ShippingCost(base_cost + weight_cost + distance_factor)

        elif shipping_method == "international":
