        return price
    }

    fun getQuantity(): Int {
        return quantity
    }

    fun getCategory(): String {
        return category
    }
}

class TaxCalculator {
    fun calculateTax(order: Order): Double {
        var totalTax = 0.0

        if (order.getCountry() == "US") {
            val stateTaxRates = getStateTaxRates()
            val categoryTaxRates = getCategoryTaxRates()

            val state = order.getCustomerId().substring(0, 2)
            val stateTaxRate = stateTaxRates.getOrDefault(state, 0.05)

            for (item in order.getItems()) {
                val itemPrice = item.getPrice() * item.getQuantity()
                val categoryRate = categoryTaxRates.getOrDefault(item.getCategory(), 0.0)
                val itemTax = itemPrice * (stateTaxRate + categoryRate)
                totalTax += itemTax
            }
        } else if (order.getCountry() == "EU") {
            val countryVatRates = getCountryVatRates()

            val countryCode = order.getCustomerId().substring(0, 2)
            val vatRate = countryVatRates.getOrDefault(countryCode, 0.20)

            for (item in order.getItems()) {
                val itemPrice = item.getPrice() * item.getQuantity()
                totalTax += itemPrice * vatRate
            }
        } else if (order.getCountry() == "CA") {
            val provinceTaxRates = getProvinceTaxRates()
            val gst = 0.05

            val province = order.getCustomerId().substring(0, 2)
            val provincialTax = provinceTaxRates.getOrDefault(province, 0.08)

            for (item in order.getItems()) {
                val itemPrice = item.getPrice() * item.getQuantity()
                totalTax += itemPrice * (gst + provincialTax)
            }
        }

        return totalTax
    }

    private fun getStateTaxRates(): Map<String, Double> {
        val rates = HashMap<String, Double>()
        rates["CA"] = 0.0725
        rates["NY"] = 0.045
        rates["TX"] = 0.0625
        return rates
    }

    private fun getCategoryTaxRates(): Map<String, Double> {
        val rates = HashMap<String, Double>()
        rates["luxury"] = 0.03
        rates["food"] = 0.0
        return rates
    }

    private fun getCountryVatRates(): Map<String, Double> {
        val rates = HashMap<String, Double>()
        rates["DE"] = 0.19
        rates["FR"] = 0.20
        rates["IT"] = 0.22
        return rates
    }

    private fun getProvinceTaxRates(): Map<String, Double> {
        val rates = HashMap<String, Double>()
        rates["ON"] = 0.08
        rates["BC"] = 0.07
        rates["AB"] = 0.0
        return rates
    }
}

