{
    private IOrderRepository orderRepository;
    private IInventoryService inventoryService;
    private ILogger logger;
    private Dictionary<string, IShippingCostCalculator> shippingCostCalculators;

    public ShippingService(IOrderRepository orderRepository, IInventoryService inventoryService, ILogger logger)
    {
        this.orderRepository = orderRepository;
        this.inventoryService = inventoryService;
        this.logger = logger;
        this.shippingCostCalculators = new Dictionary<string, IShippingCostCalculator>
        {
            { "standard", new StandardShippingCostCalculator() },
            { "express", new ExpressShippingCostCalculator() },
            { "overnight", new OvernightShippingCostCalculator() },
            { "international", new InternationalShippingCostCalculator() }
        };
    }

    public ShipmentResult ProcessShipment(string orderId)
    {
        Order order = orderRepository.FindById(orderId);

        if (order == null)
        {
            throw new ArgumentException($"Order not found: {orderId}");
        }

        if (order.GetStatus() != OrderStatus.PAID)
        {
            return new ShipmentResult(false, "Order is not paid yet", null);
        }

        foreach (OrderItem item in order.GetItems())
        {
            if (!inventoryService.IsInStock(item.GetProductId(), item.GetQuantity()))
            {
                return new ShipmentResult(false, $"Product out of stock: {item.GetProductId()}", null);
            }
        }

        Shipment shipment = new Shipment(orderId, DateTime.Now);
        ShippingCost shippingCost = CalculateShippingCost(order);
        shipment.SetCost(shippingCost.GetCost());

        // Reserve inventory items
        foreach (OrderItem item in order.GetItems())
        {
            inventoryService.Reserve(item.GetProductId(), item.GetQuantity());
        }

        // Update order status
        order.SetStatus(OrderStatus.SHIPPED);
        orderRepository.Update(order);

        logger.Log($"Order shipped: {orderId}, cost: {shippingCost.GetCost()}");
        return new ShipmentResult(true, "Order shipped successfully", shipment);
    }

    private ShippingCost CalculateShippingCost(Order order)
    {
        string shippingMethod = order.GetShippingMethod();
        if (shippingCostCalculators.TryGetValue(shippingMethod, out var calculator))
        {
            return calculator.CalculateShippingCost(order);
        }

        // Default shipping cost if method not recognized
        return new ShippingCost(10.0);
    }
}
```
The original `ShippingService` class contained multiple `if-else` statements to determine the shipping cost based on the shipping method, violating the Open/Closed Principle (OCP). The refactored code introduces an interface `IShippingCostCalculator` and separate classes for each shipping method, allowing the `ShippingService` to be extended with new shipping methods without modifying existing code.

