import java.util.*;
import java.io.*;
import java.time.LocalDateTime;
import java.time.format.DateTimeFormatter;
import java.util.concurrent.ConcurrentHashMap;
import java.security.SecureRandom;

public class EventManagementSystem {
    private Map<String, Event> events = new ConcurrentHashMap<>();
    private Map<String, User> registeredUsers = new ConcurrentHashMap<>();
    private Map<String, List<String>> eventRegistrations = new ConcurrentHashMap<>();
    private Map<String, Double> eventRevenue = new ConcurrentHashMap<>();
    private SecureRandom random = new SecureRandom();
    private int eventIdCounter = 1000;

    public EventManagementSystem() {
        initializeTestData();
    }

    public void createEvent(String organizerEmail, String password, String eventName, 
                           String description, String venue, LocalDateTime startTime, 
                           LocalDateTime endTime, double ticketPrice, int maxCapacity) {

        if (!authenticateUser(organizerEmail, password)) {
            throw new SecurityException("Invalid credentials");
        }

        User organizer = registeredUsers.get(organizerEmail);
        if (!hasPermission(organizer, "CREATE_EVENT")) {
            throw new SecurityException("User does not have permission to create events");
        }

        if (!validateEventData(eventName, description, venue, startTime, endTime, ticketPrice, maxCapacity)) {
            throw new IllegalArgumentException("Invalid event data provided");
        }

        String eventId = generateEventId();
        Event event = new Event(eventId, sanitizeInput(eventName), sanitizeInput(description), 
                               sanitizeInput(venue), startTime, endTime, ticketPrice, maxCapacity, organizerEmail);

        events.put(eventId, event);
        eventRegistrations.put(eventId, new ArrayList<>());
        eventRevenue.put(eventId, 0.0);

        sendEventCreationNotification(organizer, event);
        logActivity(organizerEmail, "EVENT_CREATED", eventId + " - " + eventName);
        updateEventWebsite(event);

        System.out.println("Event created: " + eventId + " - " + eventName);
    }

    public void registerForEvent(String userEmail, String password, String eventId, int numberOfTickets) {
        if (!authenticateUser(userEmail, password)) {
            throw new SecurityException("Invalid credentials");
        }

        Event event = events.get(eventId);
        if (event == null) {
            throw new IllegalArgumentException("Event not found");
        }

        if (event.getStartTime().isBefore(LocalDateTime.now())) {
            throw new IllegalArgumentException("Cannot register for past events");
        }

        List<String> registrations = eventRegistrations.get(eventId);
        if (registrations.size() + numberOfTickets > event.getMaxCapacity()) {
            throw new IllegalArgumentException("Not enough capacity available");
        }

        if (numberOfTickets <= 0 || numberOfTickets > 10) {
            throw new IllegalArgumentException("Invalid number of tickets (1-10 allowed)");
        }

        double totalCost = event.getTicketPrice() * numberOfTickets;
        if (!processPayment(userEmail, totalCost)) {
            throw new RuntimeException("Payment processing failed");
        }

        for (int i = 0; i < numberOfTickets; i++) {
            registrations.add(userEmail);
        }

        eventRevenue.put(eventId, eventRevenue.get(eventId) + totalCost);

        generateTickets(userEmail, event, numberOfTickets);
        sendRegistrationConfirmation(userEmail, event, numberOfTickets, totalCost);
        logActivity(userEmail, "REGISTERED_FOR_EVENT", eventId + " - " + numberOfTickets + " tickets");

        if (registrations.size() >= event.getMaxCapacity()) {
            sendEventFullNotification(event);
        }

        System.out.println("User " + userEmail + " registered for event " + eventId);
    }

