    def __init__(self, username: str, password_hash: str, email: str, created_at: datetime):
        self.username = username
        self.password_hash = password_hash
        self.email = email
        self.created_at = created_at
        self.last_login: Optional[datetime] = None

    def get_username(self) -> str:
        return self.username

    def get_password_hash(self) -> str:
        return self.password_hash

    def get_email(self) -> str:
        return self.email

    def set_email(self, email: str) -> None:
        self.email = email

    def get_created_at(self) -> datetime:
        return self.created_at

    def get_last_login(self) -> Optional[datetime]:
        return self.last_login

    def set_last_login(self, last_login: datetime) -> None:
        self.last_login = last_login

class UserManager:
    def __init__(self):
        self.users: Dict[str, User] = {}
        self.sessions: Dict[str, str] = {}

    def create_user(self, username: str, password: str, email: str) -> None:
        if username in self.users:
            raise ValueError("Username already exists")

        if not self._is_valid_email(email):
            raise ValueError("Invalid email format")

        if len(password) < 8:
            raise ValueError("Password must be at least 8 characters")

        hashed_password = self._hash_password(password)
        user = User(username, hashed_password, email, datetime.now())
        self.users[username] = user

        print(f"User created successfully: {username}")

    def login(self, username: str, password: str) -> str:
        user = self.users.get(username)
        if user is None:
            raise ValueError("Invalid username or password")

        if not self._verify_password(password, user.get_password_hash()):
            raise ValueError("Invalid username or password")

        session_token = self._generate_session_token()
        self.sessions[session_token] = username

        user.set_last_login(datetime.now())

        print(f"User logged in: {username}")
        return session_token

    def logout(self, session_token: str) -> None:
        username = self.sessions.pop(session_token, None)
        if username:
            print(f"User logged out: {username}")

    def is_logged_in(self, session_token: str) -> bool:
        return session_token in self.sessions

    def get_user_by_session(self, session_token: str) -> Optional[User]:
        username = self.sessions.get(session_token)
        if username is None:
            return None
        return self.users.get(username)

    def _is_valid_email(self, email: str) -> bool:
        pattern = r'^[A-Za-z0-9+_.-]+@(.+)$'
        return bool(re.match(pattern, email))

    def _hash_password(self, password: str) -> str:
        return base64.b64encode(password.encode()).decode()

    def _verify_password(self, password: str, hash_value: str) -> bool:
        return self._hash_password(password) == hash_value

    def _generate_session_token(self) -> str:
        return base64.b64encode(secrets.token_bytes(32)).decode()

    def update_user_email(self, username: str, new_email: str) -> None:
        user = self.users.get(username)
        if user is None:
            raise ValueError("User not found")

        if not self._is_valid_email(new_email):
            raise ValueError("Invalid email format")

        user.set_email(new_email)
        print(f"Email updated for user: {username}")

    def get_all_users(self) -> List[User]:
        return list(self.users.values())

