import sqlite3
import smtplib
import json
from datetime import datetime
from email.mime.text import MIMEText
from email.mime.multipart import MIMEMultipart
from typing import List, Optional

class OrderManager:
    def __init__(self):
        self.orders = []
        self.db_connection = None
        self.email_config = {
            'smtp_server': 'smtp.gmail.com',
            'smtp_port': 587,
            'email': 'orders@company.com',
            'password': 'password'
        }
        self.initialize_database()

    # Order management responsibility
    def add_order(self, order):
        if self.validate_order(order):
            self.orders.append(order)
            self.save_order_to_database(order)
            self.send_order_confirmation_email(order)
            self.generate_invoice(order)
            self.update_inventory(order)
            self.log_order_activity(order, "ORDER_CREATED")

    def update_order_status(self, order_id, status):
        order = self.find_order_by_id(order_id)
        if order:
            order['status'] = status
            self.update_order_in_database(order)
            self.send_status_update_email(order)
            self.log_order_activity(order, "STATUS_UPDATED")

    def find_order_by_id(self, order_id):
        return next((order for order in self.orders if order['id'] == order_id), None)

    # Validation responsibility
    def validate_order(self, order):
        if not order or not order.get('customer'):
            return False
        if not order.get('items') or len(order['items']) == 0:
            return False
        if order.get('total_amount', 0) <= 0:
            return False
        return self.validate_customer_credit_limit(order)

    def validate_customer_credit_limit(self, order):
        customer_credit_limit = self.get_customer_credit_limit(order['customer']['id'])
        outstanding_amount = self.get_customer_outstanding_amount(order['customer']['id'])
        return (outstanding_amount + order['total_amount']) <= customer_credit_limit

    # Database operations responsibility
    def initialize_database(self):
        try:
            self.db_connection = sqlite3.connect('orders.db')
            cursor = self.db_connection.cursor()
            cursor.execute('''
                CREATE TABLE IF NOT EXISTS orders (
                    id INTEGER PRIMARY KEY,
                    customer_id INTEGER,
                    total_amount REAL,
                    status TEXT,
                    created_date TEXT
                )
            ''')
            cursor.execute('''
                CREATE TABLE IF NOT EXISTS customers (
                    id INTEGER PRIMARY KEY,
                    name TEXT,
                    email TEXT,
                    credit_limit REAL
                )
            ''')
            cursor.execute('''
                CREATE TABLE IF NOT EXISTS inventory (
                    product_id INTEGER PRIMARY KEY,
                    quantity INTEGER
                )
            ''')
            self.db_connection.commit()
        except sqlite3.Error as e:
            print(f"Database connection failed: {e}")

    def save_order_to_database(self, order):
        try:
            cursor = self.db_connection.cursor()
            cursor.execute(
                "INSERT INTO orders (customer_id, total_amount, status, created_date) VALUES (?, ?, ?, ?)",
                (order['customer']['id'], order['total_amount'], order['status'], datetime.now().isoformat())
            )
            self.db_connection.commit()
        except sqlite3.Error as e:
            print(f"Failed to save order: {e}")

    def update_order_in_database(self, order):
        try:
            cursor = self.db_connection.cursor()
            cursor.execute(
                "UPDATE orders SET status = ?, updated_date = ? WHERE id = ?",
                (order['status'], datetime.now().isoformat(), order['id'])
            )
            self.db_connection.commit()
        except sqlite3.Error as e:
            print(f"Failed to update order: {e}")

    def get_customer_credit_limit(self, customer_id):
        try:
            cursor = self.db_connection.cursor()
            cursor.execute("SELECT credit_limit FROM customers WHERE id = ?", (customer_id,))
            result = cursor.fetchone()
            return result[0] if result else 0
        except sqlite3.Error as e:
            print(f"Failed to get credit limit: {e}")
            return 0

    def get_customer_outstanding_amount(self, customer_id):
        try:
            cursor = self.db_connection.cursor()
            cursor.execute(
                "SELECT SUM(total_amount) FROM orders WHERE customer_id = ? AND status = 'PENDING'",
                (customer_id,)
            )
            result = cursor.fetchone()
            return result[0] if result and result[0] else 0
        except sqlite3.Error as e:
            print(f"Failed to get outstanding amount: {e}")
            return 0

    # Email notification responsibility
    def send_order_confirmation_email(self, order):
        try:
            subject = f"Order Confirmation - Order #{order['id']}"
            body = f"Your order has been confirmed. Total: ${order['total_amount']}"
            self.send_email(order['customer']['email'], subject, body)
        except Exception as e:
            print(f"Failed to send confirmation email: {e}")

    def send_status_update_email(self, order):
        try:
            subject = f"Order Status Update - Order #{order['id']}"
            body = f"Your order status has been updated to: {order['status']}"
            self.send_email(order['customer']['email'], subject, body)
        except Exception as e:
            print(f"Failed to send status email: {e}")

    def send_email(self, to_email, subject, body):
        try:
            msg = MIMEMultipart()
            msg['From'] = self.email_config['email']
            msg['To'] = to_email
            msg['Subject'] = subject
            msg.attach(MIMEText(body, 'plain'))

            server = smtplib.SMTP(self.email_config['smtp_server'], self.email_config['smtp_port'])
            server.starttls()
            server.login(self.email_config['email'], self.email_config['password'])
            server.sendmail(self.email_config['email'], to_email, msg.as_string())
            server.quit()
        except Exception as e:
            print(f"Failed to send email: {e}")

    # Invoice generation responsibility
    def generate_invoice(self, order):
        try:
            filename = f"invoice_{order['id']}.txt"
            with open(filename, 'w') as file:
                file.write("INVOICE\n")
                file.write(f"Order ID: {order['id']}\n")
                file.write(f"Customer: {order['customer']['name']}\n")
                file.write(f"Date: {datetime.now().strftime('%Y-%m-%d')}\n")
                file.write(f"Total Amount: ${order['total_amount']}\n")
        except IOError as e:
            print(f"Failed to generate invoice: {e}")

    # Inventory management responsibility
    def update_inventory(self, order):
        for item in order['items']:
            try:
                cursor = self.db_connection.cursor()
                cursor.execute(
                    "UPDATE inventory SET quantity = quantity - ? WHERE product_id = ?",
                    (item['quantity'], item['product_id'])
                )
                self.db_connection.commit()
            except sqlite3.Error as e:
                print(f"Failed to update inventory: {e}")

    # Logging responsibility
    def log_order_activity(self, order, activity):
        try:
            with open('order_log.txt', 'a') as file:
                log_entry = f"{datetime.now().isoformat()} - Order {order['id']}: {activity}\n"
                file.write(log_entry)
        except IOError as e:
            print(f"Failed to log activity: {e}")

from typing import Dict, List, Optional, Collection
from datetime import datetime
from concurrent.futures import ThreadPoolExecutor
import hashlib
import re
import os
import json
import time

class BlogPostManager:
    def __init__(self):
        self.posts: Dict[int, BlogPost] = {}
        self.user_sessions: Dict[str, List[str]] = {}
        self.user_permissions: Dict[str, int] = {}
        self.next_post_id = 1
        self.initialize_default_users()
        self.setup_logging()

    def create_post(self, session_token: str, title: str, content: str, tags: List[str]):
        if not self.is_valid_session(session_token):
            raise SecurityError("Invalid session")

        username = self.get_user_from_session(session_token)
        if not self.has_permission(username, "CREATE_POST"):
            raise SecurityError("Insufficient permissions")

        if not self.validate_post_content(title, content):
            raise ValueError("Invalid post content")

        sanitized_title = self.sanitize_html(title)
        sanitized_content = self.sanitize_html(content)
        processed_tags = self.process_tags(tags)

        post = BlogPost(self.next_post_id, sanitized_title, sanitized_content,
                       username, datetime.now(), processed_tags)
        self.next_post_id += 1
        self.posts[post.id] = post

        self.update_search_index(post)
        self.generate_sitemap()
        self.notify_subscribers(post)
        self.log_activity(username, "POST_CREATED", post.id)

        print(f"Blog post created successfully: {post.id}")

    def update_post(self, session_token: str, post_id: int, new_title: str, new_content: str):
        if not self.is_valid_session(session_token):
            raise SecurityError("Invalid session")

        username = self.get_user_from_session(session_token)
        post = self.posts.get(post_id)

        if post is None:
            raise ValueError("Post not found")

        if post.author != username and not self.has_permission(username, "EDIT_ANY_POST"):
            raise SecurityError("Cannot edit this post")

        if not self.validate_post_content(new_title, new_content):
            raise ValueError("Invalid post content")

        post.title = self.sanitize_html(new_title)
        post.content = self.sanitize_html(new_content)
        post.last_modified = datetime.now()

        self.update_search_index(post)
        self.generate_sitemap()
        self.log_activity(username, "POST_UPDATED", post_id)

        print(f"Post updated: {post_id}")

    def delete_post(self, session_token: str, post_id: int):
        if not self.is_valid_session(session_token):
            raise SecurityError("Invalid session")

        username = self.get_user_from_session(session_token)
        post = self.posts.get(post_id)

        if post is None:
            raise ValueError("Post not found")

        if post.author != username and not self.has_permission(username, "DELETE_ANY_POST"):
            raise SecurityError("Cannot delete this post")

        del self.posts[post_id]
        self.remove_from_search_index(post_id)
        self.generate_sitemap()
        self.log_activity(username, "POST_DELETED", post_id)

        print(f"Post deleted: {post_id}")

    def initialize_default_users(self):
        self.user_permissions["admin"] = 7  # All permissions
        self.user_permissions["editor"] = 3  # Create and edit
        self.user_permissions["author"] = 1  # Create only

    def setup_logging(self):
        try:
            if not os.path.exists("logs"):
                os.makedirs("logs")
        except Exception as e:
            print(f"Failed to setup logging: {e}")

    def is_valid_session(self, session_token: str) -> bool:
        return session_token is not None and session_token in self.user_sessions

    def get_user_from_session(self, session_token: str) -> Optional[str]:
        session_data = self.user_sessions.get(session_token)
        return session_data[0] if session_data else None

    def has_permission(self, username: str, permission: str) -> bool:
        user_level = self.user_permissions.get(username)
        if user_level is None:
            return False

        permission_map = {
            "CREATE_POST": 1,
            "EDIT_ANY_POST": 2,
            "DELETE_ANY_POST": 4
        }

        required_level = permission_map.get(permission, 0)
        return (user_level & required_level) != 0

    def validate_post_content(self, title: str, content: str) -> bool:
        if not title or not title.strip() or len(title) > 200:
            return False
        if not content or not content.strip() or len(content) > 10000:
            return False

        script_pattern = re.compile(r'<script[^>]*>.*?</script>', re.IGNORECASE)
        return not script_pattern.search(title) and not script_pattern.search(content)

    def sanitize_html(self, input_str: str) -> str:
        if not input_str:
            return ""
        result = re.sub(r'<script[^>]*>.*?</script>', '', input_str, flags=re.IGNORECASE)
        result = re.sub(r'<[^>]+>', '', result)
        result = result.replace('&', '&amp;')
        result = result.replace('<', '&lt;')
        result = result.replace('>', '&gt;')
        return result

    def process_tags(self, tags: List[str]) -> List[str]:
        if not tags:
            return []

        processed = []
        seen = set()
        for tag in tags:
            clean_tag = tag.lower().strip()
            if clean_tag and len(clean_tag) <= 50 and clean_tag not in seen:
                processed.append(clean_tag)
                seen.add(clean_tag)
        return processed

    def update_search_index(self, post: 'BlogPost'):
        try:
            with open("search_index.txt", "a") as f:
                f.write(f"{post.id}|{post.title}|{post.content}\n")
        except IOError as e:
            print(f"Failed to update search index: {e}")

    def remove_from_search_index(self, post_id: int):
        try:
            if not os.path.exists("search_index.txt"):
                return

            with open("search_index.txt", "r") as f:
                lines = f.readlines()

            with open("search_index.txt", "w") as f:
                for line in lines:
                    if not line.startswith(f"{post_id}|"):
                        f.write(line)
        except IOError as e:
            print(f"Failed to update search index: {e}")

    def generate_sitemap(self):
        try:
            with open("sitemap.xml", "w") as f:
                f.write('<?xml version="1.0" encoding="UTF-8"?>\n')
                f.write('<urlset xmlns="http://www.sitemaps.org/schemas/sitemap/0.9">\n')

                for post in self.posts.values():
                    f.write('  <url>\n')
                    f.write(f'    <loc>https://blog.com/post/{post.id}</loc>\n')
                    f.write(f'    <lastmod>{post.last_modified}</lastmod>\n')
                    f.write('  </url>\n')

                f.write('</urlset>\n')
        except IOError as e:
            print(f"Failed to generate sitemap: {e}")

    def notify_subscribers(self, post: 'BlogPost'):
        print(f"Sending notifications for new post: {post.title}")
        time.sleep(0.1)  # Simulate network delay

    def log_activity(self, username: str, action: str, post_id: int):
        try:
            log_file = f"logs/activity_{datetime.now().date()}.log"
            with open(log_file, "a") as f:
                log_entry = f"{datetime.now()} - User: {username}, Action: {action}, Post: {post_id}\n"
                f.write(log_entry)
        except IOError as e:
            print(f"Failed to log activity: {e}")

    def search_posts(self, query: str) -> List['BlogPost']:
        results = []
        lower_query = query.lower()

        for post in self.posts.values():
            if (lower_query in post.title.lower() or 
                lower_query in post.content.lower() or 
                any(lower_query in tag for tag in post.tags)):
                results.append(post)

        return results

    def login_user(self, username: str, password: str) -> str:
        hashed_password = self.hash_password(password)
        if self.validate_credentials(username, hashed_password):
            session_token = self.generate_session_token(username)
            self.user_sessions[session_token] = [username, str(datetime.now())]
            self.log_activity(username, "LOGIN", 0)
            return session_token
        raise SecurityError("Invalid credentials")

    def hash_password(self, password: str) -> str:
        return hashlib.sha256(password.encode()).hexdigest()

    def validate_credentials(self, username: str, hashed_password: str) -> bool:
        return username in self.user_permissions

    def generate_session_token(self, username: str) -> str:
        return self.hash_password(f"{username}{int(time.time() * 1000)}")

class BlogPost:
    def __init__(self, post_id: int, title: str, content: str, author: str, 
                 created_at: datetime, tags: List[str]):
        self.id = post_id
        self.title = title
        self.content = content
        self.author = author
        self.created_at = created_at
        self.last_modified = created_at
        self.tags = tags or []

class SecurityError(Exception):
    pass

