        writer.write(f"Generated: {datetime.now()}\n\n")

        for supplier in self.suppliers.values():
            writer.write(f"Supplier: {supplier.name} ({supplier.id})\n")
            writer.write(f"Email: {supplier.email}\n")
            writer.write(f"Phone: {supplier.phone}\n\n")

    def _update_inventory_file(self):
        try:
            with open("inventory.dat", "w") as writer:
                for product in self.products.values():
                    writer.write(f"{product.id}|{product.name}|{product.quantity}|{product.price}|{product.supplier_id}|{product.min_threshold}\n")
        except IOError as e:
            print(f"Failed to update inventory file: {e}")

    def _load_data_from_files(self):
        try:
            if os.path.exists("inventory.dat"):
                with open("inventory.dat", "r") as reader:
                    for line in reader:
                        parts = line.strip().split("|")
                        if len(parts) == 6:
                            product = Product(
                                id=parts[0],
                                name=parts[1],
                                quantity=int(parts[2]),
                                price=float(parts[3]),
                                supplier_id=parts[4],
                                min_threshold=int(parts[5]),
                                last_updated=datetime.now()
                            )
                            self.products[product.id] = product
        except Exception as e:
            print(f"Failed to load inventory data: {e}")

    def _log_activity(self, username: str, action: str, details: str):
        try:
            with open("inventory_log.txt", "a") as writer:
                writer.write(f"{datetime.now()} - User: {username}, Action: {action}, Details: {details}\n")
        except IOError as e:
            print(f"Failed to log activity: {e}")

