import java.util.*

data class SalesData(
    val productName: String,
    val amount: Double,
    val date: Date
)

interface DataSource {
    fun getSalesDataForMonth(month: String): List<SalesData>
}

interface EmailService {
    fun sendEmail(to: String, subject: String, body: String)
}

class ReportGenerator(
    private val dataSource: DataSource,
    private val emailService: EmailService
) {

    fun generateAndSendMonthlyReport(month: String, recipientEmail: String) {
        val salesData = dataSource.getSalesDataForMonth(month)

        val report = StringBuilder().apply {
            append("Monthly Sales Report - $month\n")
            append("================================\n\n")
        }

        var totalSales = 0.0
        val productSales = mutableMapOf<String, Double>()

        salesData.forEach { data ->
            totalSales += data.amount
            productSales[data.productName] = productSales.getOrDefault(data.productName, 0.0) + data.amount
        }

        report.apply {
            append("Total Sales: $${String.format("%.2f", totalSales)}\n\n")
            append("Sales by Product:\n")
            append("-----------------\n")
        }

        productSales.forEach { (product, amount) ->
            report.append("$product: $${String.format("%.2f", amount)}\n")
        }

        report.apply {
            append("\nTop Performing Products:\n")
            append("------------------------\n")
        }

        productSales.entries
            .sortedByDescending { it.value }
            .take(5)
            .forEach { (product, amount) ->
                report.append("$product: $${String.format("%.2f", amount)}\n")
            }

        val reportContent = report.toString()

        val subject = "Monthly Sales Report - $month"
        val body = "Please find attached the monthly sales report.\n\n$reportContent"

        emailService.sendEmail(recipientEmail, subject, body)

        println("Report sent successfully to $recipientEmail")
    }
}

