import java.io.*
import java.sql.*
import java.time.LocalDateTime
import java.time.format.DateTimeFormatter
import java.util.*
import javax.mail.*
import javax.mail.internet.*

data class Order(
    val id: Int,
    val customer: Customer,
    val items: List<OrderItem>,
    val totalAmount: Double,
    var status: String
)

data class Customer(
    val id: Int,
    val name: String,
    val email: String
)

data class OrderItem(
    val productId: Int,
    val quantity: Int
)

class OrderManager {
    private val orders: MutableList<Order> = mutableListOf()
    private var dbConnection: Connection? = null
    private val emailProperties: Properties = Properties()

    init {
        initializeDatabase()
        setupEmailConfiguration()
    }

    // Order management responsibility
    fun addOrder(order: Order) {
        if (validateOrder(order)) {
            orders.add(order)
            saveOrderToDatabase(order)
            sendOrderConfirmationEmail(order)
            generateInvoice(order)
            updateInventory(order)
            logOrderActivity(order, "ORDER_CREATED")
        }
    }

    fun updateOrderStatus(orderId: Int, status: String) {
        val order = findOrderById(orderId)
        order?.let {
            it.status = status
            updateOrderInDatabase(it)
            sendStatusUpdateEmail(it)
            logOrderActivity(it, "STATUS_UPDATED")
        }
    }

    fun findOrderById(orderId: Int): Order? {
        return orders.find { it.id == orderId }
    }

    // Validation responsibility
    private fun validateOrder(order: Order?): Boolean {
        if (order?.customer == null) {
            return false
        }
        if (order.items.isEmpty()) {
            return false
        }
        if (order.totalAmount <= 0) {
            return false
        }
        return validateCustomerCreditLimit(order)
    }

    private fun validateCustomerCreditLimit(order: Order): Boolean {
        val customerCreditLimit = getCustomerCreditLimit(order.customer.id)
        val outstandingAmount = getCustomerOutstandingAmount(order.customer.id)
        return (outstandingAmount + order.totalAmount) <= customerCreditLimit
    }

    // Database operations responsibility
    private fun initializeDatabase() {
        try {
            val url = "jdbc:mysql://localhost:3306/orders"
            val username = "admin"
            val password = "password"
            dbConnection = DriverManager.getConnection(url, username, password)
        } catch (e: SQLException) {
            System.err.println("Database connection failed: ${e.message}")
        }
    }

    private fun saveOrderToDatabase(order: Order) {
        try {
            val sql = "INSERT INTO orders (customer_id, total_amount, status, created_date) VALUES (?, ?, ?, ?)"
            dbConnection?.prepareStatement(sql)?.use { stmt ->
                stmt.setInt(1, order.customer.id)
                stmt.setDouble(2, order.totalAmount)
                stmt.setString(3, order.status)
                stmt.setTimestamp(4, Timestamp.valueOf(LocalDateTime.now()))
                stmt.executeUpdate()
            }
        } catch (e: SQLException) {
            System.err.println("Failed to save order: ${e.message}")
        }
    }

    private fun updateOrderInDatabase(order: Order) {
        try {
            val sql = "UPDATE orders SET status = ?, updated_date = ? WHERE id = ?"
            dbConnection?.prepareStatement(sql)?.use { stmt ->
                stmt.setString(1, order.status)
                stmt.setTimestamp(2, Timestamp.valueOf(LocalDateTime.now()))
                stmt.setInt(3, order.id)
                stmt.executeUpdate()
            }
        } catch (e: SQLException) {
            System.err.println("Failed to update order: ${e.message}")
        }
    }

    private fun getCustomerCreditLimit(customerId: Int): Double {
        return try {
            val sql = "SELECT credit_limit FROM customers WHERE id = ?"
            dbConnection?.prepareStatement(sql)?.use { stmt ->
                stmt.setInt(1, customerId)
                val rs = stmt.executeQuery()
                if (rs.next()) {
                    rs.getDouble("credit_limit")
                } else {
                    0.0
                }
            } ?: 0.0
        } catch (e: SQLException) {
            System.err.println("Failed to get credit limit: ${e.message}")
            0.0
        }
    }

    private fun getCustomerOutstandingAmount(customerId: Int): Double {
        return try {
            val sql = "SELECT SUM(total_amount) FROM orders WHERE customer_id = ? AND status = 'PENDING'"
            dbConnection?.prepareStatement(sql)?.use { stmt ->
                stmt.setInt(1, customerId)
                val rs = stmt.executeQuery()
                if (rs.next()) {
                    rs.getDouble(1)
                } else {
                    0.0
                }
            } ?: 0.0
        } catch (e: SQLException) {
            System.err.println("Failed to get outstanding amount: ${e.message}")
            0.0
        }
    }

    // Email notification responsibility
    private fun setupEmailConfiguration() {
        emailProperties.apply {
            put("mail.smtp.host", "smtp.gmail.com")
            put("mail.smtp.port", "587")
            put("mail.smtp.auth", "true")
            put("mail.smtp.starttls.enable", "true")
        }
    }

    private fun sendOrderConfirmationEmail(order: Order) {
        try {
            val session = Session.getInstance(emailProperties)
            val message = MimeMessage(session)
            message.setFrom(InternetAddress("orders@company.com"))
            message.setRecipients(Message.RecipientType.TO, InternetAddress.parse(order.customer.email))
            message.subject = "Order Confirmation - Order #${order.id}"
            message.setText("Your order has been confirmed. Total: $${order.totalAmount}")
            Transport.send(message)
        } catch (e: MessagingException) {
            System.err.println("Failed to send confirmation email: ${e.message}")
        }
    }

    private fun sendStatusUpdateEmail(order: Order) {
        try {
            val session = Session.getInstance(emailProperties)
            val message = MimeMessage(session)
            message.setFrom(InternetAddress("orders@company.com"))
            message.setRecipients(Message.RecipientType.TO, InternetAddress.parse(order.customer.email))
            message.subject = "Order Status Update - Order #${order.id}"
            message.setText("Your order status has been updated to: ${order.status}")
            Transport.send(message)
        } catch (e: MessagingException) {
            System.err.println("Failed to send status email: ${e.message}")
        }
    }

    // Invoice generation responsibility
    private fun generateInvoice(order: Order) {
        try {
            val filename = "invoice_${order.id}.txt"
            FileWriter(filename).use { writer ->
                writer.write("INVOICE\n")
                writer.write("Order ID: ${order.id}\n")
                writer.write("Customer: ${order.customer.name}\n")
                writer.write("Date: ${LocalDateTime.now().format(DateTimeFormatter.ISO_LOCAL_DATE)}\n")
                writer.write("Total Amount: $${order.totalAmount}\n")
            }
        } catch (e: IOException) {
            System.err.println("Failed to generate invoice: ${e.message}")
        }
    }

    // Inventory management responsibility
    private fun updateInventory(order: Order) {
        order.items.forEach { item ->
            try {
                val sql = "UPDATE inventory SET quantity = quantity - ? WHERE product_id = ?"
                dbConnection?.prepareStatement(sql)?.use { stmt ->
                    stmt.setInt(1, item.quantity)
                    stmt.setInt(2, item.productId)
                    stmt.executeUpdate()
                }
            } catch (e: SQLException) {
                System.err.println("Failed to update inventory: ${e.message}")
            }
        }
    }

    // Logging responsibility
    private fun logOrderActivity(order: Order, activity: String) {
        try {
            val filename = "order_log.txt"
            FileWriter(filename, true).use { writer ->
                val logEntry = "${LocalDateTime.now().format(DateTimeFormatter.ISO_LOCAL_DATE_TIME)} - Order ${order.id}: $activity\n"
                writer.write(logEntry)
            }
        } catch (e: IOException) {
            System.err.println("Failed to log activity: ${e.message}")
        }
    }
}

