import java.util.*
import java.io.*
import java.time.LocalDateTime
import java.util.concurrent.ConcurrentHashMap
import java.security.MessageDigest
import javax.mail.*
import javax.mail.internet.*

data class Product(
    val id: String,
    var name: String,
    var quantity: Int,
    var price: Double,
    val supplierId: String,
    val minThreshold: Int,
    var lastUpdated: LocalDateTime
)

data class User(
    val username: String,
    val passwordHash: String,
    val permissions: List<String>
)

data class Supplier(
    val id: String,
    val name: String,
    val email: String,
    val phone: String
)

data class StockAlert(
    val productId: String,
    val productName: String,
    val currentStock: Int,
    val minThreshold: Int
)

class InventoryManager {
    private val products: MutableMap<String, Product> = ConcurrentHashMap()
    private val suppliers: MutableMap<String, Supplier> = ConcurrentHashMap()
    private val users: MutableMap<String, User> = ConcurrentHashMap()
    private val alerts: MutableList<StockAlert> = ArrayList()
    private val emailConfig: Properties = Properties()

    init {
        initializeUsers()
        setupEmailConfiguration()
        loadDataFromFiles()
    }

    fun addProduct(
        username: String,
        password: String,
        productId: String,
        name: String,
        quantity: Int,
        price: Double,
        supplierId: String,
        minThreshold: Int
    ) {
        if (!authenticateUser(username, password)) {
            throw SecurityException("Authentication failed")
        }

        if (!hasPermission(username, "ADD_PRODUCT")) {
            throw SecurityException("Insufficient permissions")
        }

        if (!validateProductData(productId, name, quantity, price, supplierId, minThreshold)) {
            throw IllegalArgumentException("Invalid product data")
        }

        if (products.containsKey(productId)) {
            throw IllegalArgumentException("Product already exists")
        }

        val supplier = suppliers[supplierId]
            ?: throw IllegalArgumentException("Supplier not found")

        val product = Product(
            id = productId,
            name = sanitizeName(name),
            quantity = quantity,
            price = price,
            supplierId = supplierId,
            minThreshold = minThreshold,
            lastUpdated = LocalDateTime.now()
        )
        products[productId] = product

        if (quantity <= minThreshold) {
            createLowStockAlert(product)
        }

        updateInventoryFile()
        logActivity(username, "PRODUCT_ADDED", productId)
        sendSupplierNotification(supplier, "New product added: $name")

        println("Product added: $productId")
    }

    fun updateStock(
        username: String,
        password: String,
        productId: String,
        newQuantity: Int,
        reason: String
    ) {
        if (!authenticateUser(username, password)) {
            throw SecurityException("Authentication failed")
        }

        if (!hasPermission(username, "UPDATE_STOCK")) {
            throw SecurityException("Insufficient permissions")
        }

        val product = products[productId]
            ?: throw IllegalArgumentException("Product not found")

        if (newQuantity < 0) {
            throw IllegalArgumentException("Quantity cannot be negative")
        }

        val oldQuantity = product.quantity
        product.quantity = newQuantity
        product.lastUpdated = LocalDateTime.now()

        if (newQuantity <= product.minThreshold && oldQuantity > product.minThreshold) {
            createLowStockAlert(product)
        }

        if (newQuantity > product.minThreshold) {
            removeLowStockAlert(productId)
        }

        updateInventoryFile()
        logActivity(username, "STOCK_UPDATED", "$productId from $oldQuantity to $newQuantity")

        if (kotlin.math.abs(newQuantity - oldQuantity) > 100) {
            sendManagerAlert("Large stock change for ${product.name}: $oldQuantity -> $newQuantity. Reason: $reason")
        }

        println("Stock updated for $productId: $newQuantity")
    }

    fun generateReport(username: String, password: String, reportType: String) {
        if (!authenticateUser(username, password)) {
            throw SecurityException("Authentication failed")
        }

        if (!hasPermission(username, "GENERATE_REPORTS")) {
            throw SecurityException("Insufficient permissions")
        }

        try {
            val filename = "${reportType}_report_${LocalDateTime.now().toString().replace(":", "-")}.txt"
            FileWriter(filename).use { writer ->
                when (reportType) {
                    "inventory" -> generateInventoryReport(writer)
                    "alerts" -> generateAlertsReport(writer)
                    "suppliers" -> generateSuppliersReport(writer)
                    else -> throw IllegalArgumentException("Unknown report type: $reportType")
                }
            }

            logActivity(username, "REPORT_GENERATED", reportType)
            println("Report generated: $filename")

        } catch (e: IOException) {
            throw RuntimeException("Failed to generate report", e)
        }
    }

    private fun initializeUsers() {
        users["admin"] = User(
            "admin",
            hashPassword("admin123"),
            listOf("ADD_PRODUCT", "UPDATE_STOCK", "GENERATE_REPORTS", "MANAGE_SUPPLIERS")
        )
        users["manager"] = User(
            "manager",
            hashPassword("mgr123"),
            listOf("UPDATE_STOCK", "GENERATE_REPORTS")
        )
        users["clerk"] = User(
            "clerk",
            hashPassword("clerk123"),
            listOf("UPDATE_STOCK")
        )

        suppliers["SUP001"] = Supplier("SUP001", "TechCorp", "tech@corp.com", "123-456-7890")
        suppliers["SUP002"] = Supplier("SUP002", "OfficeSupply", "orders@office.com", "987-654-3210")
    }

    private fun setupEmailConfiguration() {
        emailConfig.apply {
            put("mail.smtp.host", "smtp.company.com")
            put("mail.smtp.port", "587")
            put("mail.smtp.auth", "true")
            put("mail.smtp.starttls.enable", "true")
        }
    }

    private fun authenticateUser(username: String, password: String): Boolean {
        val user = users[username]
        return user != null && user.passwordHash == hashPassword(password)
    }

    private fun hasPermission(username: String, permission: String): Boolean {
        val user = users[username]
        return user != null && user.permissions.contains(permission)
    }

    private fun hashPassword(password: String): String {
        return try {
            val md = MessageDigest.getInstance("SHA-256")
            val hash = md.digest(password.toByteArray())
            Base64.getEncoder().encodeToString(hash)
        } catch (e: Exception) {
            throw RuntimeException("Password hashing failed", e)
        }
    }

    private fun validateProductData(
        productId: String,
        name: String,
        quantity: Int,
        price: Double,
        supplierId: String,
        minThreshold: Int
    ): Boolean {
        return productId.isNotBlank() &&
                name.isNotBlank() &&
                quantity >= 0 && price >= 0 && minThreshold >= 0 &&
                supplierId.isNotBlank()
    }

    private fun sanitizeName(name: String): String {
        return name.replace(Regex("[<>\"'&]"), "").trim()
    }

    private fun createLowStockAlert(product: Product) {
        val alert = StockAlert(
            productId = product.id,
            productName = product.name,
            currentStock = product.quantity,
            minThreshold = product.minThreshold
        )
        alerts.add(alert)

        val message = "LOW STOCK ALERT: ${product.name} (${product.id}) " +
                "has only ${product.quantity} units remaining. " +
                "Minimum threshold: ${product.minThreshold}"

        sendManagerAlert(message)

        suppliers[product.supplierId]?.let { supplier ->
            sendSupplierNotification(supplier, "Reorder needed for: ${product.name}")
        }
    }

    private fun removeLowStockAlert(productId: String) {
        alerts.removeIf { it.productId == productId }
    }

    private fun sendManagerAlert(message: String) {
        try {
            val session = Session.getInstance(emailConfig)
            val email = MimeMessage(session).apply {
                setFrom(InternetAddress("inventory@company.com"))
                setRecipients(Message.RecipientType.TO, InternetAddress.parse("manager@company.com"))
                subject = "Inventory Alert"
                setText("$message\n\nTime: ${LocalDateTime.now()}")
            }

            println("Manager alert sent: $message")
            // Transport.send(email) // Commented out for demo

        } catch (e: MessagingException) {
            System.err.println("Failed to send manager alert: ${e.message}")
        }
    }

    private fun sendSupplierNotification(supplier: Supplier, message: String) {
        try {
            val session = Session.getInstance(emailConfig)
            val email = MimeMessage(session).apply {
                setFrom(InternetAddress("inventory@company.com"))
                setRecipients(Message.RecipientType.TO, InternetAddress.parse(supplier.email))
                subject = "Supplier Notification"
                setText("$message\n\nBest regards,\nInventory Team")
            }

            println("Supplier notification sent to ${supplier.name}: $message")
            // Transport.send(email) // Commented out for demo

        } catch (e: MessagingException) {
            System.err.println("Failed to send supplier notification: ${e.message}")
        }
    }

    private fun generateInventoryReport(writer: FileWriter) {
        writer.write("INVENTORY REPORT\n")
        writer.write("Generated: ${LocalDateTime.now()}\n\n")

        products.values.forEach { product ->
            writer.write("Product: ${product.name} (${product.id})\n")
            writer.write("Quantity: ${product.quantity}\n")
            writer.write("Price: $${product.price}\n")
            writer.write("Supplier: ${suppliers[product.supplierId]?.name}\n\n")
        }
    }

    private fun generateAlertsReport(writer: FileWriter) {
        writer.write("STOCK ALERTS REPORT\n")
        writer.write("Generated: ${LocalDateTime.now()}\n\n")

        alerts.forEach { alert ->
            writer.write("Alert: ${alert.productName} (${alert.productId})\n")
            writer.write("Current Stock: ${alert.currentStock}\n")
            writer.write("Minimum Threshold: ${alert.minThreshold}\n\n")
        }
    }

    private fun generateSuppliersReport(writer: FileWriter) {
        writer.write("SUPPLIERS REPORT\n")
        writer.write("Generated: ${LocalDateTime.now()}\n\n")

        suppliers.values.forEach { supplier ->
            writer.write("Supplier: ${supplier.name} (${supplier.id})\n")
            writer.write("Email: ${supplier.email}\n")
            writer.write("Phone: ${supplier.phone}\n\n")
        }
    }

    private fun updateInventoryFile() {
        try {
            FileWriter("inventory.dat").use { writer ->
                products.values.forEach { product ->
                    writer.write("${product.id}|${product.name}|${product.quantity}|${product.price}|${product.supplierId}|${product.minThreshold}\n")
                }
            }
        } catch (e: IOException) {
            System.err.println("Failed to update inventory file: ${e.message}")
        }
    }

    private fun loadDataFromFiles() {
        try {
            val file = File("inventory.dat")
            if (file.exists()) {
                file.bufferedReader().use { reader ->
                    reader.lineSequence().forEach { line ->
                        val parts = line.split("|")
                        if (parts.size == 6) {
                            val product = Product(
                                id = parts[0],
                                name = parts[1],
                                quantity = parts[2].toInt(),
                                price = parts[3].toDouble(),
                                supplierId = parts[4],
                                minThreshold = parts[5].toInt(),
                                lastUpdated = LocalDateTime.now()
                            )
                            products[product.id] = product
                        }
                    }
                }
            }
        } catch (e: Exception) {
            System.err.println("Failed to load inventory data: ${e.message}")
        }
    }

    private fun logActivity(username: String, action: String, details: String) {
        try {
            FileWriter("inventory_log.txt", true).use { writer ->
                writer.write("${LocalDateTime.now()} - User: $username, Action: $action, Details: $details\n")
            }
        } catch (e: IOException) {
            System.err.println("Failed to log activity: ${e.message}")
        }
    }
}

