import java.io.FileWriter
import java.io.IOException
import java.security.SecureRandom
import java.time.LocalDateTime
import java.time.format.DateTimeFormatter
import java.util.concurrent.ConcurrentHashMap

data class User(
    val email: String,
    val name: String,
    val passwordHash: String,
    val permissions: List<String>
)

data class Event(
    val eventId: String,
    val eventName: String,
    val description: String,
    val venue: String,
    val startTime: LocalDateTime,
    val endTime: LocalDateTime,
    val ticketPrice: Double,
    val maxCapacity: Int,
    val organizerEmail: String
)

class EventManagementSystem {
    private val events = ConcurrentHashMap<String, Event>()
    private val registeredUsers = ConcurrentHashMap<String, User>()
    private val eventRegistrations = ConcurrentHashMap<String, MutableList<String>>()
    private val eventRevenue = ConcurrentHashMap<String, Double>()
    private val random = SecureRandom()
    private var eventIdCounter = 1000

    init {
        initializeTestData()
    }

    fun createEvent(
        organizerEmail: String,
        password: String,
        eventName: String,
        description: String,
        venue: String,
        startTime: LocalDateTime,
        endTime: LocalDateTime,
        ticketPrice: Double,
        maxCapacity: Int
    ) {
        if (!authenticateUser(organizerEmail, password)) {
            throw SecurityException("Invalid credentials")
        }

        val organizer = registeredUsers[organizerEmail]
            ?: throw SecurityException("User not found")

        if (!hasPermission(organizer, "CREATE_EVENT")) {
            throw SecurityException("User does not have permission to create events")
        }

        if (!validateEventData(eventName, description, venue, startTime, endTime, ticketPrice, maxCapacity)) {
            throw IllegalArgumentException("Invalid event data provided")
        }

        val eventId = generateEventId()
        val event = Event(
            eventId,
            sanitizeInput(eventName),
            sanitizeInput(description),
            sanitizeInput(venue),
            startTime,
            endTime,
            ticketPrice,
            maxCapacity,
            organizerEmail
        )

        events[eventId] = event
        eventRegistrations[eventId] = mutableListOf()
        eventRevenue[eventId] = 0.0

        sendEventCreationNotification(organizer, event)
        logActivity(organizerEmail, "EVENT_CREATED", "$eventId - $eventName")
        updateEventWebsite(event)

        println("Event created: $eventId - $eventName")
    }

    fun registerForEvent(
        userEmail: String,
        password: String,
        eventId: String,
        numberOfTickets: Int
    ) {
        if (!authenticateUser(userEmail, password)) {
            throw SecurityException("Invalid credentials")
        }

        val event = events[eventId] ?: throw IllegalArgumentException("Event not found")

        if (event.startTime.isBefore(LocalDateTime.now())) {
            throw IllegalArgumentException("Cannot register for past events")
        }

        val registrations = eventRegistrations[eventId]!!
        if (registrations.size + numberOfTickets > event.maxCapacity) {
            throw IllegalArgumentException("Not enough capacity available")
        }

        if (numberOfTickets <= 0 || numberOfTickets > 10) {
            throw IllegalArgumentException("Invalid number of tickets (1-10 allowed)")
        }

        val totalCost = event.ticketPrice * numberOfTickets
        if (!processPayment(userEmail, totalCost)) {
            throw RuntimeException("Payment processing failed")
        }

        repeat(numberOfTickets) {
            registrations.add(userEmail)
        }

        eventRevenue[eventId] = eventRevenue[eventId]!! + totalCost

        generateTickets(userEmail, event, numberOfTickets)
        sendRegistrationConfirmation(userEmail, event, numberOfTickets, totalCost)
        logActivity(userEmail, "REGISTERED_FOR_EVENT", "$eventId - $numberOfTickets tickets")

        if (registrations.size >= event.maxCapacity) {
            sendEventFullNotification(event)
        }

        println("User $userEmail registered for event $eventId")
    }

    fun generateEventReport(
        organizerEmail: String,
        password: String,
        eventId: String
    ) {
        if (!authenticateUser(organizerEmail, password)) {
            throw SecurityException("Invalid credentials")
        }

        val event = events[eventId] ?: throw IllegalArgumentException("Event not found")

        if (event.organizerEmail != organizerEmail) {
            val user = registeredUsers[organizerEmail]
            if (!hasPermission(user, "VIEW_ALL_REPORTS")) {
                throw SecurityException("No permission to view this event's report")
            }
        }

        try {
            val filename = "event_report_${eventId}_${LocalDateTime.now().format(DateTimeFormatter.ofPattern("yyyyMMdd_HHmmss"))}.txt"

            FileWriter(filename).use { writer ->
                writer.write("EVENT REPORT\n")
                writer.write("Event: ${event.eventName}\n")
                writer.write("Event ID: $eventId\n")
                writer.write("Organizer: ${event.organizerEmail}\n")
                writer.write("Venue: ${event.venue}\n")
                writer.write("Start Time: ${event.startTime}\n")
                writer.write("Ticket Price: $${event.ticketPrice}\n")
                writer.write("Max Capacity: ${event.maxCapacity}\n\n")

                val registrations = eventRegistrations[eventId]!!
                writer.write("Registrations: ${registrations.size}\n")
                writer.write("Revenue: $${eventRevenue[eventId]}\n")
                writer.write("Occupancy Rate: ${String.format("%.1f", registrations.size * 100.0 / event.maxCapacity)}%\n\n")

                val userTicketCounts = mutableMapOf<String, Int>()
                for (email in registrations) {
                    userTicketCounts[email] = userTicketCounts.getOrDefault(email, 0) + 1
                }

                writer.write("Registered Users:\n")
                for ((email, count) in userTicketCounts) {
                    writer.write("- $email: $count ticket(s)\n")
                }
            }

            logActivity(organizerEmail, "REPORT_GENERATED", eventId)
            sendReportNotification(organizerEmail, filename)

            println("Report generated: $filename")

        } catch (e: IOException) {
            throw RuntimeException("Failed to generate report: ${e.message}")
        }
    }

    private fun initializeTestData() {
        registeredUsers["organizer@company.com"] = User(
            "organizer@company.com",
            "John Organizer",
            hashPassword("org123"),
            listOf("CREATE_EVENT", "VIEW_ALL_REPORTS")
        )
        registeredUsers["admin@company.com"] = User(
            "admin@company.com",
            "Admin User",
            hashPassword("admin123"),
            listOf("CREATE_EVENT", "VIEW_ALL_REPORTS")
        )
        registeredUsers["user@company.com"] = User(
            "user@company.com",
            "Regular User",
            hashPassword("user123"),
            emptyList()
        )
    }

    private fun authenticateUser(email: String, password: String): Boolean {
        val user = registeredUsers[email]
        return user != null && user.passwordHash == hashPassword(password)
    }

    private fun hasPermission(user: User?, permission: String): Boolean {
        return user != null && user.permissions.contains(permission)
    }

    private fun hashPassword(password: String): String {
        return "hashed_$password"
    }

    private fun validateEventData(
        eventName: String,
        description: String,
        venue: String,
        startTime: LocalDateTime,
        endTime: LocalDateTime,
        ticketPrice: Double,
        maxCapacity: Int
    ): Boolean {
        return eventName.isNotBlank() &&
                description.isNotBlank() &&
                venue.isNotBlank() &&
                startTime.isBefore(endTime) &&
                startTime.isAfter(LocalDateTime.now()) &&
                ticketPrice >= 0 &&
                maxCapacity > 0
    }

    private fun sanitizeInput(input: String): String {
        return input.replace(Regex("[<>\"'&]"), "").trim()
    }

    @Synchronized
    private fun generateEventId(): String {
        return "EVT${eventIdCounter++}"
    }

    private fun processPayment(userEmail: String, amount: Double): Boolean {
        println("Processing payment of $$amount for user $userEmail")
        return random.nextDouble() > 0.1
    }

    private fun generateTickets(userEmail: String, event: Event, numberOfTickets: Int) {
        try {
            val filename = "tickets_${userEmail.replace("@", "_")}_${event.eventId}.txt"
            FileWriter(filename).use { writer ->
                repeat(numberOfTickets) { i ->
                    writer.write("TICKET #${random.nextInt(100000)}\n")
                    writer.write("Event: ${event.eventName}\n")
                    writer.write("Venue: ${event.venue}\n")
                    writer.write("Date: ${event.startTime.format(DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm"))}\n")
                    writer.write("Holder: $userEmail\n")
                    writer.write("================\n\n")
                }
            }

            println("Tickets generated: $filename")

        } catch (e: IOException) {
            System.err.println("Failed to generate tickets: ${e.message}")
        }
    }

    private fun sendEventCreationNotification(organizer: User, event: Event) {
        println("Email sent to ${organizer.email}: Your event '${event.eventName}' has been created successfully.")
    }

    private fun sendRegistrationConfirmation(
        userEmail: String,
        event: Event,
        numberOfTickets: Int,
        totalCost: Double
    ) {
        println("Email sent to $userEmail: Registration confirmed for '${event.eventName}'. Tickets: $numberOfTickets, Total: $$totalCost")
    }

    private fun sendEventFullNotification(event: Event) {
        println("Email sent to ${event.organizerEmail}: Event '${event.eventName}' is now fully booked.")
    }

    private fun sendReportNotification(organizerEmail: String, filename: String) {
        println("Email sent to $organizerEmail: Event report generated: $filename")
    }

    private fun logActivity(userEmail: String, action: String, details: String) {
        try {
            FileWriter("event_system.log", true).use { writer ->
                writer.write("${LocalDateTime.now().format(DateTimeFormatter.ISO_LOCAL_DATE_TIME)} - User: $userEmail, Action: $action, Details: $details\n")
            }
        } catch (e: IOException) {
            System.err.println("Failed to log activity: ${e.message}")
        }
    }

