public class ReportGenerator {
    private DataSource dataSource;
    private EmailService emailService;

    public ReportGenerator(DataSource dataSource, EmailService emailService) {
        this.dataSource = dataSource;
        this.emailService = emailService;
    }

    public void generateAndSendMonthlyReport(String month, String recipientEmail) {
        List<SalesData> salesData = dataSource.getSalesDataForMonth(month);

        StringBuilder report = new StringBuilder();
        report.append("Monthly Sales Report - " + month + "\n");
        report.append("================================\n\n");

        double totalSales = 0;
        Map<String, Double> productSales = new HashMap<>();

        for (SalesData data : salesData) {
            totalSales += data.getAmount();
            productSales.put(data.getProductName(), 
                productSales.getOrDefault(data.getProductName(), 0.0) + data.getAmount());
        }

        report.append("Total Sales: $" + String.format("%.2f", totalSales) + "\n\n");
        report.append("Sales by Product:\n");
        report.append("-----------------\n");

        for (Map.Entry<String, Double> entry : productSales.entrySet()) {
            report.append(entry.getKey() + ": $" + String.format("%.2f", entry.getValue()) + "\n");
        }

        report.append("\nTop Performing Products:\n");
        report.append("------------------------\n");

        productSales.entrySet().stream()
            .sorted(Map.Entry.<String, Double>comparingByValue().reversed())
            .limit(5)
            .forEach(entry -> report.append(entry.getKey() + ": $" + 
                String.format("%.2f", entry.getValue()) + "\n"));

        String reportContent = report.toString();

        String subject = "Monthly Sales Report - " + month;
        String body = "Please find attached the monthly sales report.\n\n" + reportContent;

        emailService.sendEmail(recipientEmail, subject, body);

        System.out.println("Report sent successfully to " + recipientEmail);
    }
}

class SalesData {
    private String productName;
    private double amount;
    private Date date;

    public SalesData(String productName, double amount, Date date) {
        this.productName = productName;
        this.amount = amount;
        this.date = date;
    }

    public String getProductName() { return productName; }
    public double getAmount() { return amount; }
    public Date getDate() { return date; }
}

interface DataSource {
    List<SalesData> getSalesDataForMonth(String month);
}

interface EmailService {
    void sendEmail(String to, String subject, String body);
}

