public class UserManager {
    private Map<String, User> users = new HashMap<>();
    private Map<String, String> sessions = new HashMap<>();
    private SecureRandom random = new SecureRandom();

    public void createUser(String username, String password, String email) {
        if (users.containsKey(username)) {
            throw new IllegalArgumentException("Username already exists");
        }

        if (!isValidEmail(email)) {
            throw new IllegalArgumentException("Invalid email format");
        }

        if (password.length() < 8) {
            throw new IllegalArgumentException("Password must be at least 8 characters");
        }

        String hashedPassword = hashPassword(password);
        User user = new User(username, hashedPassword, email, new Date());
        users.put(username, user);

        System.out.println("User created successfully: " + username);
    }

    public String login(String username, String password) {
        User user = users.get(username);
        if (user == null) {
            throw new IllegalArgumentException("Invalid username or password");
        }

        if (!verifyPassword(password, user.getPasswordHash())) {
            throw new IllegalArgumentException("Invalid username or password");
        }

        String sessionToken = generateSessionToken();
        sessions.put(sessionToken, username);

        user.setLastLogin(new Date());

        System.out.println("User logged in: " + username);
        return sessionToken;
    }

    public void logout(String sessionToken) {
        String username = sessions.remove(sessionToken);
        if (username != null) {
            System.out.println("User logged out: " + username);
        }
    }

    public boolean isLoggedIn(String sessionToken) {
        return sessions.containsKey(sessionToken);
    }

    public User getUserBySession(String sessionToken) {
        String username = sessions.get(sessionToken);
        if (username == null) {
            return null;
        }
        return users.get(username);
    }

    private boolean isValidEmail(String email) {
        return email.matches("^[A-Za-z0-9+_.-]+@(.+)$");
    }

    private String hashPassword(String password) {
        return Base64.getEncoder().encodeToString(password.getBytes());
    }

    private boolean verifyPassword(String password, String hash) {
        return hashPassword(password).equals(hash);
    }

    private String generateSessionToken() {
        byte[] bytes = new byte[32];
        random.nextBytes(bytes);
        return Base64.getEncoder().encodeToString(bytes);
    }

    public void updateUserEmail(String username, String newEmail) {
        User user = users.get(username);
        if (user == null) {
            throw new IllegalArgumentException("User not found");
        }

        if (!isValidEmail(newEmail)) {
            throw new IllegalArgumentException("Invalid email format");
        }

        user.setEmail(newEmail);
        System.out.println("Email updated for user: " + username);
    }

    public List<User> getAllUsers() {
        return new ArrayList<>(users.values());
    }
}

