public class FileProcessor {
    private String inputDirectory;
    private String outputDirectory;
    private String archiveDirectory;

    public FileProcessor(String inputDirectory, String outputDirectory, String archiveDirectory) {
        this.inputDirectory = inputDirectory;
        this.outputDirectory = outputDirectory;
        this.archiveDirectory = archiveDirectory;
    }

    public void processAndArchiveFiles() {
        File inputDir = new File(inputDirectory);
        File[] files = inputDir.listFiles((dir, name) -> name.endsWith(".csv"));

        if (files == null || files.length == 0) {
            System.out.println("No CSV files found to process");
            return;
        }

        for (File file : files) {
            try {
                List<String> lines = readFile(file);
                List<String> processedLines = new ArrayList<>();

                processedLines.add("PROCESSED_HEADER,ORIGINAL_DATA,TIMESTAMP");

                for (int i = 1; i < lines.size(); i++) {
                    String line = lines.get(i);
                    String[] parts = line.split(",");

                    StringBuilder processedLine = new StringBuilder();
                    processedLine.append("PROCESSED");

                    for (String part : parts) {
                        processedLine.append(",").append(part.trim().toUpperCase());
                    }

                    processedLine.append(",").append(new Date().toString());
                    processedLines.add(processedLine.toString());
                }

                String outputFileName = "processed_" + file.getName();
                File outputFile = new File(outputDirectory, outputFileName);
                writeFile(outputFile, processedLines);

                System.out.println("Processed file: " + file.getName() + " -> " + outputFileName);

                String archiveName = "archive_" + System.currentTimeMillis() + "_" + file.getName();
                File archiveFile = new File(archiveDirectory, archiveName);

                if (file.renameTo(archiveFile)) {
                    System.out.println("Archived original file: " + file.getName() + " -> " + archiveName);
                } else {
                    System.err.println("Failed to archive file: " + file.getName());
                }

            } catch (IOException e) {
                System.err.println("Error processing file " + file.getName() + ": " + e.getMessage());
            }
        }
    }

    private List<String> readFile(File file) throws IOException {
        List<String> lines = new ArrayList<>();
        try (BufferedReader reader = new BufferedReader(new FileReader(file))) {
            String line;
            while ((line = reader.readLine()) != null) {
                lines.add(line);
            }
        }
        return lines;
    }

    private void writeFile(File file, List<String> lines) throws IOException {
        try (BufferedWriter writer = new BufferedWriter(new FileWriter(file))) {
            for (String line : lines) {
                writer.write(line);
                writer.newLine();
            }
        }
    }

    public List<String> getProcessingHistory() {
        List<String> history = new ArrayList<>();
        File archiveDir = new File(archiveDirectory);
        File[] archivedFiles = archiveDir.listFiles();

        if (archivedFiles != null) {
            for (File file : archivedFiles) {
                history.add("Archived: " + file.getName() + " at " + new Date(file.lastModified()));
            }
        }

        return history;
    }
}

