public class CustomerService {
    private Map<String, Customer> customers = new HashMap<>();
    private List<String> blacklistedEmails = Arrays.asList("spam@test.com", "fake@test.com");

    public void registerCustomer(String name, String email, String phone) {
        if (customers.containsKey(email)) {
            throw new IllegalArgumentException("Customer already exists");
        }

        if (blacklistedEmails.contains(email)) {
            throw new IllegalArgumentException("Email is blacklisted");
        }

        if (!email.matches("^[A-Za-z0-9+_.-]+@(.+)$")) {
            throw new IllegalArgumentException("Invalid email format");
        }

        Customer customer = new Customer(UUID.randomUUID().toString(), name, email, phone);
        customers.put(email, customer);

        sendWelcomeNotification(customer);

        System.out.println("Customer registered: " + name);
    }

    public void updateCustomerInfo(String email, String newPhone) {
        Customer customer = customers.get(email);
        if (customer == null) {
            throw new IllegalArgumentException("Customer not found");
        }

        String oldPhone = customer.getPhone();
        customer.setPhone(newPhone);

        sendUpdateNotification(customer, oldPhone, newPhone);

        System.out.println("Customer info updated: " + email);
    }

    private void sendWelcomeNotification(Customer customer) {
        String subject = "Welcome to Our Service!";
        String message = "Dear " + customer.getName() + ",\n\n" +
                        "Thank you for registering with us.\n" +
                        "Your customer ID is: " + customer.getId() + "\n\n" +
                        "Best regards,\nThe Team";

        System.out.println("Sending email to: " + customer.getEmail());
        System.out.println("Subject: " + subject);
        System.out.println("Message: " + message);

        String smsMessage = "Welcome " + customer.getName() + "! Your ID: " + customer.getId();
        System.out.println("Sending SMS to: " + customer.getPhone());
        System.out.println("SMS: " + smsMessage);
    }

    private void sendUpdateNotification(Customer customer, String oldPhone, String newPhone) {
        String subject = "Your Information Has Been Updated";
        String message = "Dear " + customer.getName() + ",\n\n" +
                        "Your phone number has been updated from " + oldPhone + 
                        " to " + newPhone + ".\n\n" +
                        "If you didn't make this change, please contact us.\n\n" +
                        "Best regards,\nThe Team";

        System.out.println("Sending email to: " + customer.getEmail());
        System.out.println("Subject: " + subject);
        System.out.println("Message: " + message);
    }

    public Customer findByEmail(String email) {
        return customers.get(email);
    }

    public List<Customer> getAllCustomers() {
        return new ArrayList<>(customers.values());
    }
}

