import java.util.*;
import java.io.*;
import java.time.LocalDateTime;
import java.time.format.DateTimeFormatter;
import java.sql.*;
import javax.mail.*;
import javax.mail.internet.*;

public class OrderManager {
    private List<Order> orders;
    private Connection dbConnection;
    private Properties emailProperties;

    public OrderManager() {
        this.orders = new ArrayList<>();
        initializeDatabase();
        setupEmailConfiguration();
    }

    // Order management responsibility
    public void addOrder(Order order) {
        if (validateOrder(order)) {
            orders.add(order);
            saveOrderToDatabase(order);
            sendOrderConfirmationEmail(order);
            generateInvoice(order);
            updateInventory(order);
            logOrderActivity(order, "ORDER_CREATED");
        }
    }

    public void updateOrderStatus(int orderId, String status) {
        Order order = findOrderById(orderId);
        if (order != null) {
            order.setStatus(status);
            updateOrderInDatabase(order);
            sendStatusUpdateEmail(order);
            logOrderActivity(order, "STATUS_UPDATED");
        }
    }

    public Order findOrderById(int orderId) {
        return orders.stream()
            .filter(order -> order.getId() == orderId)
            .findFirst()
            .orElse(null);
    }

    // Validation responsibility
    private boolean validateOrder(Order order) {
        if (order == null || order.getCustomer() == null) {
            return false;
        }
        if (order.getItems().isEmpty()) {
            return false;
        }
        if (order.getTotalAmount() <= 0) {
            return false;
        }
        return validateCustomerCreditLimit(order);
    }

    private boolean validateCustomerCreditLimit(Order order) {
        double customerCreditLimit = getCustomerCreditLimit(order.getCustomer().getId());
        double outstandingAmount = getCustomerOutstandingAmount(order.getCustomer().getId());
        return (outstandingAmount + order.getTotalAmount()) <= customerCreditLimit;
    }

    // Database operations responsibility
    private void initializeDatabase() {
        try {
            String url = "jdbc:mysql://localhost:3306/orders";
            String username = "admin";
            String password = "password";
            dbConnection = DriverManager.getConnection(url, username, password);
        } catch (SQLException e) {
            System.err.println("Database connection failed: " + e.getMessage());
        }
    }

    private void saveOrderToDatabase(Order order) {
        try {
            String sql = "INSERT INTO orders (customer_id, total_amount, status, created_date) VALUES (?, ?, ?, ?)";
            PreparedStatement stmt = dbConnection.prepareStatement(sql);
            stmt.setInt(1, order.getCustomer().getId());
            stmt.setDouble(2, order.getTotalAmount());
            stmt.setString(3, order.getStatus());
            stmt.setTimestamp(4, Timestamp.valueOf(LocalDateTime.now()));
            stmt.executeUpdate();
        } catch (SQLException e) {
            System.err.println("Failed to save order: " + e.getMessage());
        }
    }

    private void updateOrderInDatabase(Order order) {
        try {
            String sql = "UPDATE orders SET status = ?, updated_date = ? WHERE id = ?";
            PreparedStatement stmt = dbConnection.prepareStatement(sql);
            stmt.setString(1, order.getStatus());
            stmt.setTimestamp(2, Timestamp.valueOf(LocalDateTime.now()));
            stmt.setInt(3, order.getId());
            stmt.executeUpdate();
        } catch (SQLException e) {
            System.err.println("Failed to update order: " + e.getMessage());
        }
    }

    private double getCustomerCreditLimit(int customerId) {
        try {
            String sql = "SELECT credit_limit FROM customers WHERE id = ?";
            PreparedStatement stmt = dbConnection.prepareStatement(sql);
            stmt.setInt(1, customerId);
            ResultSet rs = stmt.executeQuery();
            if (rs.next()) {
                return rs.getDouble("credit_limit");
            }
        } catch (SQLException e) {
            System.err.println("Failed to get credit limit: " + e.getMessage());
        }
        return 0;
    }

    private double getCustomerOutstandingAmount(int customerId) {
        try {
            String sql = "SELECT SUM(total_amount) FROM orders WHERE customer_id = ? AND status = 'PENDING'";
            PreparedStatement stmt = dbConnection.prepareStatement(sql);
            stmt.setInt(1, customerId);
            ResultSet rs = stmt.executeQuery();
            if (rs.next()) {
                return rs.getDouble(1);
            }
        } catch (SQLException e) {
            System.err.println("Failed to get outstanding amount: " + e.getMessage());
        }
        return 0;
    }

    // Email notification responsibility
    private void setupEmailConfiguration() {
        emailProperties = new Properties();
        emailProperties.put("mail.smtp.host", "smtp.gmail.com");
        emailProperties.put("mail.smtp.port", "587");
        emailProperties.put("mail.smtp.auth", "true");
        emailProperties.put("mail.smtp.starttls.enable", "true");
    }

    private void sendOrderConfirmationEmail(Order order) {
        try {
            Session session = Session.getInstance(emailProperties);
            Message message = new MimeMessage(session);
            message.setFrom(new InternetAddress("orders@company.com"));
            message.setRecipients(Message.RecipientType.TO, 
                InternetAddress.parse(order.getCustomer().getEmail()));
            message.setSubject("Order Confirmation - Order #" + order.getId());
            message.setText("Your order has been confirmed. Total: $" + order.getTotalAmount());
            Transport.send(message);
        } catch (MessagingException e) {
            System.err.println("Failed to send confirmation email: " + e.getMessage());
        }
    }

    private void sendStatusUpdateEmail(Order order) {
        try {
            Session session = Session.getInstance(emailProperties);
            Message message = new MimeMessage(session);
            message.setFrom(new InternetAddress("orders@company.com"));
            message.setRecipients(Message.RecipientType.TO, 
                InternetAddress.parse(order.getCustomer().getEmail()));
            message.setSubject("Order Status Update - Order #" + order.getId());
            message.setText("Your order status has been updated to: " + order.getStatus());
            Transport.send(message);
        } catch (MessagingException e) {
            System.err.println("Failed to send status email: " + e.getMessage());
        }
    }

    // Invoice generation responsibility
    private void generateInvoice(Order order) {
        try {
            String filename = "invoice_" + order.getId() + ".txt";
            FileWriter writer = new FileWriter(filename);
            writer.write("INVOICE\n");
            writer.write("Order ID: " + order.getId() + "\n");
            writer.write("Customer: " + order.getCustomer().getName() + "\n");
            writer.write("Date: " + LocalDateTime.now().format(DateTimeFormatter.ISO_LOCAL_DATE) + "\n");
            writer.write("Total Amount: $" + order.getTotalAmount() + "\n");
            writer.close();
        } catch (IOException e) {
            System.err.println("Failed to generate invoice: " + e.getMessage());
        }
    }

    // Inventory management responsibility
    private void updateInventory(Order order) {
        for (OrderItem item : order.getItems()) {
            try {
                String sql = "UPDATE inventory SET quantity = quantity - ? WHERE product_id = ?";
                PreparedStatement stmt = dbConnection.prepareStatement(sql);
                stmt.setInt(1, item.getQuantity());
                stmt.setInt(2, item.getProductId());
                stmt.executeUpdate();
            } catch (SQLException e) {
                System.err.println("Failed to update inventory: " + e.getMessage());
            }
        }
    }

    // Logging responsibility
    private void logOrderActivity(Order order, String activity) {
        try {
            String filename = "order_log.txt";
            FileWriter writer = new FileWriter(filename, true);
            String logEntry = String.format("%s - Order %d: %s\n", 
                LocalDateTime.now().format(DateTimeFormatter.ISO_LOCAL_DATE_TIME),
                order.getId(), activity);
            writer.write(logEntry);
            writer.close();
        } catch (IOException e) {
            System.err.println("Failed to log activity: " + e.getMessage());
        }
    }
}

