Identify the type of SOLID violation (Single Responsibility Principle, Open-Closed Principle, Liskov Substitution Principle, Interface Segregation Principle) in the following JAVA code. If you cannot find a violation, return NONE:
import java.util.*;
import java.io.*;
import java.time.LocalDateTime;
import java.security.MessageDigest;
import java.util.concurrent.ConcurrentHashMap;
import java.util.regex.Pattern;

public class BlogPostManager {
    private Map<Integer, BlogPost> posts = new ConcurrentHashMap<>();
    private Map<String, List<String>> userSessions = new ConcurrentHashMap<>();
    private Map<String, Integer> userPermissions = new HashMap<>();
    private int nextPostId = 1;

    public BlogPostManager() {
        initializeDefaultUsers();
        setupLogging();
    }

    public void createPost(String sessionToken, String title, String content, List<String> tags) {
        if (!isValidSession(sessionToken)) {
            throw new SecurityException("Invalid session");
        }

        String username = getUserFromSession(sessionToken);
        if (!hasPermission(username, "CREATE_POST")) {
            throw new SecurityException("Insufficient permissions");
        }

        if (!validatePostContent(title, content)) {
            throw new IllegalArgumentException("Invalid post content");
        }

        String sanitizedTitle = sanitizeHtml(title);
        String sanitizedContent = sanitizeHtml(content);
        List<String> processedTags = processTags(tags);

        BlogPost post = new BlogPost(nextPostId++, sanitizedTitle, sanitizedContent, 
                                   username, LocalDateTime.now(), processedTags);
        posts.put(post.getId(), post);

        updateSearchIndex(post);
        generateSitemap();
        notifySubscribers(post);
        logActivity(username, "POST_CREATED", post.getId());

        System.out.println("Blog post created successfully: " + post.getId());
    }

    public void updatePost(String sessionToken, int postId, String newTitle, String newContent) {
        if (!isValidSession(sessionToken)) {
            throw new SecurityException("Invalid session");
        }

        String username = getUserFromSession(sessionToken);
        BlogPost post = posts.get(postId);

        if (post == null) {
            throw new IllegalArgumentException("Post not found");
        }

        if (!post.getAuthor().equals(username) && !hasPermission(username, "EDIT_ANY_POST")) {
            throw new SecurityException("Cannot edit this post");
        }

        if (!validatePostContent(newTitle, newContent)) {
            throw new IllegalArgumentException("Invalid post content");
        }

        post.setTitle(sanitizeHtml(newTitle));
        post.setContent(sanitizeHtml(newContent));
        post.setLastModified(LocalDateTime.now());

        updateSearchIndex(post);
        generateSitemap();
        logActivity(username, "POST_UPDATED", postId);

        System.out.println("Post updated: " + postId);
    }

    public void deletePost(String sessionToken, int postId) {
        if (!isValidSession(sessionToken)) {
            throw new SecurityException("Invalid session");
        }

        String username = getUserFromSession(sessionToken);
        BlogPost post = posts.get(postId);

        if (post == null) {
            throw new IllegalArgumentException("Post not found");
        }

        if (!post.getAuthor().equals(username) && !hasPermission(username, "DELETE_ANY_POST")) {
            throw new SecurityException("Cannot delete this post");
        }

        posts.remove(postId);
        removeFromSearchIndex(postId);
        generateSitemap();
        logActivity(username, "POST_DELETED", postId);

        System.out.println("Post deleted: " + postId);
    }

    private void initializeDefaultUsers() {
        userPermissions.put("admin", 7); // All permissions
        userPermissions.put("editor", 3); // Create and edit
        userPermissions.put("author", 1); // Create only
    }

    private void setupLogging() {
        try {
            File logDir = new File("logs");
            if (!logDir.exists()) {
                logDir.mkdirs();
            }
        } catch (Exception e) {
            System.err.println("Failed to setup logging: " + e.getMessage());
        }
    }

    private boolean isValidSession(String sessionToken) {
        return sessionToken != null && userSessions.containsKey(sessionToken);
    }

    private String getUserFromSession(String sessionToken) {
        List<String> sessionData = userSessions.get(sessionToken);
        return sessionData != null ? sessionData.get(0) : null;
    }

    private boolean hasPermission(String username, String permission) {
        Integer userLevel = userPermissions.get(username);
        if (userLevel == null) return false;

        switch (permission) {
            case "CREATE_POST": return (userLevel & 1) != 0;
            case "EDIT_ANY_POST": return (userLevel & 2) != 0;
            case "DELETE_ANY_POST": return (userLevel & 4) != 0;
            default: return false;
        }
    }

    private boolean validatePostContent(String title, String content) {
        if (title == null || title.trim().isEmpty() || title.length() > 200) {
            return false;
        }
        if (content == null || content.trim().isEmpty() || content.length() > 10000) {
            return false;
        }

        Pattern scriptPattern = Pattern.compile("<script[^>]*>.*?</script>", Pattern.CASE_INSENSITIVE);
        return !scriptPattern.matcher(title).find() && !scriptPattern.matcher(content).find();
    }

    private String sanitizeHtml(String input) {
        if (input == null) return "";
        return input.replaceAll("<script[^>]*>.*?</script>", "")
                   .replaceAll("<[^>]+>", "")
                   .replaceAll("&", "&amp;")
                   .replaceAll("<", "&lt;")
                   .replaceAll(">", "&gt;");
    }

    private List<String> processTags(List<String> tags) {
        if (tags == null) return new ArrayList<>();

        return tags.stream()
                  .map(tag -> tag.toLowerCase().trim())
                  .filter(tag -> !tag.isEmpty() && tag.length() <= 50)
                  .distinct()
                  .collect(ArrayList::new, (list, item) -> list.add(item), (list1, list2) -> list1.addAll(list2));
    }

    private void updateSearchIndex(BlogPost post) {
        try {
            String indexFile = "search_index.txt";
            FileWriter writer = new FileWriter(indexFile, true);
            writer.write(post.getId() + "|" + post.getTitle() + "|" + post.getContent() + "\n");
            writer.close();
        } catch (IOException e) {
            System.err.println("Failed to update search index: " + e.getMessage());
        }
    }

    private void removeFromSearchIndex(int postId) {
        try {
            File indexFile = new File("search_index.txt");
            if (!indexFile.exists()) return;

            List<String> lines = new ArrayList<>();
            BufferedReader reader = new BufferedReader(new FileReader(indexFile));
            String line;
            while ((line = reader.readLine()) != null) {
                if (!line.startsWith(postId + "|")) {
                    lines.add(line);
                }
            }
            reader.close();

            FileWriter writer = new FileWriter(indexFile);
            for (String l : lines) {
                writer.write(l + "\n");
            }
            writer.close();
        } catch (IOException e) {
            System.err.println("Failed to update search index: " + e.getMessage());
        }
    }

    private void generateSitemap() {
        try {
            FileWriter writer = new FileWriter("sitemap.xml");
            writer.write("<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n");
            writer.write("<urlset xmlns=\"http://www.sitemaps.org/schemas/sitemap/0.9\">\n");

            for (BlogPost post : posts.values()) {
                writer.write("  <url>\n");
                writer.write("    <loc>https://blog.com/post/" + post.getId() + "</loc>\n");
                writer.write("    <lastmod>" + post.getLastModified() + "</lastmod>\n");
                writer.write("  </url>\n");
            }

            writer.write("</urlset>\n");
            writer.close();
        } catch (IOException e) {
            System.err.println("Failed to generate sitemap: " + e.getMessage());
        }
    }

    private void notifySubscribers(BlogPost post) {
        System.out.println("Sending notifications for new post: " + post.getTitle());
        // Simulate email notifications
        try {
            Thread.sleep(100); // Simulate network delay
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
        }
    }

    private void logActivity(String username, String action, int postId) {
        try {
            String logFile = "logs/activity_" + LocalDateTime.now().toLocalDate() + ".log";
            FileWriter writer = new FileWriter(logFile, true);
            String logEntry = String.format("%s - User: %s, Action: %s, Post: %d\n",
                                           LocalDateTime.now(), username, action, postId);
            writer.write(logEntry);
            writer.close();
        } catch (IOException e) {
            System.err.println("Failed to log activity: " + e.getMessage());
        }
    }

    public List<BlogPost> searchPosts(String query) {
        List<BlogPost> results = new ArrayList<>();
        String lowerQuery = query.toLowerCase();

        for (BlogPost post : posts.values()) {
            if (post.getTitle().toLowerCase().contains(lowerQuery) ||
