import java.util.*;
import java.io.*;
import java.time.LocalDateTime;
import java.util.concurrent.ConcurrentHashMap;
import java.security.MessageDigest;
import javax.mail.*;
import javax.mail.internet.*;

public class InventoryManager {
    private Map<String, Product> products = new ConcurrentHashMap<>();
    private Map<String, Supplier> suppliers = new ConcurrentHashMap<>();
    private Map<String, User> users = new ConcurrentHashMap<>();
    private List<StockAlert> alerts = new ArrayList<>();
    private Properties emailConfig;

    public InventoryManager() {
        initializeUsers();
        setupEmailConfiguration();
        loadDataFromFiles();
    }

    public void addProduct(String username, String password, String productId, String name, 
                          int quantity, double price, String supplierId, int minThreshold) {
        if (!authenticateUser(username, password)) {
            throw new SecurityException("Authentication failed");
        }

        if (!hasPermission(username, "ADD_PRODUCT")) {
            throw new SecurityException("Insufficient permissions");
        }

        if (!validateProductData(productId, name, quantity, price, supplierId, minThreshold)) {
            throw new IllegalArgumentException("Invalid product data");
        }

        if (products.containsKey(productId)) {
            throw new IllegalArgumentException("Product already exists");
        }

        Supplier supplier = suppliers.get(supplierId);
        if (supplier == null) {
            throw new IllegalArgumentException("Supplier not found");
        }

        Product product = new Product(productId, sanitizeName(name), quantity, price, 
                                    supplierId, minThreshold, LocalDateTime.now());
        products.put(productId, product);

        if (quantity <= minThreshold) {
            createLowStockAlert(product);
        }

        updateInventoryFile();
        logActivity(username, "PRODUCT_ADDED", productId);
        sendSupplierNotification(supplier, "New product added: " + name);

        System.out.println("Product added: " + productId);
    }

    public void updateStock(String username, String password, String productId, 
                           int newQuantity, String reason) {
        if (!authenticateUser(username, password)) {
            throw new SecurityException("Authentication failed");
        }

        if (!hasPermission(username, "UPDATE_STOCK")) {
            throw new SecurityException("Insufficient permissions");
        }

        Product product = products.get(productId);
        if (product == null) {
            throw new IllegalArgumentException("Product not found");
        }

        if (newQuantity < 0) {
            throw new IllegalArgumentException("Quantity cannot be negative");
        }

        int oldQuantity = product.getQuantity();
        product.setQuantity(newQuantity);
        product.setLastUpdated(LocalDateTime.now());

        if (newQuantity <= product.getMinThreshold() && oldQuantity > product.getMinThreshold()) {
            createLowStockAlert(product);
        }

        if (newQuantity > product.getMinThreshold()) {
            removeLowStockAlert(productId);
        }

        updateInventoryFile();
        logActivity(username, "STOCK_UPDATED", productId + " from " + oldQuantity + " to " + newQuantity);

        if (Math.abs(newQuantity - oldQuantity) > 100) {
            sendManagerAlert("Large stock change for " + product.getName() + ": " + 
                           oldQuantity + " -> " + newQuantity + ". Reason: " + reason);
        }

        System.out.println("Stock updated for " + productId + ": " + newQuantity);
    }

    public void generateReport(String username, String password, String reportType) {
        if (!authenticateUser(username, password)) {
            throw new SecurityException("Authentication failed");
        }

        if (!hasPermission(username, "GENERATE_REPORTS")) {
            throw new SecurityException("Insufficient permissions");
        }

        try {
            String filename = reportType + "_report_" + 
                            LocalDateTime.now().toString().replace(":", "-") + ".txt";
            FileWriter writer = new FileWriter(filename);

            if ("inventory".equals(reportType)) {
                generateInventoryReport(writer);
            } else if ("alerts".equals(reportType)) {
                generateAlertsReport(writer);
            } else if ("suppliers".equals(reportType)) {
                generateSuppliersReport(writer);
            } else {
                throw new IllegalArgumentException("Unknown report type: " + reportType);
            }

            writer.close();
            logActivity(username, "REPORT_GENERATED", reportType);
            System.out.println("Report generated: " + filename);

        } catch (IOException e) {
            throw new RuntimeException("Failed to generate report", e);
        }
    }

    private void initializeUsers() {
        users.put("admin", new User("admin", hashPassword("admin123"), 
                  Arrays.asList("ADD_PRODUCT", "UPDATE_STOCK", "GENERATE_REPORTS", "MANAGE_SUPPLIERS")));
        users.put("manager", new User("manager", hashPassword("mgr123"), 
                  Arrays.asList("UPDATE_STOCK", "GENERATE_REPORTS")));
        users.put("clerk", new User("clerk", hashPassword("clerk123"), 
                  Arrays.asList("UPDATE_STOCK")));

        suppliers.put("SUP001", new Supplier("SUP001", "TechCorp", "tech@corp.com", "123-456-7890"));
        suppliers.put("SUP002", new Supplier("SUP002", "OfficeSupply", "orders@office.com", "987-654-3210"));
    }

    private void setupEmailConfiguration() {
        emailConfig = new Properties();
        emailConfig.put("mail.smtp.host", "smtp.company.com");
        emailConfig.put("mail.smtp.port", "587");
        emailConfig.put("mail.smtp.auth", "true");
        emailConfig.put("mail.smtp.starttls.enable", "true");
    }

    private boolean authenticateUser(String username, String password) {
        User user = users.get(username);
        return user != null && user.getPasswordHash().equals(hashPassword(password));
    }

    private boolean hasPermission(String username, String permission) {
        User user = users.get(username);
        return user != null && user.getPermissions().contains(permission);
    }

    private String hashPassword(String password) {
        try {
            MessageDigest md = MessageDigest.getInstance("SHA-256");
            byte[] hash = md.digest(password.getBytes());
            return Base64.getEncoder().encodeToString(hash);
        } catch (Exception e) {
            throw new RuntimeException("Password hashing failed", e);
        }
    }

    private boolean validateProductData(String productId, String name, int quantity, 
                                       double price, String supplierId, int minThreshold) {
        return productId != null && !productId.trim().isEmpty() &&
               name != null && !name.trim().isEmpty() &&
               quantity >= 0 && price >= 0 && minThreshold >= 0 &&
               supplierId != null && !supplierId.trim().isEmpty();
    }

    private String sanitizeName(String name) {
        return name.replaceAll("[<>\"'&]", "").trim();
    }

    private void createLowStockAlert(Product product) {
        StockAlert alert = new StockAlert(product.getId(), product.getName(), 
                                        product.getQuantity(), product.getMinThreshold());
        alerts.add(alert);

        String message = "LOW STOCK ALERT: " + product.getName() + " (" + product.getId() + ") " +
                        "has only " + product.getQuantity() + " units remaining. " +
                        "Minimum threshold: " + product.getMinThreshold();

        sendManagerAlert(message);

        Supplier supplier = suppliers.get(product.getSupplierId());
        if (supplier != null) {
            sendSupplierNotification(supplier, "Reorder needed for: " + product.getName());
        }
    }

    private void removeLowStockAlert(String productId) {
        alerts.removeIf(alert -> alert.getProductId().equals(productId));
    }

    private void sendManagerAlert(String message) {
        try {
            Session session = Session.getInstance(emailConfig);
            Message email = new MimeMessage(session);
            email.setFrom(new InternetAddress("inventory@company.com"));
            email.setRecipients(Message.RecipientType.TO, 
                              InternetAddress.parse("manager@company.com"));
            email.setSubject("Inventory Alert");
            email.setText(message + "\n\nTime: " + LocalDateTime.now());

            System.out.println("Manager alert sent: " + message);
            // Transport.send(email); // Commented out for demo

        } catch (MessagingException e) {
            System.err.println("Failed to send manager alert: " + e.getMessage());
        }
    }

    private void sendSupplierNotification(Supplier supplier, String message) {
        try {
            Session session = Session.getInstance(emailConfig);
            Message email = new MimeMessage(session);
            email.setFrom(new InternetAddress("inventory@company.com"));
            email.setRecipients(Message.RecipientType.TO, 
                              InternetAddress.parse(supplier.getEmail()));
            email.setSubject("Supplier Notification");
            email.setText(message + "\n\nBest regards,\nInventory Team");

            System.out.println("Supplier notification sent to " + supplier.getName() + ": " + message);
            // Transport.send(email); // Commented out for demo

        } catch (MessagingException e) {
            System.err.println("Failed to send supplier notification: " + e.getMessage());
        }
    }

    private void generateInventoryReport(FileWriter writer) throws IOException {
        writer.write("INVENTORY REPORT\n");
        writer.write("Generated: " + LocalDateTime.now() + "\n\n");

        for (Product product : products.values()) {
            writer.write("Product: " + product.getName() + " (" + product.getId() + ")\n");
            writer.write("Quantity: " + product.getQuantity() + "\n");
            writer.write("Price: $" + product.getPrice() + "\n");
            writer.write("Supplier: " + suppliers.get(product.getSupplierId()).getName() + "\n\n");
        }
    }

    private void generateAlertsReport(FileWriter writer) throws IOException {
        writer.write("STOCK ALERTS REPORT\n");
        writer.write("Generated: " + LocalDateTime.now() + "\n\n");

        for (StockAlert alert : alerts) {
            writer.write("Alert: " + alert.getProductName() + " (" + alert.getProductId() + ")\n");
            writer.write("Current Stock: " + alert.getCurrentStock() + "\n");
            writer.write("Minimum Threshold: " + alert.getMinThreshold() + "\n\n");
        }
    }

    private void generateSuppliersReport(FileWriter writer) throws IOException {
        writer.write("SUPPLIERS REPORT\n");
        writer.write("Generated: " + LocalDateTime.now() + "\n\n");

        for (Supplier supplier : suppliers.values()) {
            writer.write("Supplier: " + supplier.getName() + " (" + supplier.getId() + ")\n");
            writer.write("Email: " + supplier.getEmail() + "\n");
            writer.write("Phone: " + supplier.getPhone() + "\n\n");
        }
    }

    private void updateInventoryFile() {
        try {
            FileWriter writer = new FileWriter("inventory.dat");
            for (Product product : products.values()) {
                writer.write(product.getId() + "|" + product.getName() + "|" + 
                           product.getQuantity() + "|" + product.getPrice() + "|" + 
                           product.getSupplierId() + "|" + product.getMinThreshold() + "\n");
            }
            writer.close();
        } catch (IOException e) {
            System.err.println("Failed to update inventory file: " + e.getMessage());
        }
    }

    private void loadDataFromFiles() {
        try {
            File file = new File("inventory.dat");
            if (file.exists()) {
                BufferedReader reader = new BufferedReader(new FileReader(file));
                String line;
                while ((line = reader.readLine()) != null) {
                    String[] parts = line.split("\\|");
                    if (parts.length == 6) {
                        Product product = new Product(parts[0], parts[1], 
                                                    Integer.parseInt(parts[2]), 
                                                    Double.parseDouble(parts[3]), 
                                                    parts[4], Integer.parseInt(parts[5]), 
                                                    LocalDateTime.now());
                        products.put(product.getId(), product);
                    }
                }
                reader.close();
            }
        } catch (Exception e) {
            System.err.println("Failed to load inventory data: " + e.getMessage());
        }
    }

    private void logActivity(String username, String action, String details) {
        try {
            FileWriter writer = new FileWriter("inventory_log.txt", true);
            writer.write(LocalDateTime.now() + " - User: " + username + 
                        ", Action: " + action + ", Details: " + details + "\n");
            writer.close();
        } catch (IOException e) {
            System.err.println("Failed to log activity: " + e.getMessage());
        }
    }
}

