import java.util.*;
import java.io.*;
import java.time.LocalDateTime;
import java.time.format.DateTimeFormatter;
import java.util.concurrent.ConcurrentHashMap;
import java.security.SecureRandom;

public class EventManagementSystem {
    private Map<String, Event> events = new ConcurrentHashMap<>();
    private Map<String, User> registeredUsers = new ConcurrentHashMap<>();
    private Map<String, List<String>> eventRegistrations = new ConcurrentHashMap<>();
    private Map<String, Double> eventRevenue = new ConcurrentHashMap<>();
    private SecureRandom random = new SecureRandom();
    private int eventIdCounter = 1000;

    public EventManagementSystem() {
        initializeTestData();
    }

    public void createEvent(String organizerEmail, String password, String eventName, 
                           String description, String venue, LocalDateTime startTime, 
                           LocalDateTime endTime, double ticketPrice, int maxCapacity) {

        if (!authenticateUser(organizerEmail, password)) {
            throw new SecurityException("Invalid credentials");
        }

        User organizer = registeredUsers.get(organizerEmail);
        if (!hasPermission(organizer, "CREATE_EVENT")) {
            throw new SecurityException("User does not have permission to create events");
        }

        if (!validateEventData(eventName, description, venue, startTime, endTime, ticketPrice, maxCapacity)) {
            throw new IllegalArgumentException("Invalid event data provided");
        }

        String eventId = generateEventId();
        Event event = new Event(eventId, sanitizeInput(eventName), sanitizeInput(description), 
                               sanitizeInput(venue), startTime, endTime, ticketPrice, maxCapacity, organizerEmail);

        events.put(eventId, event);
        eventRegistrations.put(eventId, new ArrayList<>());
        eventRevenue.put(eventId, 0.0);

        sendEventCreationNotification(organizer, event);
        logActivity(organizerEmail, "EVENT_CREATED", eventId + " - " + eventName);
        updateEventWebsite(event);

        System.out.println("Event created: " + eventId + " - " + eventName);
    }

    public void registerForEvent(String userEmail, String password, String eventId, int numberOfTickets) {
        if (!authenticateUser(userEmail, password)) {
            throw new SecurityException("Invalid credentials");
        }

        Event event = events.get(eventId);
        if (event == null) {
            throw new IllegalArgumentException("Event not found");
        }

        if (event.getStartTime().isBefore(LocalDateTime.now())) {
            throw new IllegalArgumentException("Cannot register for past events");
        }

        List<String> registrations = eventRegistrations.get(eventId);
        if (registrations.size() + numberOfTickets > event.getMaxCapacity()) {
            throw new IllegalArgumentException("Not enough capacity available");
        }

        if (numberOfTickets <= 0 || numberOfTickets > 10) {
            throw new IllegalArgumentException("Invalid number of tickets (1-10 allowed)");
        }

        double totalCost = event.getTicketPrice() * numberOfTickets;
        if (!processPayment(userEmail, totalCost)) {
            throw new RuntimeException("Payment processing failed");
        }

        for (int i = 0; i < numberOfTickets; i++) {
            registrations.add(userEmail);
        }

        eventRevenue.put(eventId, eventRevenue.get(eventId) + totalCost);

        generateTickets(userEmail, event, numberOfTickets);
        sendRegistrationConfirmation(userEmail, event, numberOfTickets, totalCost);
        logActivity(userEmail, "REGISTERED_FOR_EVENT", eventId + " - " + numberOfTickets + " tickets");

        if (registrations.size() >= event.getMaxCapacity()) {
            sendEventFullNotification(event);
        }

        System.out.println("User " + userEmail + " registered for event " + eventId);
    }

    public void generateEventReport(String organizerEmail, String password, String eventId) {
        if (!authenticateUser(organizerEmail, password)) {
            throw new SecurityException("Invalid credentials");
        }

        Event event = events.get(eventId);
        if (event == null) {
            throw new IllegalArgumentException("Event not found");
        }

        if (!event.getOrganizerEmail().equals(organizerEmail)) {
            User user = registeredUsers.get(organizerEmail);
            if (!hasPermission(user, "VIEW_ALL_REPORTS")) {
                throw new SecurityException("No permission to view this event's report");
            }
        }

        try {
            String filename = "event_report_" + eventId + "_" + 
                            LocalDateTime.now().format(DateTimeFormatter.ofPattern("yyyyMMdd_HHmmss")) + ".txt";

            FileWriter writer = new FileWriter(filename);

            writer.write("EVENT REPORT\n");
            writer.write("Event: " + event.getEventName() + "\n");
            writer.write("Event ID: " + eventId + "\n");
            writer.write("Organizer: " + event.getOrganizerEmail() + "\n");
            writer.write("Venue: " + event.getVenue() + "\n");
            writer.write("Start Time: " + event.getStartTime() + "\n");
            writer.write("Ticket Price: $" + event.getTicketPrice() + "\n");
            writer.write("Max Capacity: " + event.getMaxCapacity() + "\n\n");

            List<String> registrations = eventRegistrations.get(eventId);
            writer.write("Registrations: " + registrations.size() + "\n");
            writer.write("Revenue: $" + eventRevenue.get(eventId) + "\n");
            writer.write("Occupancy Rate: " + String.format("%.1f", (registrations.size() * 100.0 / event.getMaxCapacity())) + "%\n\n");

            Map<String, Integer> userTicketCounts = new HashMap<>();
            for (String email : registrations) {
                userTicketCounts.put(email, userTicketCounts.getOrDefault(email, 0) + 1);
            }

            writer.write("Registered Users:\n");
            for (Map.Entry<String, Integer> entry : userTicketCounts.entrySet()) {
                writer.write("- " + entry.getKey() + ": " + entry.getValue() + " ticket(s)\n");
            }

            writer.close();

            logActivity(organizerEmail, "REPORT_GENERATED", eventId);
            sendReportNotification(organizerEmail, filename);

            System.out.println("Report generated: " + filename);

        } catch (IOException e) {
            throw new RuntimeException("Failed to generate report: " + e.getMessage());
        }
    }

    private void initializeTestData() {
        registeredUsers.put("organizer@company.com", 
            new User("organizer@company.com", "John Organizer", hashPassword("org123"), 
                    Arrays.asList("CREATE_EVENT", "VIEW_ALL_REPORTS")));
        registeredUsers.put("admin@company.com", 
            new User("admin@company.com", "Admin User", hashPassword("admin123"), 
                    Arrays.asList("CREATE_EVENT", "VIEW_ALL_REPORTS")));
        registeredUsers.put("user@company.com", 
            new User("user@company.com", "Regular User", hashPassword("user123"), 
                    Arrays.asList()));
    }

    private boolean authenticateUser(String email, String password) {
        User user = registeredUsers.get(email);
        return user != null && user.getPasswordHash().equals(hashPassword(password));
    }

    private boolean hasPermission(User user, String permission) {
        return user != null && user.getPermissions().contains(permission);
    }

    private String hashPassword(String password) {
        return "hashed_" + password;
    }

    private boolean validateEventData(String eventName, String description, String venue, 
                                     LocalDateTime startTime, LocalDateTime endTime, 
                                     double ticketPrice, int maxCapacity) {
        return eventName != null && !eventName.trim().isEmpty() && 
               description != null && venue != null && !venue.trim().isEmpty() &&
               startTime != null && endTime != null && startTime.isBefore(endTime) &&
               startTime.isAfter(LocalDateTime.now()) && ticketPrice >= 0 && maxCapacity > 0;
    }

    private String sanitizeInput(String input) {
        if (input == null) return "";
        return input.replaceAll("[<>\"'&]", "").trim();
    }

    private String generateEventId() {
        return "EVT" + (eventIdCounter++);
    }

    private boolean processPayment(String userEmail, double amount) {
        System.out.println("Processing payment of $" + amount + " for user " + userEmail);
        return random.nextDouble() > 0.1;
    }

    private void generateTickets(String userEmail, Event event, int numberOfTickets) {
        try {
            String filename = "tickets_" + userEmail.replace("@", "_") + "_" + event.getEventId() + ".txt";
            FileWriter writer = new FileWriter(filename);

            for (int i = 1; i <= numberOfTickets; i++) {
                writer.write("TICKET #" + random.nextInt(100000) + "\n");
                writer.write("Event: " + event.getEventName() + "\n");
                writer.write("Venue: " + event.getVenue() + "\n");
                writer.write("Date: " + event.getStartTime().format(DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm")) + "\n");
                writer.write("Holder: " + userEmail + "\n");
                writer.write("================\n\n");
            }

            writer.close();
            System.out.println("Tickets generated: " + filename);

        } catch (IOException e) {
            System.err.println("Failed to generate tickets: " + e.getMessage());
        }
    }

    private void sendEventCreationNotification(User organizer, Event event) {
        System.out.println("Email sent to " + organizer.getEmail() + ": Your event '" + 
                         event.getEventName() + "' has been created successfully.");
    }

    private void sendRegistrationConfirmation(String userEmail, Event event, int numberOfTickets, double totalCost) {
        System.out.println("Email sent to " + userEmail + ": Registration confirmed for '" + 
                         event.getEventName() + "'. Tickets: " + numberOfTickets + ", Total: $" + totalCost);
    }

    private void sendEventFullNotification(Event event) {
        System.out.println("Email sent to " + event.getOrganizerEmail() + ": Event '" + 
                         event.getEventName() + "' is now fully booked.");
    }

    private void sendReportNotification(String organizerEmail, String filename) {
        System.out.println("Email sent to " + organizerEmail + ": Event report generated: " + filename);
    }

    private void logActivity(String userEmail, String action, String details) {
        try {
            FileWriter writer = new FileWriter("event_system.log", true);
            writer.write(LocalDateTime.now().format(DateTimeFormatter.ISO_LOCAL_DATE_TIME) + 
                        " - User: " + userEmail + ", Action: " + action + ", Details: " + details + "\n");
            writer.close();
        } catch (IOException e) {
            System.err.println("Failed to log activity: " + e.getMessage());
        }
    }

    private void updateEventWebsite(Event event) {
        System.out.println("Updating website with event information for: " + event.getEventName());
    }
}

