            print(f"Failed to update search index: {e}")

    def generate_sitemap(self):
        try:
            with open("sitemap.xml", "w") as f:
                f.write('<?xml version="1.0" encoding="UTF-8"?>\n')
                f.write('<urlset xmlns="http://www.sitemaps.org/schemas/sitemap/0.9">\n')

                for post in self.posts.values():
                    f.write('  <url>\n')
                    f.write(f'    <loc>https://blog.com/post/{post.id}</loc>\n')
                    f.write(f'    <lastmod>{post.last_modified}</lastmod>\n')
                    f.write('  </url>\n')

                f.write('</urlset>\n')
        except IOError as e:
            print(f"Failed to generate sitemap: {e}")

    def notify_subscribers(self, post: 'BlogPost'):
        print(f"Sending notifications for new post: {post.title}")
        time.sleep(0.1)  # Simulate network delay

    def log_activity(self, username: str, action: str, post_id: int):
        try:
            log_file = f"logs/activity_{datetime.now().date()}.log"
            with open(log_file, "a") as f:
                log_entry = f"{datetime.now()} - User: {username}, Action: {action}, Post: {post_id}\n"
                f.write(log_entry)
        except IOError as e:
            print(f"Failed to log activity: {e}")

    def search_posts(self, query: str) -> List['BlogPost']:
        results = []
        lower_query = query.lower()

        for post in self.posts.values():
            if (lower_query in post.title.lower() or 
                lower_query in post.content.lower() or 
                any(lower_query in tag for tag in post.tags)):
                results.append(post)

        return results

    def login_user(self, username: str, password: str) -> str:
        hashed_password = self.hash_password(password)
        if self.validate_credentials(username, hashed_password):
            session_token = self.generate_session_token(username)
            self.user_sessions[session_token] = [username, str(datetime.now())]
            self.log_activity(username, "LOGIN", 0)
            return session_token
        raise SecurityError("Invalid credentials")

    def hash_password(self, password: str) -> str:
        return hashlib.sha256(password.encode()).hexdigest()

    def validate_credentials(self, username: str, hashed_password: str) -> bool:
        return username in self.user_permissions

    def generate_session_token(self, username: str) -> str:
        return self.hash_password(f"{username}{int(time.time() * 1000)}")

class BlogPost:
    def __init__(self, post_id: int, title: str, content: str, author: str, 
                 created_at: datetime, tags: List[str]):
        self.id = post_id
        self.title = title
        self.content = content
        self.author = author
        self.created_at = created_at
        self.last_modified = created_at
        self.tags = tags or []

