import hashlib
import base64
import json
import os
from datetime import datetime
from typing import Dict, List, Optional
from dataclasses import dataclass
from concurrent.futures import ThreadPoolExecutor
import smtplib
from email.mime.text import MIMEText
from email.mime.multipart import MIMEMultipart

@dataclass
class Product:
    id: str
    name: str
    quantity: int
    price: float
    supplier_id: str
    min_threshold: int
    last_updated: datetime

@dataclass
class User:
    username: str
    password_hash: str
    permissions: List[str]

@dataclass
class Supplier:
    id: str
    name: str
    email: str
    phone: str

@dataclass
class StockAlert:
    product_id: str
    product_name: str
    current_stock: int
    min_threshold: int

class InventoryManager:
    def __init__(self):
        self.products: Dict[str, Product] = {}
        self.suppliers: Dict[str, Supplier] = {}
        self.users: Dict[str, User] = {}
        self.alerts: List[StockAlert] = []
        self.email_config = {
            'smtp_host': 'smtp.company.com',
            'smtp_port': 587,
            'use_tls': True,
            'username': 'inventory@company.com',
            'password': 'email_password'
        }

        self._initialize_users()
        self._setup_email_configuration()
        self._load_data_from_files()

    def add_product(self, username: str, password: str, product_id: str, name: str,
                   quantity: int, price: float, supplier_id: str, min_threshold: int):
        if not self._authenticate_user(username, password):
            raise SecurityError("Authentication failed")

        if not self._has_permission(username, "ADD_PRODUCT"):
            raise SecurityError("Insufficient permissions")

        if not self._validate_product_data(product_id, name, quantity, price, supplier_id, min_threshold):
            raise ValueError("Invalid product data")

        if product_id in self.products:
            raise ValueError("Product already exists")

        if supplier_id not in self.suppliers:
            raise ValueError("Supplier not found")

        product = Product(
            id=product_id,
            name=self._sanitize_name(name),
            quantity=quantity,
            price=price,
            supplier_id=supplier_id,
            min_threshold=min_threshold,
            last_updated=datetime.now()
        )
        self.products[product_id] = product

        if quantity <= min_threshold:
            self._create_low_stock_alert(product)

        self._update_inventory_file()
        self._log_activity(username, "PRODUCT_ADDED", product_id)
        self._send_supplier_notification(self.suppliers[supplier_id], f"New product added: {name}")

        print(f"Product added: {product_id}")

    def update_stock(self, username: str, password: str, product_id: str, new_quantity: int, reason: str):
        if not self._authenticate_user(username, password):
            raise SecurityError("Authentication failed")

        if not self._has_permission(username, "UPDATE_STOCK"):
            raise SecurityError("Insufficient permissions")

        if product_id not in self.products:
            raise ValueError("Product not found")

        if new_quantity < 0:
            raise ValueError("Quantity cannot be negative")

        product = self.products[product_id]
        old_quantity = product.quantity
        product.quantity = new_quantity
        product.last_updated = datetime.now()

        if new_quantity <= product.min_threshold and old_quantity > product.min_threshold:
            self._create_low_stock_alert(product)

        if new_quantity > product.min_threshold:
            self._remove_low_stock_alert(product_id)

        self._update_inventory_file()
        self._log_activity(username, "STOCK_UPDATED", f"{product_id} from {old_quantity} to {new_quantity}")

        if abs(new_quantity - old_quantity) > 100:
            self._send_manager_alert(f"Large stock change for {product.name}: {old_quantity} -> {new_quantity}. Reason: {reason}")

        print(f"Stock updated for {product_id}: {new_quantity}")

    def generate_report(self, username: str, password: str, report_type: str):
        if not self._authenticate_user(username, password):
            raise SecurityError("Authentication failed")

        if not self._has_permission(username, "GENERATE_REPORTS"):
            raise SecurityError("Insufficient permissions")

        filename = f"{report_type}_report_{datetime.now().isoformat().replace(':', '-')}.txt"

        try:
            with open(filename, 'w') as writer:
                if report_type == "inventory":
                    self._generate_inventory_report(writer)
                elif report_type == "alerts":
                    self._generate_alerts_report(writer)
                elif report_type == "suppliers":
                    self._generate_suppliers_report(writer)
                else:
                    raise ValueError(f"Unknown report type: {report_type}")

            self._log_activity(username, "REPORT_GENERATED", report_type)
            print(f"Report generated: {filename}")

        except IOError as e:
            raise RuntimeError(f"Failed to generate report: {e}")

    def _initialize_users(self):
        self.users = {
            "admin": User("admin", self._hash_password("admin123"), 
                         ["ADD_PRODUCT", "UPDATE_STOCK", "GENERATE_REPORTS", "MANAGE_SUPPLIERS"]),
            "manager": User("manager", self._hash_password("mgr123"), 
                           ["UPDATE_STOCK", "GENERATE_REPORTS"]),
            "clerk": User("clerk", self._hash_password("clerk123"), 
                         ["UPDATE_STOCK"])
        }

        self.suppliers = {
            "SUP001": Supplier("SUP001", "TechCorp", "tech@corp.com", "123-456-7890"),
            "SUP002": Supplier("SUP002", "OfficeSupply", "orders@office.com", "987-654-3210")
        }

    def _setup_email_configuration(self):
        self.email_config = {
            'smtp_host': 'smtp.company.com',
            'smtp_port': 587,
            'use_tls': True,
            'username': 'inventory@company.com'
        }

    def _authenticate_user(self, username: str, password: str) -> bool:
        user = self.users.get(username)
        return user is not None and user.password_hash == self._hash_password(password)

    def _has_permission(self, username: str, permission: str) -> bool:
        user = self.users.get(username)
        return user is not None and permission in user.permissions

    def _hash_password(self, password: str) -> str:
        try:
            hash_bytes = hashlib.sha256(password.encode()).digest()
            return base64.b64encode(hash_bytes).decode()
        except Exception as e:
            raise RuntimeError(f"Password hashing failed: {e}")

    def _validate_product_data(self, product_id: str, name: str, quantity: int, 
                              price: float, supplier_id: str, min_threshold: int) -> bool:
        return (product_id and product_id.strip() and 
                name and name.strip() and 
                quantity >= 0 and price >= 0 and min_threshold >= 0 and 
                supplier_id and supplier_id.strip())

    def _sanitize_name(self, name: str) -> str:
        import re
        return re.sub(r'[<>"\'&]', '', name).strip()

    def _create_low_stock_alert(self, product: Product):
        alert = StockAlert(
            product_id=product.id,
            product_name=product.name,
            current_stock=product.quantity,
            min_threshold=product.min_threshold
        )
        self.alerts.append(alert)

        message = (f"LOW STOCK ALERT: {product.name} ({product.id}) "
                  f"has only {product.quantity} units remaining. "
                  f"Minimum threshold: {product.min_threshold}")

        self._send_manager_alert(message)

        supplier = self.suppliers.get(product.supplier_id)
        if supplier:
            self._send_supplier_notification(supplier, f"Reorder needed for: {product.name}")

    def _remove_low_stock_alert(self, product_id: str):
        self.alerts = [alert for alert in self.alerts if alert.product_id != product_id]

    def _send_manager_alert(self, message: str):
        try:
            print(f"Manager alert sent: {message}")
        except Exception as e:
            print(f"Failed to send manager alert: {e}")

    def _send_supplier_notification(self, supplier: Supplier, message: str):
        try:
            print(f"Supplier notification sent to {supplier.name}: {message}")
        except Exception as e:
            print(f"Failed to send supplier notification: {e}")

    def _generate_inventory_report(self, writer):
        writer.write("INVENTORY REPORT\n")
        writer.write(f"Generated: {datetime.now()}\n\n")

        for product in self.products.values():
            supplier_name = self.suppliers.get(product.supplier_id).name if product.supplier_id in self.suppliers else "Unknown"
            writer.write(f"Product: {product.name} ({product.id})\n")
            writer.write(f"Quantity: {product.quantity}\n")
            writer.write(f"Price: ${product.price}\n")
            writer.write(f"Supplier: {supplier_name}\n\n")

    def _generate_alerts_report(self, writer):
        writer.write("STOCK ALERTS REPORT\n")
        writer.write(f"Generated: {datetime.now()}\n\n")

        for alert in self.alerts:
            writer.write(f"Alert: {alert.product_name} ({alert.product_id})\n")
            writer.write(f"Current Stock: {alert.current_stock}\n")
            writer.write(f"Minimum Threshold: {alert.min_threshold}\n\n")

    def _generate_suppliers_report(self, writer):
        writer.write("SUPPLIERS REPORT\n")
        writer.write(f"Generated: {datetime.now()}\n\n")

        for supplier in self.suppliers.values():
            writer.write(f"Supplier: {supplier.name} ({supplier.id})\n")
            writer.write(f"Email: {supplier.email}\n")
            writer.write(f"Phone: {supplier.phone}\n\n")

    def _update_inventory_file(self):
        try:
            with open("inventory.dat", "w") as writer:
                for product in self.products.values():
                    writer.write(f"{product.id}|{product.name}|{product.quantity}|{product.price}|{product.supplier_id}|{product.min_threshold}\n")
        except IOError as e:
            print(f"Failed to update inventory file: {e}")

    def _load_data_from_files(self):
        try:
            if os.path.exists("inventory.dat"):
                with open("inventory.dat", "r") as reader:
                    for line in reader:
                        parts = line.strip().split("|")
                        if len(parts) == 6:
                            product = Product(
                                id=parts[0],
                                name=parts[1],
                                quantity=int(parts[2]),
                                price=float(parts[3]),
                                supplier_id=parts[4],
                                min_threshold=int(parts[5]),
                                last_updated=datetime.now()
                            )
                            self.products[product.id] = product
        except Exception as e:
            print(f"Failed to load inventory data: {e}")

    def _log_activity(self, username: str, action: str, details: str):
        try:
            with open("inventory_log.txt", "a") as writer:
                writer.write(f"{datetime.now()} - User: {username}, Action: {action}, Details: {details}\n")
        except IOError as e:
            print(f"Failed to log activity: {e}")

