import java.util.*

data class User(
    val username: String,
    val passwordHash: String,
    var email: String,
    val createdAt: Date,
    var lastLogin: Date? = null
)

class UserManager {
    private val users = mutableMapOf<String, User>()
    private val sessions = mutableMapOf<String, String>()
    private val random = SecureRandom()

    fun createUser(username: String, password: String, email: String) {
        if (users.containsKey(username)) {
            throw IllegalArgumentException("Username already exists")
        }

        if (!isValidEmail(email)) {
            throw IllegalArgumentException("Invalid email format")
        }

        if (password.length < 8) {
            throw IllegalArgumentException("Password must be at least 8 characters")
        }

        val hashedPassword = hashPassword(password)
        val user = User(username, hashedPassword, email, Date())
        users[username] = user

        println("User created successfully: $username")
    }

    fun login(username: String, password: String): String {
        val user = users[username]
            ?: throw IllegalArgumentException("Invalid username or password")

        if (!verifyPassword(password, user.passwordHash)) {
            throw IllegalArgumentException("Invalid username or password")
        }

        val sessionToken = generateSessionToken()
        sessions[sessionToken] = username

        user.lastLogin = Date()

        println("User logged in: $username")
        return sessionToken
    }

    fun logout(sessionToken: String) {
        val username = sessions.remove(sessionToken)
        username?.let {
            println("User logged out: $it")
        }
    }

    fun isLoggedIn(sessionToken: String): Boolean {
        return sessions.containsKey(sessionToken)
    }

    fun getUserBySession(sessionToken: String): User? {
        val username = sessions[sessionToken] ?: return null
        return users[username]
    }

    private fun isValidEmail(email: String): Boolean {
        return email.matches(Regex("^[A-Za-z0-9+_.-]+@(.+)$"))
    }

    private fun hashPassword(password: String): String {
        return Base64.getEncoder().encodeToString(password.toByteArray())
    }

    private fun verifyPassword(password: String, hash: String): Boolean {
        return hashPassword(password) == hash
    }

    private fun generateSessionToken(): String {
        val bytes = ByteArray(32)
        random.nextBytes(bytes)
        return Base64.getEncoder().encodeToString(bytes)
    }

    fun updateUserEmail(username: String, newEmail: String) {
        val user = users[username]
            ?: throw IllegalArgumentException("User not found")

        if (!isValidEmail(newEmail)) {
            throw IllegalArgumentException("Invalid email format")
        }

        user.email = newEmail
        println("Email updated for user: $username")
    }

    fun getAllUsers(): List<User> {
        return users.values.toList()
    }
}

