import java.io.*
import java.util.*

class FileProcessor(
    private val inputDirectory: String,
    private val outputDirectory: String,
    private val archiveDirectory: String
) {

    fun processAndArchiveFiles() {
        val inputDir = File(inputDirectory)
        val files = inputDir.listFiles { _, name -> name.endsWith(".csv") }

        if (files == null || files.isEmpty()) {
            println("No CSV files found to process")
            return
        }

        for (file in files) {
            try {
                val lines = readFile(file)
                val processedLines = mutableListOf<String>()

                processedLines.add("PROCESSED_HEADER,ORIGINAL_DATA,TIMESTAMP")

                for (i in 1 until lines.size) {
                    val line = lines[i]
                    val parts = line.split(",")

                    val processedLine = StringBuilder()
                    processedLine.append("PROCESSED")

                    for (part in parts) {
                        processedLine.append(",").append(part.trim().uppercase())
                    }

                    processedLine.append(",").append(Date().toString())
                    processedLines.add(processedLine.toString())
                }

                val outputFileName = "processed_${file.name}"
                val outputFile = File(outputDirectory, outputFileName)
                writeFile(outputFile, processedLines)

                println("Processed file: ${file.name} -> $outputFileName")

                val archiveName = "archive_${System.currentTimeMillis()}_${file.name}"
                val archiveFile = File(archiveDirectory, archiveName)

                if (file.renameTo(archiveFile)) {
                    println("Archived original file: ${file.name} -> $archiveName")
                } else {
                    System.err.println("Failed to archive file: ${file.name}")
                }

            } catch (e: IOException) {
                System.err.println("Error processing file ${file.name}: ${e.message}")
            }
        }
    }

    private fun readFile(file: File): List<String> {
        val lines = mutableListOf<String>()
        BufferedReader(FileReader(file)).use { reader ->
            var line: String?
            while (reader.readLine().also { line = it } != null) {
                lines.add(line!!)
            }
        }
        return lines
    }

    private fun writeFile(file: File, lines: List<String>) {
        BufferedWriter(FileWriter(file)).use { writer ->
            for (line in lines) {
                writer.write(line)
                writer.newLine()
            }
        }
    }

    fun getProcessingHistory(): List<String> {
        val history = mutableListOf<String>()
        val archiveDir = File(archiveDirectory)
        val archivedFiles = archiveDir.listFiles()

        archivedFiles?.let { files ->
            for (file in files) {
                history.add("Archived: ${file.name} at ${Date(file.lastModified())}")
            }
        }

        return history
    }
}

