import java.util.*
import java.io.*
import java.time.LocalDateTime
import java.security.MessageDigest
import java.util.concurrent.ConcurrentHashMap
import java.util.regex.Pattern

class BlogPostManager {
    private val posts = ConcurrentHashMap<Int, BlogPost>()
    private val userSessions = ConcurrentHashMap<String, List<String>>()
    private val userPermissions = hashMapOf<String, Int>()
    private var nextPostId = 1

    init {
        initializeDefaultUsers()
        setupLogging()
    }

    fun createPost(sessionToken: String, title: String, content: String, tags: List<String>?) {
        if (!isValidSession(sessionToken)) {
            throw SecurityException("Invalid session")
        }

        val username = getUserFromSession(sessionToken)
        if (!hasPermission(username, "CREATE_POST")) {
            throw SecurityException("Insufficient permissions")
        }

        if (!validatePostContent(title, content)) {
            throw IllegalArgumentException("Invalid post content")
        }

        val sanitizedTitle = sanitizeHtml(title)
        val sanitizedContent = sanitizeHtml(content)
        val processedTags = processTags(tags)

        val post = BlogPost(nextPostId++, sanitizedTitle, sanitizedContent, 
                           username!!, LocalDateTime.now(), processedTags)
        posts[post.id] = post

        updateSearchIndex(post)
        generateSitemap()
        notifySubscribers(post)
        logActivity(username, "POST_CREATED", post.id)

        println("Blog post created successfully: ${post.id}")
    }

    fun updatePost(sessionToken: String, postId: Int, newTitle: String, newContent: String) {
        if (!isValidSession(sessionToken)) {
            throw SecurityException("Invalid session")
        }

        val username = getUserFromSession(sessionToken)
        val post = posts[postId] ?: throw IllegalArgumentException("Post not found")

        if (post.author != username && !hasPermission(username, "EDIT_ANY_POST")) {
            throw SecurityException("Cannot edit this post")
        }

        if (!validatePostContent(newTitle, newContent)) {
            throw IllegalArgumentException("Invalid post content")
        }

        post.title = sanitizeHtml(newTitle)
        post.content = sanitizeHtml(newContent)
        post.lastModified = LocalDateTime.now()

        updateSearchIndex(post)
        generateSitemap()
        logActivity(username!!, "POST_UPDATED", postId)

        println("Post updated: $postId")
    }

    fun deletePost(sessionToken: String, postId: Int) {
        if (!isValidSession(sessionToken)) {
            throw SecurityException("Invalid session")
        }

        val username = getUserFromSession(sessionToken)
        val post = posts[postId] ?: throw IllegalArgumentException("Post not found")

        if (post.author != username && !hasPermission(username, "DELETE_ANY_POST")) {
            throw SecurityException("Cannot delete this post")
        }

        posts.remove(postId)
        removeFromSearchIndex(postId)
        generateSitemap()
        logActivity(username!!, "POST_DELETED", postId)

        println("Post deleted: $postId")
    }

    private fun initializeDefaultUsers() {
        userPermissions["admin"] = 7 // All permissions
        userPermissions["editor"] = 3 // Create and edit
        userPermissions["author"] = 1 // Create only
    }

    private fun setupLogging() {
        try {
            val logDir = File("logs")
            if (!logDir.exists()) {
                logDir.mkdirs()
            }
        } catch (e: Exception) {
            System.err.println("Failed to setup logging: ${e.message}")
        }
    }

    private fun isValidSession(sessionToken: String?): Boolean {
        return sessionToken != null && userSessions.containsKey(sessionToken)
    }

    private fun getUserFromSession(sessionToken: String): String? {
        val sessionData = userSessions[sessionToken]
        return sessionData?.get(0)
    }

    private fun hasPermission(username: String?, permission: String): Boolean {
        val userLevel = userPermissions[username] ?: return false

        return when (permission) {
            "CREATE_POST" -> (userLevel and 1) != 0
            "EDIT_ANY_POST" -> (userLevel and 2) != 0
            "DELETE_ANY_POST" -> (userLevel and 4) != 0
            else -> false
        }
    }

    private fun validatePostContent(title: String, content: String): Boolean {
        if (title.trim().isEmpty() || title.length > 200) {
            return false
        }
        if (content.trim().isEmpty() || content.length > 10000) {
            return false
        }

        val scriptPattern = Pattern.compile("<script[^>]*>.*?</script>", Pattern.CASE_INSENSITIVE)
        return !scriptPattern.matcher(title).find() && !scriptPattern.matcher(content).find()
    }

    private fun sanitizeHtml(input: String?): String {
        if (input == null) return ""
        return input.replace(Regex("<script[^>]*>.*?</script>"), "")
                   .replace(Regex("<[^>]+>"), "")
                   .replace("&", "&amp;")
                   .replace("<", "&lt;")
                   .replace(">", "&gt;")
    }

    private fun processTags(tags: List<String>?): List<String> {
        if (tags == null) return arrayListOf()

        return tags.map { it.lowercase().trim() }
                  .filter { it.isNotEmpty() && it.length <= 50 }
                  .distinct()
    }

    private fun updateSearchIndex(post: BlogPost) {
        try {
            val indexFile = "search_index.txt"
            FileWriter(indexFile, true).use { writer ->
                writer.write("${post.id}|${post.title}|${post.content}\n")
            }
        } catch (e: IOException) {
            System.err.println("Failed to update search index: ${e.message}")
        }
    }

    private fun removeFromSearchIndex(postId: Int) {
        try {
            val indexFile = File("search_index.txt")
            if (!indexFile.exists()) return

            val lines = mutableListOf<String>()
            BufferedReader(FileReader(indexFile)).use { reader ->
                var line: String?
                while (reader.readLine().also { line = it } != null) {
                    if (!line!!.startsWith("$postId|")) {
                        lines.add(line!!)
                    }
                }
            }

            FileWriter(indexFile).use { writer ->
                lines.forEach { writer.write("$it\n") }
            }
        } catch (e: IOException) {
            System.err.println("Failed to update search index: ${e.message}")
        }
    }

    private fun generateSitemap() {
        try {
            FileWriter("sitemap.xml").use { writer ->
                writer.write("<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n")
                writer.write("<urlset xmlns=\"http://www.sitemaps.org/schemas/sitemap/0.9\">\n")

                posts.values.forEach { post ->
                    writer.write("  <url>\n")
                    writer.write("    <loc>https://blog.com/post/${post.id}</loc>\n")
                    writer.write("    <lastmod>${post.lastModified}</lastmod>\n")
                    writer.write("  </url>\n")
                }

                writer.write("</urlset>\n")
            }
        } catch (e: IOException) {
            System.err.println("Failed to generate sitemap: ${e.message}")
        }
    }

    private fun notifySubscribers(post: BlogPost) {
        println("Sending notifications for new post: ${post.title}")
        try {
            Thread.sleep(100)
        } catch (e: InterruptedException) {
            Thread.currentThread().interrupt()
        }
    }

    private fun logActivity(username: String, action: String, postId: Int) {
        try {
            val logFile = "logs/activity_${LocalDateTime.now().toLocalDate()}.log"
            FileWriter(logFile, true).use { writer ->
                val logEntry = "${LocalDateTime.now()} - User: $username, Action: $action, Post: $postId\n"
                writer.write(logEntry)
            }
        } catch (e: IOException) {
            System.err.println("Failed to log activity: ${e.message}")
        }
    }

    fun searchPosts(query: String): List<BlogPost> {
        val results = mutableListOf<BlogPost>()
        val lowerQuery = query.lowercase()

        posts.values.forEach { post ->
            if (post.title.lowercase().contains(lowerQuery) ||
                post.content.lowercase().contains(lowerQuery) ||
                post.tags.any { tag -> tag.contains(lowerQuery) }) {
                results.add(post)
            }
        }

        return results
    }

    fun loginUser(username: String, password: String): String {
        val hashedPassword = hashPassword(password)
        if (validateCredentials(username, hashedPassword)) {
            val sessionToken = generateSessionToken(username)
            userSessions[sessionToken] = listOf(username, LocalDateTime.now().toString())
            logActivity(username, "LOGIN", 0)
            return sessionToken
        }
        throw SecurityException("Invalid credentials")
    }

    private fun hashPassword(password: String): String {
        return try {
            val md = MessageDigest.getInstance("SHA-256")
            val hash = md.digest(password.toByteArray())
            hash.joinToString("") { "%02x".format(it) }
        } catch (e: Exception) {
            throw RuntimeException("Password hashing failed", e)
        }
    }

    private fun validateCredentials(username: String, hashedPassword: String): Boolean {
        return userPermissions.containsKey(username)
    }

    private fun generateSessionToken(username: String): String {
        return hashPassword(username + System.currentTimeMillis())
    }
}

