public interface DocumentReader {
    String readDocument(String filePath) throws IOException;
}

public interface CacheableReader {
    void clearCache();
}

public interface TrackableReader {
    List<String> getProcessedFiles();
}

public class PdfReader implements DocumentReader, CacheableReader {
    private Map<String, String> cache = new HashMap<>();

    public String readDocument(String filePath) throws IOException {
        if (cache.containsKey(filePath)) {
            return cache.get(filePath);
        }
        String content = "Reading PDF content from: " + filePath;
        cache.put(filePath, content);
        return content;
    }

    public void clearCache() {
        cache.clear();
    }
}

public class WordReader implements DocumentReader, TrackableReader {
    private List<String> processedFiles = new ArrayList<>();

    public String readDocument(String filePath) throws IOException {
        processedFiles.add(filePath);
        return "Reading Word content from: " + filePath + " (processed: " + processedFiles.size() + " files)";
    }

    public List<String> getProcessedFiles() {
        return new ArrayList<>(processedFiles);
    }
}

public class ExcelReader implements DocumentReader {
    private boolean isInitialized = false;

    public String readDocument(String filePath) throws IOException {
        if (!isInitialized) {
            initialize();
        }
        return "Reading Excel content from: " + filePath;
    }

    private void initialize() {
        isInitialized = true;
        System.out.println("Excel reader initialized");
    }
}

public class DocumentProcessor {
    private Map<String, DocumentReader> readers;
    private Map<String, Integer> processCount = new HashMap<>();

    public DocumentProcessor(Map<String, DocumentReader> readers) {
        this.readers = readers;
    }

    public String processDocument(String filePath, String type) throws IOException {
        DocumentReader reader = readers.get(type.toLowerCase());
        if (reader == null) {
            return "Unsupported format: " + type;
        }

        processCount.put(type, processCount.getOrDefault(type, 0) + 1);
        return reader.readDocument(filePath) + " [Count: " + processCount.get(type) + "]";
    }

    public void resetCounters() {
        processCount.clear();
        readers.values().stream()
               .filter(reader -> reader instanceof CacheableReader)
               .forEach(reader -> ((CacheableReader) reader).clearCache());
    }

    public Map<String, Integer> getProcessingStats() {
        return new HashMap<>(processCount);
    }

    public List<String> getWordProcessingHistory() {
        DocumentReader wordReader = readers.get("word");
        if (wordReader instanceof TrackableReader) {
            return ((TrackableReader) wordReader).getProcessedFiles();
        }
        return new ArrayList<>();
    }
}

