import threading
from collections import defaultdict

class MySQLConnection:
    def __init__(self):
        self.connection = None
        self.url = "mysql://localhost:3306/app"
        self.username = "root"
        self.password = "password"

    def connect(self):
        self.connection = f"Connected to {self.url}"
