using System;
using System.Collections.Generic;

enum CharacterType
{
    WARRIOR,
    MAGE,
    ARCHER
}

class GameCharacter
{
    private readonly string name;
    private readonly CharacterType type;
    private readonly int baseAttack;

    public GameCharacter(string name, CharacterType type, int baseAttack)
    {
        this.name = name;
        this.type = type;
        this.baseAttack = baseAttack;
    }

    public string GetName() { return name; }
    public CharacterType GetCharacterType() { return type; }
    public int GetBaseAttack() { return baseAttack; }
}

class DamageCalculator
{
    private static readonly Random random = new Random();

    public int CalculateDamage(GameCharacter character)
    {
        switch (character.GetCharacterType())
        {
            case CharacterType.WARRIOR:
                return (int)(character.GetBaseAttack() * 1.5);
            case CharacterType.MAGE:
                return character.GetBaseAttack() + 20;
            case CharacterType.ARCHER:
                bool crit = random.Next(2) == 0;
                return crit ? character.GetBaseAttack() * 2 : character.GetBaseAttack();
            default:
                throw new ArgumentException("Unknown character type: " + character.GetCharacterType());
        }
    }
}

