using System;
using System.Collections.Generic;

enum DeviceType { LIGHT, THERMOSTAT, DOOR_LOCK }

class SmartDevice
{
    private readonly string id;
    private readonly DeviceType type;

    public SmartDevice(string id, DeviceType type)
    {
        this.id = id;
        this.type = type;
    }

    public string GetId() { return id; }
    public DeviceType GetDeviceType() { return type; }
}

class SmartHomeController
{
    public void TurnOn(SmartDevice d)
    {
        switch (d.GetDeviceType())
        {
            case DeviceType.LIGHT:
                Console.WriteLine("Light " + d.GetId() + " is ON");
                break;
            case DeviceType.THERMOSTAT:
                Console.WriteLine("Thermostat " + d.GetId() + " heating to 22°C");
                break;
            case DeviceType.DOOR_LOCK:
                Console.WriteLine("Door lock " + d.GetId() + " cannot be turned ON");
                break;
            default:
                throw new ArgumentException("Unknown device");
        }
    }

    public double EnergyUsageKwh(SmartDevice d, int minutes)
    {
        switch (d.GetDeviceType())
        {
            case DeviceType.LIGHT: return 0.012 * minutes;      // ~12W bulb
            case DeviceType.THERMOSTAT: return 0.08 * minutes;  // HVAC running
            case DeviceType.DOOR_LOCK: return 0.0002 * minutes; // idle electronics
            default: throw new ArgumentException("Unknown device");
        }
    }
}

