using System;
using System.Collections.Generic;
using System.Linq;

abstract class Entity
{
    private static long NEXT_ID = 1;
    private readonly long id;

    protected Entity()
    {
        this.id = NEXT_ID++;
    }

    public long GetId()
    {
        return id;
    }
}

abstract class GraphicObject : Entity
{
    private readonly string color;
    private readonly int layer;

    protected GraphicObject(string color, int layer)
    {
        this.color = color;
        this.layer = layer;
    }

    public string GetColor() { return color; }
    public int GetLayer() { return layer; }
}

enum ShapeKind { RECTANGLE, CIRCLE, TRIANGLE, CUBE, SPHERE }

abstract class Shape : GraphicObject
{
    private readonly ShapeKind kind;

    protected Shape(ShapeKind kind, string color, int layer) : base(color, layer)
    {
        this.kind = kind;
    }

    public ShapeKind GetKind() { return kind; }
}

abstract class TwoDShape : Shape
{
    protected TwoDShape(ShapeKind kind, string color, int layer) : base(kind, color, layer) { }
}

abstract class ThreeDShape : Shape
{
    protected ThreeDShape(ShapeKind kind, string color, int layer) : base(kind, color, layer) { }
}

class Rectangle : TwoDShape
{
    private readonly double width, height;

    public Rectangle(double width, double height, string color, int layer) : base(ShapeKind.RECTANGLE, color, layer)
    {
        this.width = width; this.height = height;
    }

    public double GetWidth() { return width; }
    public double GetHeight() { return height; }
}

class Circle : TwoDShape
{
    private readonly double radius;

    public Circle(double radius, string color, int layer) : base(ShapeKind.CIRCLE, color, layer)
    {
        this.radius = radius;
    }

    public double GetRadius() { return radius; }
}

class Triangle : TwoDShape
{
    private readonly double a, b, c;

    public Triangle(double a, double b, double c, string color, int layer) : base(ShapeKind.TRIANGLE, color, layer)
    {
        this.a = a; this.b = b; this.c = c;
    }

    public double GetA() { return a; }
    public double GetB() { return b; }
    public double GetC() { return c; }
}

class Cube : ThreeDShape
{
    private readonly double edge;

    public Cube(double edge, string color, int layer) : base(ShapeKind.CUBE, color, layer)
    {
        this.edge = edge;
    }

    public double GetEdge() { return edge; }
}

class Sphere : ThreeDShape
{
    private readonly double radius;

    public Sphere(double radius, string color, int layer) : base(ShapeKind.SPHERE, color, layer)
    {
        this.radius = radius;
    }

    public double GetRadius() { return radius; }
}

enum Metric { AREA, PERIMETER, VOLUME, DIAGONAL, BOUNDING_BOX_WIDTH }

class BoundingBox
{
    public readonly double width;
    public readonly double height;
    public BoundingBox(double width, double height) { this.width = width; this.height = height; }
    public override string ToString() { return "BBox(" + width + "x" + height + ")"; }
}

class MetricCalculator
{
    public double Compute(Shape shape, Metric metric)
    {
        switch (shape.GetKind())
        {
            case ShapeKind.RECTANGLE:
                {
                    var r = (Rectangle)shape;
                    switch (metric)
                    {
                        case Metric.AREA: return r.GetWidth() * r.GetHeight();
                        case Metric.PERIMETER: return 2 * (r.GetWidth() + r.GetHeight());
                        case Metric.DIAGONAL: return Math.Sqrt(r.GetWidth() * r.GetWidth() + r.GetHeight() * r.GetHeight());
                        case Metric.BOUNDING_BOX_WIDTH: return r.GetWidth();
                        case Metric.VOLUME: return 0;
                    }
                    break;
                }
            case ShapeKind.CIRCLE:
                {
                    var c = (Circle)shape;
                    switch (metric)
                    {
                        case Metric.AREA: return Math.PI * c.GetRadius() * c.GetRadius();
                        case Metric.PERIMETER: return 2 * Math.PI * c.GetRadius();
                        case Metric.DIAGONAL: return 2 * c.GetRadius();
                        case Metric.BOUNDING_BOX_WIDTH: return 2 * c.GetRadius();
                        case Metric.VOLUME: return 0;
                    }
                    break;
                }
            case ShapeKind.TRIANGLE:
                {
                    var t = (Triangle)shape;
                    double s = (t.GetA() + t.GetB() + t.GetC()) / 2.0;
                    switch (metric)
                    {
                        case Metric.AREA: return Math.Sqrt(Math.Max(0, s * (s - t.GetA()) * (s - t.GetB()) * (s - t.GetC())));
                        case Metric.PERIMETER: return (t.GetA() + t.GetB() + t.GetC());
                        case Metric.DIAGONAL: return Math.Max(t.GetA(), Math.Max(t.GetB(), t.GetC()));
                        case Metric.BOUNDING_BOX_WIDTH: return Math.Max(t.GetA(), Math.Max(t.GetB(), t.GetC()));
                        case Metric.VOLUME: return 0;
                    }
                    break;
                }
            case ShapeKind.CUBE:
                {
                    var cube = (Cube)shape;
                    switch (metric)
                    {
                        case Metric.AREA: return 6 * cube.GetEdge() * cube.GetEdge();
                        case Metric.PERIMETER: return 12 * cube.GetEdge();
                        case Metric.VOLUME: return Math.Pow(cube.GetEdge(), 3);
                        case Metric.DIAGONAL: return Math.Sqrt(3) * cube.GetEdge();
                        case Metric.BOUNDING_BOX_WIDTH: return cube.GetEdge();
                    }
                    break;
                }
            case ShapeKind.SPHERE:
                {
                    var s = (Sphere)shape;
                    switch (metric)
                    {
                        case Metric.AREA: return 4 * Math.PI * s.GetRadius() * s.GetRadius();
                        case Metric.PERIMETER: return 0;
                        case Metric.VOLUME: return (4.0 / 3.0) * Math.PI * Math.Pow(s.GetRadius(), 3);
                        case Metric.DIAGONAL: return 2 * s.GetRadius();
                        case Metric.BOUNDING_BOX_WIDTH: return 2 * s.GetRadius();
                    }
                    break;
                }
        }
        return 0;
    }
}

class ShapeValidator
{
    public bool IsValid(Shape shape)
    {
        switch (shape.GetKind())
        {
            case ShapeKind.RECTANGLE:
                var r = (Rectangle)shape;
                return r.GetWidth() > 0 && r.GetHeight() > 0;
            case ShapeKind.CIRCLE:
                var c = (Circle)shape;
                return c.GetRadius() > 0;
            case ShapeKind.TRIANGLE:
                var t = (Triangle)shape;
                return t.GetA() > 0 && t.GetB() > 0 && t.GetC() > 0
                    && t.GetA() + t.GetB() > t.GetC()
                    && t.GetA() + t.GetC() > t.GetB()
                    && t.GetB() + t.GetC() > t.GetA();
            case ShapeKind.CUBE:
                var cube = (Cube)shape;
                return cube.GetEdge() > 0;
            case ShapeKind.SPHERE:
                var s = (Sphere)shape;
                return s.GetRadius() > 0;
        }
        return false;
    }
}

class ShapeRenderer
{
    public string RenderPseudoSvg(Shape shape)
    {
        switch (shape.GetKind())
        {
            case ShapeKind.RECTANGLE:
                var r = (Rectangle)shape;
                return $"<rect w=\"{r.GetWidth()}\" h=\"{r.GetHeight()}\" fill=\"{shape.GetColor()}\" layer=\"{shape.GetLayer()}\"/>";
            case ShapeKind.CIRCLE:
                var c = (Circle)shape;
                return $"<circle r=\"{c.GetRadius()}\" fill=\"{shape.GetColor()}\" layer=\"{shape.GetLayer()}\"/>";
            case ShapeKind.TRIANGLE:
                var t = (Triangle)shape;
                return $"<polygon a=\"{t.GetA()}\" b=\"{t.GetB()}\" c=\"{t.GetC()}\" fill=\"{shape.GetColor()}\" layer=\"{shape.GetLayer()}\"/>";
            case ShapeKind.CUBE:
                var cube = (Cube)shape;
                return $"<cube edge=\"{cube.GetEdge()}\" materialColor=\"{shape.GetColor()}\" layer=\"{shape.GetLayer()}\"/>";
            case ShapeKind.SPHERE:
                var s = (Sphere)shape;
                return $"<sphere r=\"{s.GetRadius()}\" materialColor=\"{shape.GetColor()}\" layer=\"{shape.GetLayer()}\"/>";
        }
        return "<unknown/>";
    }
}

class ShapeSerializer
{
    public string ToCsvRow(Shape shape)
    {
        switch (shape.GetKind())
        {
            case ShapeKind.RECTANGLE:
                var r = (Rectangle)shape;
                return $"rectangle,{r.GetWidth()},{r.GetHeight()},{shape.GetColor()},{shape.GetLayer()}";
            case ShapeKind.CIRCLE:
                var c = (Circle)shape;
                return $"circle,{c.GetRadius()},{shape.GetColor()},{shape.GetLayer()}";
            case ShapeKind.TRIANGLE:
                var t = (Triangle)shape;
                return $"triangle,{t.GetA()},{t.GetB()},{t.GetC()},{shape.GetColor()},{shape.GetLayer()}";
            case ShapeKind.CUBE:
                var cube = (Cube)shape;
                return $"cube,{cube.GetEdge()},{shape.GetColor()},{shape.GetLayer()}";
            case ShapeKind.SPHERE:
                var s = (Sphere)shape;
                return $"sphere,{s.GetRadius()},{shape.GetColor()},{shape.GetLayer()}";
        }
        return "unknown";
    }
}

class ShapeFactory
{
    public Shape Create(string spec)
    {
        var parts = spec.Split(';');
        string type = parts[0].Trim().ToLower();
        var map = new Dictionary<string, string>();
        for (int i = 1; i < parts.Length; i++)
        {
            var kv = parts[i].Split('=');
            if (kv.Length == 2) map[kv[0].Trim().ToLower()] = kv[1].Trim();
        }
        string color = map.ContainsKey("color") ? map["color"] : "black";
        int layer = int.Parse(map.ContainsKey("layer") ? map["layer"] : "0");

        switch (type)
        {
            case "rectangle":
                return new Rectangle(
                    double.Parse(map.ContainsKey("width") ? map["width"] : "0"),
                    double.Parse(map.ContainsKey("height") ? map["height"] : "0"),
                    color, layer);
            case "circle":
                return new Circle(
                    double.Parse(map.ContainsKey("radius") ? map["radius"] : "0"),
                    color, layer);
            case "triangle":
                return new Triangle(
                    double.Parse(map.ContainsKey("a") ? map["a"] : "0"),
                    double.Parse(map.ContainsKey("b") ? map["b"] : "0"),
                    double.Parse(map.ContainsKey("c") ? map["c"] : "0"),
                    color, layer);
            case "cube":
                return new Cube(
                    double.Parse(map.ContainsKey("edge") ? map["edge"] : "0"),
                    color, layer);
            case "sphere":
                return new Sphere(
                    double.Parse(map.ContainsKey("radius") ? map["radius"] : "0"),
                    color, layer);
        }
        throw new ArgumentException("Unknown shape type: " + type);
    }
}

class ShapePricing
{
    private readonly MetricCalculator calc = new MetricCalculator();

    public double Price(Shape shape)
    {
        switch (shape.GetKind())
        {
            case ShapeKind.RECTANGLE:
                double area = calc.Compute(shape, Metric.AREA);
                return 0.5 * area + shape.GetLayer() * 2;
            case ShapeKind.CIRCLE:
                double a = calc.Compute(shape, Metric.AREA);
                double diag = calc.Compute(shape, Metric.DIAGONAL);
                return 0.6 * a + 0.1 * diag;
            case ShapeKind.TRIANGLE:
                double per = calc.Compute(shape, Metric.PERIMETER);
                return per * 0.8;
            case ShapeKind.CUBE:
                double vol = calc.Compute(shape, Metric.VOLUME);
                return 1.2 * vol;
            case ShapeKind.SPHERE:
                double v = calc.Compute(shape, Metric.VOLUME);
                return 1.0 * v + 10;
        }
        return 0;
    }
}

class LegendBuilder
{
    public string LegendSymbol(Shape shape)
    {
        switch (shape.GetKind())
        {
            case ShapeKind.RECTANGLE: return "▭";
            case ShapeKind.CIRCLE: return "◯";
            case ShapeKind.TRIANGLE: return "△";
            case ShapeKind.CUBE: return "⬛";
            case ShapeKind.SPHERE: return "◯";
        }
        return "?";
    }
}

class CollisionEngine
{
    public BoundingBox BoundingBox(Shape shape)
    {
        switch (shape.GetKind())
        {
            case ShapeKind.RECTANGLE:
                var r = (Rectangle)shape;
                return new BoundingBox(r.GetWidth(), r.GetHeight());
            case ShapeKind.CIRCLE:
                var c = (Circle)shape;
                double d = 2 * c.GetRadius();
                return new BoundingBox(d, d);
            case ShapeKind.TRIANGLE:
                var t = (Triangle)shape;
                double max = Math.Max(t.GetA(), Math.Max(t.GetB(), t.GetC()));
                double avg = (t.GetA() + t.GetB() + t.GetC() - max) / 2.0;
                return new BoundingBox(max, avg);
            case ShapeKind.CUBE:
                var cube = (Cube)shape;
                return new BoundingBox(cube.GetEdge(), cube.GetEdge());
            case ShapeKind.SPHERE:
                var s = (Sphere)shape;
                double ds = 2 * s.GetRadius();
                return new BoundingBox(ds, ds);
        }
        return new BoundingBox(0, 0);
    }

    public bool Overlaps(Shape a, Shape b)
    {
        var A = BoundingBox(a);
        var B = BoundingBox(b);
        return (A.width * A.height) > 0 && (B.width * B.height) > 0;
    }
}

class ShapeReport
{
    private readonly MetricCalculator calc = new MetricCalculator();
    private readonly ShapeSerializer ser = new ShapeSerializer();
    private readonly ShapeRenderer rnd = new ShapeRenderer();
    private readonly CollisionEngine col = new CollisionEngine();
    private readonly ShapePricing price = new ShapePricing();
    private readonly LegendBuilder legend = new LegendBuilder();

    public string Summarize(List<Shape> shapes)
    {
        var sb = new System.Text.StringBuilder();
        sb.Append("=== Shape Report (OCP-violating) ===\n");
        foreach (Shape s in shapes)
        {
            sb.Append("ID ").Append(s.GetId())
              .Append(" ").Append(s.GetKind())
              .Append(" color=").Append(s.GetColor())
              .Append(" layer=").Append(s.GetLayer()).Append("\n");

            sb.Append("  CSV: ").Append(ser.ToCsvRow(s)).Append("\n");
            sb.Append("  SVG: ").Append(rnd.RenderPseudoSvg(s)).Append("\n");
            sb.Append("  Legend: ").Append(legend.LegendSymbol(s)).Append("\n");

            sb.Append("  Metrics: ");
            sb.Append(" area=").Append(calc.Compute(s, Metric.AREA));
            sb.Append(" perim=").Append(calc.Compute(s, Metric.PERIMETER));
            sb.Append(" diag=").Append(calc.Compute(s, Metric.DIAGONAL));
            if (s is ThreeDShape)
            {
                sb.Append(" vol=").Append(calc.Compute(s, Metric.VOLUME));
            }
            sb.Append("\n");

            sb.Append("  BBox: ").Append(col.BoundingBox(s)).Append("\n");
            sb.Append("  Price: ").Append(price.Price(s)).Append("\n\n");
        }
        return sb.ToString();
    }
}

