using System;
using System.Collections.Generic;
using System.Globalization;
using System.Text;

abstract class Entity {
    private static long NEXT_ID = 1;
    private readonly long id;
    protected Entity() { this.id = NEXT_ID++; }
    public long GetId() { return id; }
}

abstract class GraphicObject : Entity {
    private readonly string color;
    private readonly int layer;
    protected GraphicObject(string color, int layer) { this.color = color; this.layer = layer; }
    public string GetColor() { return color; }
    public int GetLayer() { return layer; }
}

enum ShapeKind { RECTANGLE, CIRCLE, TRIANGLE, CUBE, SPHERE }

sealed class BoundingBox {
    public readonly double width;
    public readonly double height;
    public BoundingBox(double width, double height) { this.width = width; this.height = height; }
    public override string ToString() { return "BBox(" + F.S(width) + "x" + F.S(height) + ")"; }
}

interface HasWidthHeight { double GetWidth(); double GetHeight(); }
interface HasRadius { double GetRadius(); }
interface HasTriangleSides { double GetA(); double GetB(); double GetC(); }
interface HasEdge { double GetEdge(); }

abstract class Shape : GraphicObject {
    private readonly ShapeKind kind;
    protected Shape(ShapeKind kind, string color, int layer) : base(color, layer) { this.kind = kind; }
    public ShapeKind GetKind() { return kind; }
    public abstract bool IsValid();
    public abstract string RenderPseudoSvg();
    public abstract string ToCsvRow();
    public abstract string LegendSymbol();
    public abstract BoundingBox BoundingBox();
    public abstract double Price(MetricCalculator calc);
}

abstract class TwoDShape : Shape {
    protected TwoDShape(ShapeKind kind, string color, int layer) : base(kind, color, layer) { }
}
abstract class ThreeDShape : Shape {
    protected ThreeDShape(ShapeKind kind, string color, int layer) : base(kind, color, layer) { }
}

sealed class Rectangle : TwoDShape, HasWidthHeight {
    private readonly double width, height;
    public Rectangle(double width, double height, string color, int layer) : base(ShapeKind.RECTANGLE, color, layer) { this.width = width; this.height = height; }
    public double GetWidth() { return width; }
    public double GetHeight() { return height; }
    public override bool IsValid() { return width > 0 && height > 0; }
    public override string RenderPseudoSvg() { return "<rect w=\"" + F.S(width) + "\" h=\"" + F.S(height) + "\" fill=\"" + GetColor() + "\" layer=\"" + GetLayer() + "\"/>"; }
    public override string ToCsvRow() { return "rectangle," + F.S(width) + "," + F.S(height) + "," + GetColor() + "," + GetLayer(); }
    public override string LegendSymbol() { return "▭"; }
    public override BoundingBox BoundingBox() { return new BoundingBox(width, height); }
    public override double Price(MetricCalculator calc) {
        double area = calc.Compute(this, "area");
        return 0.5 * area + GetLayer() * 2;
    }
}

sealed class Circle : TwoDShape, HasRadius {
    private readonly double radius;
    public Circle(double radius, string color, int layer) : base(ShapeKind.CIRCLE, color, layer) { this.radius = radius; }
    public double GetRadius() { return radius; }
    public override bool IsValid() { return radius > 0; }
    public override string RenderPseudoSvg() { return "<circle r=\"" + F.S(radius) + "\" fill=\"" + GetColor() + "\" layer=\"" + GetLayer() + "\"/>"; }
    public override string ToCsvRow() { return "circle," + F.S(radius) + "," + GetColor() + "," + GetLayer(); }
    public override string LegendSymbol() { return "◯"; }
    public override BoundingBox BoundingBox() { double d = 2 * radius; return new BoundingBox(d, d); }
    public override double Price(MetricCalculator calc) {
        double area = calc.Compute(this, "area");
        double diag = calc.Compute(this, "diagonal");
        return 0.6 * area + 0.1 * diag;
    }
}

sealed class Triangle : TwoDShape, HasTriangleSides {
    private readonly double a, b, c;
    public Triangle(double a, double b, double c, string color, int layer) : base(ShapeKind.TRIANGLE, color, layer) { this.a = a; this.b = b; this.c = c; }
    public double GetA() { return a; } public double GetB() { return b; } public double GetC() { return c; }
    public override bool IsValid() { return a > 0 && b > 0 && c > 0 && a + b > c && a + c > b && b + c > a; }
    public override string RenderPseudoSvg() { return "<polygon a=\"" + F.S(a) + "\" b=\"" + F.S(b) + "\" c=\"" + F.S(c) + "\" fill=\"" + GetColor() + "\" layer=\"" + GetLayer() + "\"/>"; }
    public override string ToCsvRow() { return "triangle," + F.S(a) + "," + F.S(b) + "," + F.S(c) + "," + GetColor() + "," + GetLayer(); }
    public override string LegendSymbol() { return "△"; }
    public override BoundingBox BoundingBox() { double max = Math.Max(a, Math.Max(b, c)); double avg = (a + b + c - max) / 2.0; return new BoundingBox(max, avg); }
    public override double Price(MetricCalculator calc) {
        double per = calc.Compute(this, "perimeter");
        return per * 0.8;
    }
}

sealed class Cube : ThreeDShape, HasEdge {
    private readonly double edge;
    public Cube(double edge, string color, int layer) : base(ShapeKind.CUBE, color, layer) { this.edge = edge; }
    public double GetEdge() { return edge; }
    public override bool IsValid() { return edge > 0; }
    public override string RenderPseudoSvg() { return "<cube edge=\"" + F.S(edge) + "\" materialColor=\"" + GetColor() + "\" layer=\"" + GetLayer() + "\"/>"; }
    public override string ToCsvRow() { return "cube," + F.S(edge) + "," + GetColor() + "," + GetLayer(); }
    public override string LegendSymbol() { return "⬛"; }
    public override BoundingBox BoundingBox() { return new BoundingBox(edge, edge); }
    public override double Price(MetricCalculator calc) {
        double vol = calc.Compute(this, "volume");
        return 1.2 * vol;
    }
}

sealed class Sphere : ThreeDShape, HasRadius {
    private readonly double radius;
    public Sphere(double radius, string color, int layer) : base(ShapeKind.SPHERE, color, layer) { this.radius = radius; }
    public double GetRadius() { return radius; }
    public override bool IsValid() { return radius > 0; }
    public override string RenderPseudoSvg() { return "<sphere r=\"" + F.S(radius) + "\" materialColor=\"" + GetColor() + "\" layer=\"" + GetLayer() + "\"/>"; }
    public override string ToCsvRow() { return "sphere," + F.S(radius) + "," + GetColor() + "," + GetLayer(); }
    public override string LegendSymbol() { return "◯"; }
    public override BoundingBox BoundingBox() { double d = 2 * radius; return new BoundingBox(d, d); }
    public override double Price(MetricCalculator calc) {
        double vol = calc.Compute(this, "volume");
        return 1.0 * vol + 10;
    }
}

sealed class RegisteredMetric {
    private readonly string name;
    private readonly List<KeyValuePair<Type, Func<object, double>>> handlers = new List<KeyValuePair<Type, Func<object, double>>>();
    public RegisteredMetric(string name) { this.name = name; }
    public string Name() { return name; }
    public RegisteredMetric On<T>(Func<T, double> fn) { handlers.Add(new KeyValuePair<Type, Func<object, double>>(typeof(T), o => fn((T)o))); return this; }
    public double Apply(Shape s) {
        foreach (var e in handlers) {
            if (e.Key.IsInstanceOfType(s)) return e.Value(s);
        }
        return 0;
    }
}

sealed class MetricCalculator {
    private readonly Dictionary<string, RegisteredMetric> metrics = new Dictionary<string, RegisteredMetric>();
    public void Register(RegisteredMetric metric) { metrics[metric.Name()] = metric; }
    public double Compute(Shape shape, string metricName) {
        if (!metrics.TryGetValue(metricName, out var m)) throw new ArgumentException("Unknown metric: " + metricName);
        return m.Apply(shape);
    }
}

sealed class ShapeValidator { public bool IsValid(Shape shape) { return shape.IsValid(); } }
sealed class ShapeRenderer { public string RenderPseudoSvg(Shape shape) { return shape.RenderPseudoSvg(); } }
sealed class ShapeSerializer { public string ToCsvRow(Shape shape) { return shape.ToCsvRow(); } }

sealed class ShapeFactory {
    public interface ICreator { Shape Create(Dictionary<string, string> map, string color, int layer); }
    private readonly Dictionary<string, ICreator> creators = new Dictionary<string, ICreator>();
    public void Register(string type, ICreator c) { creators[type.ToLowerInvariant()] = c; }
    public Shape Create(string spec) {
        string[] parts = spec.Split(';');
        string type = parts[0].Trim().ToLowerInvariant();
        var map = new Dictionary<string, string>();
        for (int i = 1; i < parts.Length; i++) {
            string[] kv = parts[i].Split('=');
            if (kv.Length == 2) map[kv[0].Trim().ToLowerInvariant()] = kv[1].Trim();
        }
        string color = map.ContainsKey("color") ? map["color"] : "black";
        int layer = int.Parse(map.ContainsKey("layer") ? map["layer"] : "0", CultureInfo.InvariantCulture);
        if (!creators.TryGetValue(type, out var c)) throw new ArgumentException("Unknown shape type: " + type);
        return c.Create(map, color, layer);
    }
}

sealed class ShapePricing {
    private readonly MetricCalculator calc;
    public ShapePricing(MetricCalculator calc) { this.calc = calc; }
    public double Price(Shape shape) { return shape.Price(calc); }
}

sealed class LegendBuilder { public string LegendSymbol(Shape shape) { return shape.LegendSymbol(); } }

sealed class CollisionEngine {
    public BoundingBox BoundingBox(Shape shape) { return shape.BoundingBox(); }
    public bool Overlaps(Shape a, Shape b) {
        BoundingBox A = BoundingBox(a);
        BoundingBox B = BoundingBox(b);
        return (A.width * A.height) > 0 && (B.width * B.height) > 0;
    }
}

sealed class ShapeReport {
    private readonly MetricCalculator calc;
    private readonly ShapeSerializer ser = new ShapeSerializer();
    private readonly ShapeRenderer rnd = new ShapeRenderer();
    private readonly CollisionEngine col = new CollisionEngine();
    private readonly ShapePricing price;
    private readonly LegendBuilder legend = new LegendBuilder();
    public ShapeReport(MetricCalculator calc) { this.calc = calc; this.price = new ShapePricing(calc); }
    public string Summarize(List<Shape> shapes) {
        var sb = new StringBuilder();
        sb.Append("=== Shape Report (OCP-violating) ===\n");
        foreach (Shape s in shapes) {
            sb.Append("ID ").Append(s.GetId())
              .Append(" ").Append(s.GetKind())
              .Append(" color=").Append(s.GetColor())
              .Append(" layer=").Append(s.GetLayer()).Append("\n");

            sb.Append("  CSV: ").Append(ser.ToCsvRow(s)).Append("\n");
            sb.Append("  SVG: ").Append(rnd.RenderPseudoSvg(s)).Append("\n");
            sb.Append("  Legend: ").Append(legend.LegendSymbol(s)).Append("\n");

            sb.Append("  Metrics: ");
            sb.Append(" area=").Append(F.S(calc.Compute(s, "area")));
            sb.Append(" perim=").Append(F.S(calc.Compute(s, "perimeter")));
            sb.Append(" diag=").Append(F.S(calc.Compute(s, "diagonal")));
            if (s is ThreeDShape) {
                sb.Append(" vol=").Append(F.S(calc.Compute(s, "volume")));
            }
            sb.Append("\n");

            sb.Append("  BBox: ").Append(col.BoundingBox(s)).Append("\n");
            sb.Append("  Price: ").Append(F.S(price.Price(s))).Append("\n\n");
        }
        return sb.ToString();
    }
}

static class F {
    public static string S(double x) {
        if (double.IsNaN(x)) return "NaN";
        if (double.IsInfinity(x)) return x > 0 ? "Infinity" : "-Infinity";
        if (x == Math.Truncate(x)) return ((long)x).ToString(CultureInfo.InvariantCulture) + ".0";
        return x.ToString("0.###################", CultureInfo.InvariantCulture);
    }
}

