using System;
using System.Collections.Generic;
using System.Linq;

enum CharacterType { WARRIOR, MAGE, ARCHER, ROGUE }
enum SkillType { BASIC, SPECIAL, SUPPORT }
enum WeaponType { SWORD, STAFF, BOW, DAGGER }
enum PetType { WOLF, FAIRY, HAWK, SHADOW_CAT }

class Stats
{
    public int hp, mp, atk, mag, def, res, spd, crit;
    public Stats(int hp, int mp, int atk, int mag, int def, int res, int spd, int crit)
    {
        this.hp = hp; this.mp = mp; this.atk = atk; this.mag = mag; this.def = def; this.res = res; this.spd = spd; this.crit = crit;
    }
    public Stats Copy() { return new Stats(hp, mp, atk, mag, def, res, spd, crit); }
}

class Skill
{
    public readonly string name;
    public readonly SkillType type;
    public readonly int cost;
    public Skill(string n, SkillType t, int c) { name = n; type = t; cost = c; }
}

class Weapon
{
    public readonly string name;
    public readonly WeaponType type;
    public readonly int power;
    public Weapon(string n, WeaponType t, int p) { name = n; type = t; power = p; }
}

class Pet
{
    public readonly string name;
    public readonly PetType type;
    public readonly int bond;
    public Pet(string n, PetType t, int b) { name = n; type = t; bond = b; }
}

class Enemy
{
    public readonly string name;
    public readonly Stats stats;
    public Enemy(string n, Stats s) { name = n; stats = s; }
}

class GameCharacter
{
    public readonly string name;
    public readonly CharacterType type;
    public readonly int level;
    public readonly Stats stats;
    public readonly List<Skill> skills;
    public readonly Weapon weapon;
    public readonly Pet pet;
    public GameCharacter(string n, CharacterType t, int lvl, Stats s, List<Skill> k, Weapon w, Pet p)
    {
        name = n; type = t; level = lvl; stats = s; skills = k; weapon = w; pet = p;
    }
}

class PetBoostSystem
{
    Random rng = new Random();
    public int OffenseBoost(GameCharacter c)
    {
        switch (c.pet.type)
        {
            case PetType.WOLF: return (int)(c.stats.atk * Math.Min(0.25, 0.05 + c.pet.bond / 100.0));
            case PetType.FAIRY: return (int)(c.stats.mag * Math.Min(0.25, 0.05 + c.pet.bond / 120.0));
            case PetType.HAWK: return c.stats.spd / 3 + c.pet.bond / 6;
            case PetType.SHADOW_CAT: return 5 + c.pet.bond / 5;
        }
        return 0;
    }
    public int DefenseBoost(GameCharacter c)
    {
        switch (c.pet.type)
        {
            case PetType.WOLF: return c.stats.def / 6;
            case PetType.FAIRY: return c.stats.res / 4;
            case PetType.HAWK: return c.stats.spd / 6;
            case PetType.SHADOW_CAT: return 3;
        }
        return 0;
    }
    public bool PetAssist(GameCharacter c)
    {
        switch (c.pet.type)
        {
            case PetType.WOLF: return rng.Next(100) < Math.Min(35, 10 + c.pet.bond / 3);
            case PetType.FAIRY: return rng.Next(100) < Math.Min(40, 15 + c.pet.bond / 2);
            case PetType.HAWK: return rng.Next(100) < Math.Min(30, 8 + c.pet.bond / 4);
            case PetType.SHADOW_CAT: return rng.Next(100) < Math.Min(45, 12 + c.pet.bond / 2);
        }
        return false;
    }
    public int PetAssistDamage(GameCharacter c)
    {
        switch (c.pet.type)
        {
            case PetType.WOLF: return 6 + c.pet.bond / 4;
            case PetType.FAIRY: return 4 + c.pet.bond / 5;
            case PetType.HAWK: return 5 + c.pet.bond / 6;
            case PetType.SHADOW_CAT: return 7 + c.pet.bond / 5;
        }
        return 0;
    }
    public int PetAssistHeal(GameCharacter c)
    {
        switch (c.pet.type)
        {
            case PetType.FAIRY: return 8 + c.pet.bond / 3;
            default: return 0;
        }
    }
}

class WeaponRuleSet
{
    public int ApplyWeapon(GameCharacter c, int baseVal)
    {
        switch (c.weapon.type)
        {
            case WeaponType.SWORD: return baseVal + c.weapon.power + c.stats.atk / 4;
            case WeaponType.STAFF: return baseVal + c.weapon.power / 2 + c.stats.mag / 2;
            case WeaponType.BOW: return baseVal + c.weapon.power + c.stats.spd / 3;
            case WeaponType.DAGGER: return baseVal + c.weapon.power + c.stats.spd / 2;
        }
        return baseVal;
    }
    public int CritChance(GameCharacter c)
    {
        switch (c.weapon.type)
        {
            case WeaponType.SWORD: return 8 + c.stats.crit;
            case WeaponType.STAFF: return 5 + c.stats.crit / 2;
            case WeaponType.BOW: return 15 + c.stats.crit;
            case WeaponType.DAGGER: return 18 + c.stats.crit;
        }
        return 0;
    }
}

class DamageCalculator
{
    readonly Random rng = new Random();
    readonly WeaponRuleSet wr = new WeaponRuleSet();
    readonly PetBoostSystem pets = new PetBoostSystem();

    public int Calc(GameCharacter c, Skill s, Enemy e)
    {
        int baseVal;
        switch (c.type)
        {
            case CharacterType.WARRIOR:
                baseVal = (int)(c.stats.atk * (1.1 + c.level * 0.03)) - e.stats.def / 3;
                if (s.type == SkillType.SPECIAL) baseVal += 12;
                break;
            case CharacterType.MAGE:
                baseVal = c.stats.mag * 2 + (s.type == SkillType.SPECIAL ? 18 : 0) - e.stats.res / 2;
                break;
            case CharacterType.ARCHER:
                baseVal = c.stats.atk + c.stats.spd / 2 + (s.type == SkillType.SPECIAL ? 10 : 0) - e.stats.def / 4;
                break;
            case CharacterType.ROGUE:
                baseVal = c.stats.atk + c.stats.spd + (s.type == SkillType.SPECIAL ? 14 : 0) - e.stats.def / 5;
                break;
            default:
                baseVal = 0;
                break;
        }
        baseVal = wr.ApplyWeapon(c, baseVal);
        baseVal += pets.OffenseBoost(c);
        if (pets.PetAssist(c)) baseVal += pets.PetAssistDamage(c);
        int critChance = wr.CritChance(c) + (c.type == CharacterType.ARCHER ? 10 : 0);
        if (rng.Next(100) < critChance) baseVal *= 2;
        return Math.Max(0, baseVal);
    }
}

class HealCalculator
{
    readonly PetBoostSystem pets = new PetBoostSystem();
    public int Heal(GameCharacter c, Skill s)
    {
        int amt;
        switch (c.type)
        {
            case CharacterType.WARRIOR: amt = c.stats.atk / 3 + 6; break;
            case CharacterType.MAGE: amt = c.stats.mag + 14; break;
            case CharacterType.ARCHER: amt = c.stats.atk / 4 + c.stats.spd / 5; break;
            case CharacterType.ROGUE: amt = c.stats.atk / 2; break;
            default: amt = 0; break;
        }
        if (s.type == SkillType.SPECIAL) amt += 6;
        amt += pets.DefenseBoost(c) / 2 + pets.PetAssistHeal(c);
        return Math.Max(0, amt);
    }
}

class LootCalculator
{
    public int Gold(GameCharacter c, Enemy e)
    {
        switch (c.type)
        {
            case CharacterType.WARRIOR: return 10 + c.level + e.stats.def / 5;
            case CharacterType.MAGE: return 9 + c.level * 2 + e.stats.res / 4;
            case CharacterType.ARCHER: return 11 + c.level + e.stats.def / 6;
            case CharacterType.ROGUE: return 14 + c.level;
        }
        return 0;
    }
}

class TargetingSystem
{
    public Enemy Pick(List<Enemy> enemies)
    {
        Enemy t = enemies[0];
        foreach (Enemy e in enemies) if (e.stats.hp < t.stats.hp) t = e;
        return t;
    }
}

class SkillSelector
{
    public Skill Choose(GameCharacter c)
    {
        foreach (Skill s in c.skills) if (s.type != SkillType.BASIC) return s;
        return c.skills[0];
    }
}

class TurnEngine
{
    readonly DamageCalculator dmg = new DamageCalculator();
    readonly HealCalculator healCalc = new HealCalculator();
    readonly LootCalculator loot = new LootCalculator();
    readonly TargetingSystem targeting = new TargetingSystem();
    readonly SkillSelector selector = new SkillSelector();

    public void Turn(List<GameCharacter> party, List<Enemy> enemies)
    {
        foreach (GameCharacter c in party)
        {
            Skill s = selector.Choose(c);
            if (s.type == SkillType.SUPPORT)
            {
                int h = healCalc.Heal(c, s);
                c.stats.hp += h;
                continue;
            }
            Enemy target = targeting.Pick(enemies);
            int d = dmg.Calc(c, s, target);
            target.stats.hp = Math.Max(0, target.stats.hp - d);
        }
    }
    public int TotalGold(List<GameCharacter> party, Enemy e)
    {
        int g = 0; foreach (GameCharacter c in party) g += loot.Gold(c, e); return g;
    }
}

