class GameCharacter {
    public readonly string name; public readonly CharacterType type; public readonly int level; public readonly Stats stats;
    public readonly List<Skill> skills; public readonly Weapon weapon; public readonly Pet pet;
    public GameCharacter(string n, CharacterType t, int lvl, Stats s, List<Skill> k, Weapon w, Pet p) {
        name = n; type = t; level = lvl; stats = s; skills = k; weapon = w; pet = p;
    }
}

class PetBoostSystem {
    public int offenseBoost(GameCharacter c) {
        return PetLogicRegistry.of(c.pet.type).offenseBoost(c);
    }
    public int defenseBoost(GameCharacter c) {
        return PetLogicRegistry.of(c.pet.type).defenseBoost(c);
    }
    public bool petAssist(GameCharacter c) {
        return PetLogicRegistry.of(c.pet.type).assist(c);
    }
    public int petAssistDamage(GameCharacter c) {
        return PetLogicRegistry.of(c.pet.type).assistDamage(c);
    }
    public int petAssistHeal(GameCharacter c) {
        return PetLogicRegistry.of(c.pet.type).assistHeal(c);
    }
}

class WeaponRuleSet {
    public int applyWeapon(GameCharacter c, int baseVal) {
        return WeaponLogicRegistry.of(c.weapon.type).applyWeapon(c, baseVal);
    }
    public int critChance(GameCharacter c) {
        return WeaponLogicRegistry.of(c.weapon.type).critChance(c);
    }
}

class RoleRuleSet {
    public int baseDamage(GameCharacter c, Skill s, Enemy e) {
        return RoleLogicRegistry.of(c.type).baseDamage(c, s, e);
    }
    public int baseHeal(GameCharacter c, Skill s) {
        return RoleLogicRegistry.of(c.type).baseHeal(c, s);
    }
    public int gold(GameCharacter c, Enemy e) {
        return RoleLogicRegistry.of(c.type).gold(c, e);
    }
    public int extraCrit(GameCharacter c) {
        return RoleLogicRegistry.of(c.type).extraCrit(c);
    }
}

class DamageCalculator {
    private readonly Random rng = new Random();
    private readonly WeaponRuleSet wr = new WeaponRuleSet();
    private readonly PetBoostSystem pets = new PetBoostSystem();
    private readonly RoleRuleSet roles = new RoleRuleSet();

    public int calc(GameCharacter c, Skill s, Enemy e) {
        int baseVal = roles.baseDamage(c, s, e);
        baseVal = wr.applyWeapon(c, baseVal);
        baseVal += pets.offenseBoost(c);
        if (pets.petAssist(c)) baseVal += pets.petAssistDamage(c);
        int critChance = wr.critChance(c) + roles.extraCrit(c);
        if (rng.Next(100) < critChance) baseVal *= 2;
        return Math.Max(0, baseVal);
    }
}

class HealCalculator {
    private readonly PetBoostSystem pets = new PetBoostSystem();
    private readonly RoleRuleSet roles = new RoleRuleSet();
    public int heal(GameCharacter c, Skill s) {
        int amt = roles.baseHeal(c, s);
        amt += pets.defenseBoost(c) / 2 + pets.petAssistHeal(c);
        return Math.Max(0, amt);
    }
}

class LootCalculator {
    private readonly RoleRuleSet roles = new RoleRuleSet();
    public int gold(GameCharacter c, Enemy e) {
        return roles.gold(c, e);
    }
}

class TargetingSystem {
    public Enemy pick(List<Enemy> enemies) {
        Enemy t = enemies[0];
        foreach (Enemy e in enemies) if (e.stats.hp < t.stats.hp) t = e;
        return t;
    }
}

class SkillSelector {
    public Skill choose(GameCharacter c) {
        foreach (Skill s in c.skills) if (s.type != SkillType.BASIC) return s;
        return c.skills[0];
    }
}

class TurnEngine {
    private readonly DamageCalculator dmg = new DamageCalculator();
    private readonly HealCalculator healCalc = new HealCalculator();
    private readonly LootCalculator loot = new LootCalculator();
    private readonly TargetingSystem targeting = new TargetingSystem();
    private readonly SkillSelector selector = new SkillSelector();

    public void turn(List<GameCharacter> party, List<Enemy> enemies) {
        foreach (GameCharacter c in party) {
            Skill s = selector.choose(c);
            if (s.type == SkillType.SUPPORT) {
                int h = healCalc.heal(c, s);
                c.stats.hp += h;
                continue;
            }
            Enemy target = targeting.pick(enemies);
            int d = dmg.calc(c, s, target);
            target.stats.hp = Math.Max(0, target.stats.hp - d);
        }
    }
    public int totalGold(List<GameCharacter> party, Enemy e) {
        int g = 0; foreach (GameCharacter c in party) g += loot.gold(c, e); return g;
    }
}

