    }
}

class UpsellEngine {
    public string Suggest(FoodItem item) {
        switch (item.GetTypeFood()) {
            case FoodType.BURGER: return "Add Fries";
            case FoodType.FRIES: return "Add Drink";
            case FoodType.PIZZA: return "Add Dessert";
            case FoodType.DRINK: return "Add Burger";
            case FoodType.SALAD: return "Add Chicken";
            case FoodType.DESSERT: return "Add Coffee";
            default: return "Add Deal";
        }
    }
}

