public abstract class BaseFoodStrategy : FoodStrategy
{
    protected bool TakeFromStock(Dictionary<string, int> stock, string key, int qty)
    {
        int have = stock.GetValueOrDefault(key, 0);
        if (have < qty) return false;
        stock[key] = have - qty;
        return true;
    }

    public abstract double GetPrice(Size size);
    public abstract double GetTaxRate();
    public abstract int GetCalories(Size size);
    public abstract double GetDiscount(FoodItem item);
    public abstract string GetStation();
    public abstract bool ReserveInventory(Dictionary<string, int> stock);
    public abstract void Prepare();
    public abstract void Serve();
    public abstract string GetPackaging(Size size);
    public abstract HashSet<string> GetAllergens(FoodItem item);
    public abstract string GetCourier();
    public abstract int GetLoyaltyPoints();
    public abstract int GetPrepWasteGrams();
    public abstract int GetStationLoadWeight();
    public abstract bool PassQuality(FoodItem item);
    public abstract int GetCookingSeconds(Size size);
    public abstract bool IsAvailable(int hour);
    public abstract int GetSpiceLevel(FoodItem item);
    public abstract string GetUpsellSuggestion();
