class SecurityPolicy
{
    public bool Allowed(SmartDevice d, string action, bool userHome)
    {
        switch (d.GetType())
        {
            case DeviceType.DOOR_LOCK: return action == "unlock" ? userHome : true;
            case DeviceType.CAMERA: return !(action == "disable") || userHome;
            case DeviceType.SPRINKLER: return !(action == "on" && userHome && d.GetMode() == Mode.PARTY);
            case DeviceType.SPEAKER: return !(action == "play" && !userHome);
            default: return true;
