class VoiceCommandProcessor
{
    private readonly SmartHomeController ctl = new SmartHomeController();
    public void Handle(string phrase, SmartDevice d)
    {
        switch (d.GetType())
        {
            case DeviceType.LIGHT:
                if (phrase.Equals("lights on", StringComparison.OrdinalIgnoreCase)) ctl.TurnOn(d);
                else if (phrase.Equals("lights off", StringComparison.OrdinalIgnoreCase)) ctl.TurnOff(d);
                break;
            case DeviceType.THERMOSTAT:
                if (phrase.Equals("heat on", StringComparison.OrdinalIgnoreCase)) ctl.TurnOn(d);
                else if (phrase.Equals("eco", StringComparison.OrdinalIgnoreCase)) ctl.SetMode(d, Mode.ECO);
                break;
            case DeviceType.DOOR_LOCK:
                if (phrase.Equals("lock door", StringComparison.OrdinalIgnoreCase)) ctl.TurnOn(d);
                break;
            case DeviceType.CAMERA:
                if (phrase.Equals("record", StringComparison.OrdinalIgnoreCase)) ctl.TurnOn(d);
                break;
            case DeviceType.SPRINKLER:
                if (phrase.Equals("water", StringComparison.OrdinalIgnoreCase)) ctl.TurnOn(d);
                break;
            case DeviceType.BLIND:
                if (phrase.Equals("open blinds", StringComparison.OrdinalIgnoreCase)) ctl.TurnOn(d);
                else if (phrase.Equals("close blinds", StringComparison.OrdinalIgnoreCase)) ctl.TurnOff(d);
                break;
            case DeviceType.SPEAKER:
                if (phrase.Equals("play music", StringComparison.OrdinalIgnoreCase)) ctl.TurnOn(d);
                else if (phrase.Equals("pause music", StringComparison.OrdinalIgnoreCase)) ctl.TurnOff(d);
                break;
        }
    }
}

class SceneManager
{
    private readonly SmartHomeController ctl = new SmartHomeController();
    public void Apply(string scene, List<SmartDevice> devices)
    {
        foreach (var d in devices)
        {
            switch (scene)
            {
                case "Evening":
                    switch (d.GetType())
                    {
                        case DeviceType.LIGHT: ctl.TurnOn(d); ctl.SetMode(d, Mode.COMFORT); break;
                        case DeviceType.THERMOSTAT: ctl.SetMode(d, Mode.COMFORT); break;
                        case DeviceType.CAMERA: ctl.TurnOn(d); break;
                        case DeviceType.DOOR_LOCK: ctl.TurnOn(d); break;
                        case DeviceType.SPRINKLER: ctl.TurnOff(d); break;
                        case DeviceType.BLIND: ctl.TurnOff(d); break;
                        case DeviceType.SPEAKER: ctl.TurnOn(d); break;
                    }
                    break;
                case "Away":
                    switch (d.GetType())
                    {
                        case DeviceType.LIGHT: ctl.TurnOff(d); break;
                        case DeviceType.THERMOSTAT: ctl.SetMode(d, Mode.AWAY); break;
                        case DeviceType.CAMERA: ctl.TurnOn(d); break;
                        case DeviceType.DOOR_LOCK: ctl.TurnOn(d); break;
                        case DeviceType.SPRINKLER: ctl.TurnOff(d); break;
                        case DeviceType.BLIND: ctl.TurnOff(d); break;
                        case DeviceType.SPEAKER: ctl.TurnOff(d); break;
                    }
                    break;
                case "Party":
                    switch (d.GetType())
                    {
                        case DeviceType.LIGHT: ctl.TurnOn(d); ctl.SetMode(d, Mode.PARTY); break;
                        case DeviceType.THERMOSTAT: ctl.SetMode(d, Mode.COMFORT); break;
                        case DeviceType.SPEAKER: ctl.TurnOn(d); break;
                        case DeviceType.CAMERA: ctl.TurnOff(d); break;
                        case DeviceType.BLIND: ctl.TurnOn(d); break;
                        case DeviceType.SPRINKLER: ctl.TurnOff(d); break;
                        case DeviceType.DOOR_LOCK: ctl.TurnOn(d); break;
                    }
                    break;
            }
        }
    }
}

class FirmwareManager
