            case DeviceType.SPEAKER: return new List<string> { "Ping", "PushFW", "RestartAudio" };
            default: return new List<string>();
        }
    }
}

class ScheduleEngine
{
    public class Entry
    {
        public readonly SmartDevice device;
        public readonly string cron;
        public readonly string action;
        public Entry(SmartDevice d, string c, string a) { device = d; cron = c; action = a; }
    }
    public void Run(Entry e, SmartHomeController ctl)
    {
        switch (e.device.GetType())
        {
            case DeviceType.LIGHT: if (e.action == "on") ctl.TurnOn(e.device); else ctl.TurnOff(e.device); break;
            case DeviceType.SPRINKLER: if (e.action == "on") ctl.TurnOn(e.device); else ctl.TurnOff(e.device); break;
            case DeviceType.THERMOSTAT: if (e.action == "comfort") ctl.SetMode(e.device, Mode.COMFORT); break;
            case DeviceType.BLIND: if (e.action == "open") ctl.TurnOn(e.device); else ctl.TurnOff(e.device); break;
            case DeviceType.SPEAKER: if (e.action == "play") ctl.TurnOn(e.device); else ctl.TurnOff(e.device); break;
            default: ctl.TurnOff(e.device); break;
        }
    }
}

