class AutomationRuleEngine
{
    private readonly SmartHomeController ctl = new SmartHomeController();
    public void OnEvent(string ev, SmartDevice d)
    {
        switch (ev)
        {
            case "motion":
                if (d.GetType() == DeviceType.CAMERA || d.GetType() == DeviceType.LIGHT) ctl.TurnOn(d);
                break;
            case "sunset":
                if (d.GetType() == DeviceType.LIGHT) ctl.TurnOn(d);
                else if (d.GetType() == DeviceType.BLIND) ctl.TurnOff(d);
                break;
            case "door-open":
                if (d.GetType() == DeviceType.DOOR_LOCK || d.GetType() == DeviceType.CAMERA) ctl.TurnOn(d);
                break;
        }
    }
}

class ReportPrinter
{
    private readonly SmartHomeController ctl = new SmartHomeController();
    private readonly DiagnosticsService diag = new DiagnosticsService();
    private readonly DeviceRenderer render = new DeviceRenderer();
    public void PrintRoom(Room room)
    {
        Console.WriteLine("Room: " + room.GetName() + " (" + room.GetTypeEnum() + ")");
        foreach (var d in room.GetDevices())
        {
            double e = ctl.EnergyUsageKwh(d, 30);
            Console.WriteLine(render.Icon(d) + " " + render.Label(d) + " mode=" + d.GetMode() +
                              " health=" + diag.Health(d) + " 30m=" + e.ToString("F3") + "kWh");
        }
    }
}

