using System;
using System.Collections.Generic;

enum DeviceType { LIGHT, THERMOSTAT, DOOR_LOCK, CAMERA, SPRINKLER, BLIND, SPEAKER }
enum RoomType { LIVING, KITCHEN, BEDROOM, BATHROOM, GARDEN, GARAGE }
enum Mode { ECO, COMFORT, AWAY, NIGHT, PARTY }
enum AlertType { INFO, WARN, CRITICAL }

abstract class SmartDevice {
    protected readonly string id;
    protected readonly DeviceType type;
    protected Mode mode = Mode.ECO;
    protected bool online = true;

    public SmartDevice(string id, DeviceType type) { this.id = id; this.type = type; }
    public string getId() { return id; }
    public DeviceType getType() { return type; }
    public Mode getMode() { return mode; }
    public bool isOnline() { return online; }
    public void setMode(Mode m) { mode = m; }
    public void setOnline(bool o) { online = o; }

    public abstract void turnOn();
    public abstract void turnOff();
    public abstract void setModeAction(Mode m);
    public abstract double getEnergyUsagePerMinute();
    public abstract string getHealthStatus();
    public abstract string getNotificationPrefix();
    public abstract bool isActionAllowed(string action, bool userHome);
    public abstract void handleVoiceCommand(string phrase, SmartHomeController ctl);
    public abstract void applyEveningScene(SmartHomeController ctl);
    public abstract void applyAwayScene(SmartHomeController ctl);
    public abstract void applyPartyScene(SmartHomeController ctl);
    public abstract List<string> getFirmwareSteps();
    public abstract void executeScheduledAction(string action, SmartHomeController ctl);
    public abstract void handleMotionEvent(SmartHomeController ctl);
    public abstract void handleSunsetEvent(SmartHomeController ctl);
    public abstract void handleDoorOpenEvent(SmartHomeController ctl);
    public abstract string getIcon();
    public abstract string getLabel();
    public abstract List<string> getGroups();
    public abstract bool canUserControl(string role);
}

class LightDevice : SmartDevice {
    public LightDevice(string id) : base(id, DeviceType.LIGHT) {}
