        if (string.Equals(phrase, "lights on", StringComparison.OrdinalIgnoreCase)) ctl.turnOn(this);
        else if (string.Equals(phrase, "lights off", StringComparison.OrdinalIgnoreCase)) ctl.turnOff(this);
    }
    public override void applyEveningScene(SmartHomeController ctl) { ctl.turnOn(this); ctl.setMode(this, Mode.COMFORT); }
