using System;
using System.Collections.Generic;

abstract class GraphicObject
{
    private readonly string color;

    protected GraphicObject(string color)
    {
        this.color = color;
    }

    public string Color => color;
}

enum ShapeKind
{
    RECTANGLE, CIRCLE, CUBE
}

abstract class Shape : GraphicObject
{
    private readonly ShapeKind kind;

    protected Shape(ShapeKind kind, string color) : base(color)
    {
        this.kind = kind;
    }

    public ShapeKind Kind => kind;
}

abstract class TwoDShape : Shape
{
    protected TwoDShape(ShapeKind kind, string color) : base(kind, color) { }
}

abstract class ThreeDShape : Shape
{
    protected ThreeDShape(ShapeKind kind, string color) : base(kind, color) { }
}

class Rectangle : TwoDShape
{
    private readonly double width;
    private readonly double height;

    public Rectangle(double width, double height, string color)
        : base(ShapeKind.RECTANGLE, color)
    {
        this.width = width;
        this.height = height;
    }

    public double Width => width;
    public double Height => height;
}

class Circle : TwoDShape
{
    private readonly double radius;

    public Circle(double radius, string color)
        : base(ShapeKind.CIRCLE, color)
    {
        this.radius = radius;
    }

    public double Radius => radius;
}

class Cube : ThreeDShape
{
    private readonly double edge;

    public Cube(double edge, string color)
        : base(ShapeKind.CUBE, color)
    {
        this.edge = edge;
    }

    public double Edge => edge;
}

enum Metric { AREA, PERIMETER, VOLUME }

class MetricCalculator
{
    public double Compute(Shape shape, Metric metric)
    {
        switch (shape.Kind)
        {
            case ShapeKind.RECTANGLE:
                {
                    var r = (Rectangle)shape;
                    return metric switch
                    {
                        Metric.AREA => r.Width * r.Height,
                        Metric.PERIMETER => 2 * (r.Width + r.Height),
                        Metric.VOLUME => 0,
                        _ => 0
                    };
                }
            case ShapeKind.CIRCLE:
                {
                    var c = (Circle)shape;
                    return metric switch
                    {
                        Metric.AREA => Math.PI * c.Radius * c.Radius,
                        Metric.PERIMETER => 2 * Math.PI * c.Radius,
                        Metric.VOLUME => 0,
                        _ => 0
                    };
                }
            case ShapeKind.CUBE:
                {
                    var cube = (Cube)shape;
                    return metric switch
                    {
                        Metric.AREA => 6 * cube.Edge * cube.Edge,
                        Metric.PERIMETER => 12 * cube.Edge,
                        Metric.VOLUME => Math.Pow(cube.Edge, 3),
                        _ => 0
                    };
                }
            default:
                return 0;
        }
    }
}

class ShapeExporter
{
    public string ToJson(Shape shape)
    {
        switch (shape.Kind)
        {
            case ShapeKind.RECTANGLE:
                {
                    var r = (Rectangle)shape;
                    return string.Format(
                        "{{\"type\":\"rectangle\",\"color\":\"{0}\",\"width\":{1},\"height\":{2}}}",
                        r.Color, r.Width, r.Height
                    );
                }
            case ShapeKind.CIRCLE:
                {
                    var c = (Circle)shape;
                    return string.Format(
                        "{{\"type\":\"circle\",\"color\":\"{0}\",\"radius\":{1}}}",
                        c.Color, c.Radius
                    );
                }
            case ShapeKind.CUBE:
                {
                    var cube = (Cube)shape;
                    return string.Format(
                        "{{\"type\":\"cube\",\"color\":\"{0}\",\"edge\":{1}}}",
                        cube.Color, cube.Edge
                    );
                }
            default:
                return "{}";
        }
    }
}


class ShapeFactory
{
    public Shape Create(string spec)
    {
        string[] parts = spec.Split(new[] { ':' }, 2);
        string type = parts[0].Trim();
        string[] kvs = (parts.Length > 1 ? parts[1] : "").Split(';');
        var map = new Dictionary<string, string>();
        foreach (string kv in kvs)
        {
            string[] p = kv.Split('=');
            if (p.Length == 2) map[p[0].Trim()] = p[1].Trim();
        }
        string color = map.ContainsKey("color") ? map["color"] : "black";

        switch (type.ToLower())
        {
            case "rectangle":
                return new Rectangle(
                    double.Parse(map.ContainsKey("width") ? map["width"] : "0"),
                    double.Parse(map.ContainsKey("height") ? map["height"] : "0"),
                    color
                );
            case "circle":
                return new Circle(
                    double.Parse(map.ContainsKey("radius") ? map["radius"] : "0"),
                    color
                );
            case "cube":
                return new Cube(
                    double.Parse(map.ContainsKey("edge") ? map["edge"] : "0"),
                    color
                );
            default:
                throw new ArgumentException("Unknown shape: " + type);
        }
    }
}

